css:
@-moz-document url-prefix("http://4pda.ru") {
body  {
    border: 0px!important;
}
.post-block {
    border:0px!important;
    border-left: 4px solid #795548!important;
    transition:0.5s;
}
.post-block.tbl {
    border:none!important;
}
.post-block:hover {
    transition:0.5s;
    border-width:0px;
	-webkit-box-shadow:#E4EAF2 0px 0px 0px 5px;
	-moz-box-shadow:#E4EAF2 0px 0px 0px 5px;
	box-shadow:#E4EAF2 0px 0px 0px 5px;
}
div.globalmesswrap {
    border:none;
    background:#FAFAFA;
}
.globalmesswrap h4 {
    background: #E1F5FE;
    border: none;
    color: #4E342E;
}
.borderwrap, .borderwrap h3, .maintitle, .maintitlecollapse {
    border-radius: 0px!important;
    border: 0px!important;
    background: #1E88E5!important;
}
table.ipbtable td a img {
    transition:0.5s;
}
table.ipbtable td a img:hover {
    transition:0.5s;
	border-radius:90px;
    -webkit-box-shadow: rgba(62, 39, 35, 0.580392) 0px 0px 0px 5px;
}
.row2 {
    background:#D6E8FF;
}
#userlinks {
    border: 0px;
    transition: 0.5s;
}
#userlinks:hover {
    transition: 0.5s;
    background: #E3F2FD;
}
.maintitle:hover, .maintitlecollapse:hover {
    background: #1976D2;
}
div.borderwrap p a {
    color: white!important;
}
a[title="Просмотреть репутацию"]:hover {
	border-radius:90px;
	box-shadow:rgba(0, 0, 0, 0.35) 0px 0px 0px 2px;
	background-color:rgba(0, 0, 0, 0.35);
}
.catend:hover, .popmenubutton:hover, .upopupmenu-item:hover, .upopupmenu-item-last:hover, .popupmenu-item:hover, .popupmenu-item-last:hover, .popmenubutton-new {
    transition: 1s;
    border: none;
    background: #1565C0!important;
}
.popupmenu-new:hover {
    background: #CFD8DC;
}
.catend, .popmenubutton {
    transition: 1s;
    background: #1E88E5;
}
.popmenubutton-new-out {
    border: none;
    padding: 3px;
    transition: 1s;
}
.popmenubutton, .popmenubutton-new-out {
    border: 0px!important;
    color: #f44336;
}
a:hover {
    transition: 0.4s;
    color: #f44336!important;
    text-decoration: none!important;
}
a {
    transition: 0.4s!important;
    color: #5D4037!important;
    text-decoration: none!important;
    font-size: 1.05em;
}
table.ipbtable, tr.ipbtable, td.ipbtable {
    background: #E8EAF6!important;
    margin:0px 0px 0px 0.01%;
	width:99.99%;
}
img[alt='Ответить'] {
    display: none;
}
.nopad {
    background: white;
}
td[style="padding-left:0px"] {
    background: white;
}
.pagelink, span.pagelink a, .pagelinklast, span.pagelinklast a {
    border: 0px;
    color: white!important;
    background: #1E88E5!important;
    transition: 0.4s;
}
.pagecurrent, span.pagecurrent {
    color: white;
    border: 0px;
    background: #1565C0;
    transition: 0.4s;
}
.pagecurrent:hover, span.pagecurrent:hover {
    transition: 0.4;
    background: #283593;
}
.popupmenu, .popupmenu-new, .popupmenu-item {
    border: 0px;
}
.popupmenu-category {
    background: #1E88E5;
    border: 0px;
}
.popupmenu-item-last {
    background: #90CAF9;
}
input {
    border: 0px;
}
input.button {
    border: 0px;
    background: #42A5F5;
    color: white;
    transition: 0.7s;
}
input[type="text"]:hover {
    -webkit-box-shadow:#90CAF9 0px 0px 0px 3px;
	-moz-box-shadow:#90CAF9 0px 0px 0px 3px;
	box-shadow:#90CAF9 0px 0px 0px 3px;
}
input.button:hover {
    transition: 0.7s;
    background: #2196F3;
    -webkit-box-shadow:rgba(0, 0, 0, 0.25) 0px 2px 10px 3px;
	-moz-box-shadow:rgba(0, 0, 0, 0.25) 0px 2px 10px 3px;
	box-shadow:rgba(0, 0, 0, 0.25) 0px 2px 10px 3px;
}
input.searchinput {
    transition: 0.7s;
    border: 0px;
    background: #BBDEFB;
}
input.searchinput:hover {
    transition: 0.7s;
    background: #90CAF9;
}
.upopupmenu-category {
    border: 0px;
    background: #2196F3;
}
.popupmenu-item-last, .upopupmenu-new, .upopupmenu-item, .upopupmenu-item-last, .popupmenu-item, .popupmenu-item-last, .usercp_menu {
    background: #42A5F5;
    transition: 0.5s;
    border: 0px;
}
.usercp_menu_out {
    border: 0px;
    transition: 0.5s;
}
.popupmenu-new {
    color: black;
    transition: 0.5s;
}
table.ipbtable tbody tr th {
    border: 0px!important;
    background: #90CAF9;
    transition: 0.5s;
    color: white;
}
table.ipbtable tbody tr th:hover {
    background: #64B5F6;
    transition: 0.5s;
}
img[src="http://s.4pda.to/URbofawp38LVohe4m4stRvU3I7DUO9MDmwxz2OG9JBvwn.gif"], img[src="http://s.4pda.to/URbofaQpwZ0CHDiiZTqbh2G9F43uRM4jF0Sq3gXEZtp.gif"], img[src="http://s.4pda.to/URbokRZz1Jkz0fz2SAnqHwvRmPyDe5Zxes2TPz1dvalG.gif"], img[src="http://s.4pda.to/URboz2CCz1w58yCVApJbOB00rmWi7IQL2YtiIlvZsAfcu.gif"] {
    display: none;
}
a[href="javascript:togglecategory(200,1);"]:after, a[href="javascript:togglecategory(-1, 1);"]:after, a[href="javascript:togglecategory(281,1);"]:after, a[href="javascript:togglecategory(295,1);"]:after, a[href="javascript:togglecategory(355,1);"]:after, a[href="javascript:togglecategory(201,1);"]:after, a[href="javascript:togglecategory(442,1);"]:after, a[href="javascript:togglecategory(236,1);"]:after, a[href="javascript:togglecategory(202,1);"]:after, a[href="javascript:togglecategory(607,1);"]:after, a[href="javascript:togglecategory(307,1);"]:after, a[href="javascript:togglecategory(203,1);"]:after, a[href="javascript:togglecategory(256,1);"]:after, a[href="javascript:togglecategory('stat',1);"]:after, a[title="Опустить репутацию"]:after {
    content: "-";
}
a[href="javascript:togglecategory(-1, 0);"]:after, a[href="javascript:togglecategory(200,0);"]:after, a[href="javascript:togglecategory(281,0);"]:after, a[href="javascript:togglecategory(295,0);"]:after, a[href="javascript:togglecategory(355,0);"]:after, a[href="javascript:togglecategory(201,0);"]:after, a[href="javascript:togglecategory(442,0);"]:after, a[href="javascript:togglecategory(236,0);"]:after, a[href="javascript:togglecategory(202,0);"]:after, a[href="javascript:togglecategory(607,0);"]:after, a[href="javascript:togglecategory(307,0);"]:after, a[href="javascript:togglecategory(203,0);"]:after, a[href="javascript:togglecategory(256,0);"]:after, a[href="javascript:togglecategory('stat',0);"]:after, a[title="Поднять репутацию"]:after {
    content: "+";
}
td.nopad a:after {
    content: "Ответить";
}
td.nopad a[title="Переключить окно быстрого ответа"]:after {
    content: "Быстрый ответ ";
}
img[alt="Быстрый ответ"], img[alt="Цитировать"], img[alt="Вставить ник"], img[alt="Жалоба"], img[src="http://s.4pda.to/URbogZc1XvkS6R6FguLZBK4Bhz1xcxXIjC2ResA.gif"], img[src="http://s.4pda.to/URbofaQpwZ0CHDiiZTqbh2G9F43uRM4jF0Sq3gXEZtp.gif"], img[src="http://s.4pda.to/URbofawp38LVohe4m4stRvU3I7DUO9MDmwxz2OG9JBvwn.gif"], img[src="http://s.4pda.to/URboz1FBpKBCyh9s0ID8LvpNwz1lBrJsBmfBo0Oz02.gif"], img[src="http://s.4pda.to/URbofawp38LVmpVdCAjDln27nz0ZNT9FqMCVmP3uz1Hs.gif"], img[src="/forum/style_images/1/fpr_down.png"], img[src="/forum/style_images/1/fpr_up.png"] {
    display: none;
}
td.formbuttonrow>div[align="right"]>a[style="cursor: pointer;"]:after {
    content:"Редактировать";
    position:absolute;
    margin: 0px 1px 0px -20.6%;
}
td.formbuttonrow>div[align="right"]>a[title="Удалить пост"]:after {
    content:"Удалить";
    position:absolute;
    margin: 0px 1px 0px -12.8%;
}
a[title="Минусуем: флуд, оффтопик, хамство, мат, оскорбления, конфликты, спам, коммерцию, явные нарушения правил."]:after {
    content: "Плохо";
    vertical-align: middle;
    position:absolute;
    margin: 0px 0px 0px -50px;
}
a[title="Плюсуем: полезную информацию или то, что стоит добавить в FAQ."]:after {
    content: "Хорошо";
    vertical-align: middle;
    position:absolute;
    margin: 0px 0px 0px -25px;
}
td.formbuttonrow div[style="float: left;"] a:after {
    content: "Имя";
    vertical-align: middle;
    position:absolute;
    margin: 0px 0px 0px 76.1%;
}
td.formbuttonrow>div[style="float: left;"]>a::hover:after {
	-webkit-box-shadow:rgba(0, 0, 0, 0.62) 0px 0px 0px 100px;
	-moz-box-shadow:rgba(0, 0, 0, 0.62) 0px 0px 0px 100px;
	box-shadow:rgba(0, 0, 0, 0.62) 0px 0px 0px 100px;
    background: rgba(0, 0, 0, 0.62);
}
a[href="javascript:pasteQ();"]:after {
    content: "Цитировать"!important;
    vertical-align: middle;
    position:relative;
    margin: 0px 0px 0px 78.4%!important;
}
td.formbuttonrow div[style="text-align:left"] a:after {
    content: attr(div) "Жалоба";
    vertical-align: middle;
}
td.formbuttonrow div[style="text-align:left"] a[href="javascript:scroll(0,0);"]:after {
    content: "↑ "!important;
    vertical-align: middle;
}
a[title="Добавить вопрос и ответ на DevFAQ"]:after {
    content: none!important;
}
select {
    border: 0px;
}
fieldset {
    transition: 1s;
    border: 0px;
    background: #BBDEFB;
}
fieldset:hover {
    transition: 1s;
    background: #90CAF9;
}
.activeusers, .ipbtable tbody tr {
    padding: 0px;
    transition: 0.7s;
    border: 0px;
    background: #BBDEFB;
}
.ipbtable tbody tr:hover {
    transition: 0.7s;
    background: #90CAF9;
}
td.nopad a:hover::after, td.formbuttonrow div[style="text-align:left"] a:hover::after, a[title="Минусуем: флуд, оффтопик, хамство, мат, оскорбления, конфликты, спам, коммерцию, явные нарушения правил."]:hover::after, a[title="Плюсуем: полезную информацию или то, что стоит добавить в FAQ."]:hover::after, a[title="Добавить вопрос и ответ на DevFAQ"]:hover, td.formbuttonrow div[style="float: left;"] a:hover::after, td.formbuttonrow>div[align="right"]>a[title="Удалить пост"]:hover::after, td.formbuttonrow>div[align="right"]>a[style="cursor: pointer;"]:hover::after {
    transition: 0.5s;
    -webkit-box-shadow:rgba(0, 0, 0, 0.35) 0px 0px 0px 3px;
	-moz-box-shadow:rgba(0, 0, 0, 0.35) 0px 0px 0px 3px;
	box-shadow:rgba(0, 0, 0, 0.35) 0px 0px 0px 3px;
    background: rgba(0, 0, 0, 0.35);
    border-radius:0px;
}
td.formbuttonrow div[style="text-align:left"] a:after, div.ipbwrapper table.ipbtable tbody td.nopad[width="60%"] a:after, a[title="Минусуем: флуд, оффтопик, хамство, мат, оскорбления, конфликты, спам, коммерцию, явные нарушения правил."]:after, a[title="Плюсуем: полезную информацию или то, что стоит добавить в FAQ."]:after, a[title="Добавить вопрос и ответ на DevFAQ"], td.formbuttonrow div[style="float: left;"] a:after, td.formbuttonrow>div[align="right"]>a[title="Удалить пост"]:after, td.formbuttonrow>div[align="right"]>a[style="cursor: pointer;"]:after {
    border-radius:0px;
    transition:0.5s;
}
a[title="Добавить вопрос и ответ на DevFAQ"] {
    position:absolute;
    padding:0px 0px 1px 0px!important;
    margin:1px 0px 0px 0px!important;
}
td.nopad a:after {
    position:absolute;
    padding:0px 0px 3px 0px;
    margin:5px 0px 0px -22%;
}
td.nopad a[title="Переключить окно быстрого ответа"] + a:after {
    position:relative;
    margin:0px 0px 0px 0px;
}
td.nopad a[title="Переключить окно быстрого ответа"]:after {
    content: "Быстрый ответ ";
    position:relative;
    margin:0px;
}
.rYRFX2z0C, .rqz2bR1cAB, .rql12Tj, .rsz2ZMH0rP9OEfV8T, #retTiUV3bU4 {
