css:
@-moz-document
url-prefix(https://www.tumblr.com/dashboard),
url-prefix("https://www.tumblr.com/inbox"),
url-prefix("https://www.tumblr.com/blog"),
url-prefix("https://www.tumblr.com/likes"),
url-prefix("https://www.tumblr.com/new"),
url-prefix("https://www.tumblr.com/edit"),
url-prefix("https://www.tumblr.com/reblog"),
url-prefix("https://www.tumblr.com/tagged") {
  
  body {
    background-color: aliceblue !important;
    /* background-image: url('http://i.imgur.com/SWcBB3j.png')!important; */
    background-attachment: fixed!important;
    background-size: cover!important;
  }
  .post-forms-glass,
  .post-tab-switching {
    background: #FFFEFEE6 !important;
  }
  .l-header-container {
    background: #FFA4A4 !important;
    box-shadow: 0 0 2px #A2A2A2 !important;
  }
  .l-header,/*
  .tab_notice,*/
  .l-content,
  .left_column,
  .new_post_label,
  .post,
  .post_full,
  .post_avatar {
    background: none !important;
  }
  .search_query {
    padding-top: 5px !important;
  }
  .tab_notice {
    padding: 1px 3px 1px 2px !important;
    border: 0px solid #FFA4A4 !important;
    box-shadow: 0 0 2px #FFA4A4 !important;
    background: #FFFEFE !important;
    height: auto !important;
    width: auto !important;
  }
  .tab_notice_value {
    vertical-align: top !important;
    color: #FFA4A4 !important;
    font-family: sans-serif !important;
    font-weight: bold !important;
  }
  .compose-button {
    fill: #FFFEFE !important;
  }
  .right_column {
    margin-top: 0 !important;
    background: #FFFEFE !important;
    border-radius: 5px !important;
  }
  .right_column * {
    color: #A2A2A2 !important;
  }
  .recommended_tumblelogs {
    margin-top: 5px !important;
  }
  .controls_section {
    margin-top: 0 !important;
  }
  .no_posts_found {
    margin-top: 0 !important;
    background: #FFFEFE !important;
    border: none !important;
    color: #A2A2A2 !important;
  }
  .sprite_icon_mail,
  .new_post_label span,
  .right_column:after,
  .follow_list_item_blog:before,
  .radar_avatar:before {
    display:none !important;
  }
  .new_post_buttons {
    min-height: auto !important;
    height: 64px;
  }
  .new_post_label {
    padding: 0 !important;
    height: 70px !important;
  }
  .post_container {
    background: #FFFEFE !important;
    border-radius: 5px !important;
  }
  .reblog_follow_button {
    margin-top: 2px !important;
  }
  .post_permalink {
    background: transparent !important;
    border-color: aliceblue aliceblue #FFA4A4 #FFA4A4 !important;
    border-radius: 0 5px 0 5px !important;
  }
  
}
