css:
@-moz-document domain("workflowy.com") {

  /* TEXT */
  
  /* General text */
  /* The scale is 11 13 15 19 */
  #documentView {
        font: 15px "Vollkorn",serif;
  }
  /* Bold headings */
  .open > .name > .content,
  .selected > .name > .content,
  .parent > .name > .content  {
    font-family: "Lora", serif;
    font-weight: bold;
  }
  .mainTreeRoot > .children > .open > .name > .content,
  .selected > .name > .content,
  .parent > .name > .content {
    font-size: 19px;
    margin-bottom: 3px !important;
  }
  .task > .name > .content {
    font-size: 15px;
  }
  /* Tags */
  .contentTag {
    font: 11px "Open Sans","Calibri","Helvetica Neue",sans-serif bold;
    color: #23D183 !important;
    padding: 1px 3px 3px 3px !important;
    white-space: nowrap !important;
  }
  .contentTagText {
    text-decoration: none !important;
    font-weight: bold;
  }
  .contentTag[title*="@"] {
    color: #FFA29F !important;
  }
  .contentTag[title*="#"] {
    color: #87D5A2 !important;
  }
  .contentTag[title*="#YOLO"] {
    text-decoration: blink !important;
    color: #FF00FF !important;
  }
  /* Notes */
  .notes .content {
    display: block !important;
    height: auto !important;
    overflow: visible !important;
    font: 13px "Ubuntu", serif;
    padding: 0 0 3px 3px;
    color: #AAAAAA !important;
    font-weight: normal !important;
  }
  /* Done items */
  .done > .name > .content{
    text-decoration: line-through double black !important;
  }
  /* Links */
  .content .contentLink {
    font-family: "Inconsolata", monospace;
    text-decoration: none;
    color: #2E6CBA;
  }
  /* Highlighted text */
  span.contentUnderline {
    background-color: #FFFF60 !important;
    text-decoration: none !important;
  }
  
  /* LIST ELEMENTS */
  
  /* Chevrons in breadcrumbs nav */
  .parent > .name > .parentArrow:before {
    content: "Â»" !important;
  }
  /* Discreet indentation */
  .children {
    border: none;
    padding: 0 0 0 5px;
  }
  /* Smaller and bigger bullets */
  .bullet {
    height: 13px;
    width: 13px;
    margin-top: 6px !important;
  }
  .open > .name > .bullet,
  .selected > .name > .bullet,
  .parent > .name > .bullet {
    height: 18px;
    width: 18px;
    border-radius: 3px !important;
    margin-left: -1px;
  }
  /* Color for shared bullets */
  .project.shared > .name > .bullet {
    background-color: #89D1EC;
  }
  .project.shared.open > .name > a.bullet {
    background: #89D1EC;
  }
  .project.shared > .name > .bullet:hover {
    background-color: #2E6CBA;
  }
  .project.shared.open > .name > .bullet:hover {
    background-color: #2E6CBA;
  }
  /* Check symbol for done items */
  .project.done > .name > a.bullet {
    background-image: url(data:image/png;base64,) !important;
    background-size: cover;
    margin: 6px 0 0 2px !important;
    }
  
}
