css:
@-moz-document domain("workflowy.com") {
  
  /* Hiding stuff */
  #getMoreSpaceButtonTopLeft,
  #getMoreSpaceButtonTopRight,
  #bottomLinks,
  #proPitchBottomRight,
  #controlsLeft > .complete,
  #controlsLeft > .note,
  #controlsLeft > .delete,
  #controlsLeft hr,
  #itemSelectionControls hr,
  .weekly-report__support .freeOnly,
  .corner,
  .edge,
  .savedViewPageFader {
    display: none
  }

  /* Header */
  #header {
    border-bottom: none !important;
    background: #2F343F !important;
    padding: 3px 0 !important;
  }
  #logo {
    background-position: center center !important;
    background-size: auto 20px !important;
    background-color: #2F343F !important;
    margin: -3px 0 0 0 !important;
    height: 29px !important;
  }
  #searchForm {
    top: 0px !important;
  }
  #searchBox {
    color: #A5B6C3 !important;
    background-color: #404552 !important;
    border: none !important;
    width: 200px !important;
    height: 15px !important;
    padding: 10px 12px !important;
    font-size: 14px !important;
    opacity: .75 !important;
    margin: -3px 0 0 0 !important;
    border-radius: 0px !important;
  }
  #searchCancel {
    top: 10px !important;
    right: 10px !important;
    width: 10px !important;
    height: 10px !important;
  }
  #searchCancel > svg > line {
    stroke: #A5B6C3;
    stroke-width: 2;
  }
  #searchCancel:hover > svg > line {
    stroke: #738088 !important;
  }
  .menu-options {
    top: 35px !important;
    background: #383C4A !important;
    border: none !important;
    border-radius: 0px !important;
  }
  #buttonBar.open {
    background: none !important;
  }
  .saveButton,
  .showCompletedButton,
  #buttonBar,
  #helpButton {
    font-weight: 500;
    color: #AAA !important;
  }
  .showCompletedButton:hover,
  #buttonBar:hover,
  #helpButton:hover {
    background-color: transparent !important;
    color: #DD5944 !important;
  }
  .menu-options > div,
  .menu-options > div > a {
    color: #AAA !important;
  }
  .menu-options > div:hover,
  .menu-options > div > a:hover {
    background: #2F343F !important;
    color: #DD5944 !important;
  }
  .saveButton .topBarButtonTextContainer {
    color: inherit !important;
  }
  .undo-button.enabled:hover,
  .redo-button.enabled:hover {
    opacity: 1 !important;
    background: none !important;
  }
  .saveButton,
  .showCompletedButton,
  #buttonBar,
  #helpButton {
    border: none !important;
    background: none;
  }
  #savedViewHUDButton {
    width: 37px !important;
    height: 37px !important;
    margin: -5px 0 0 0 !important;
    background: url('https://workflowy.com/media/i/star-icon-empty.svg') !important;
    background-size: 23px !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-color: #2F343F !important;
    border-radius: 0px !important;
  }
  #savedViewHUDButton:hover,
  #savedViewHUDButton:active {
    background-color: #DD5944 !important;
  }

  .errorMessage {
    padding: 4px 12px !important;
    margin-top: -1px !important;
    color: #2F343F !important;
    background-color: #DD5944 !important;
    border: none !important;
  }

  /* Starred pages */
  #pageContainer,
  .savedViewPageSearchStringContainer {
    margin-top: 0
  }
  #savedViewHUD {
    background: #383C4A !important;
    width: 224px !important;
    top: 35px !important;
    left: 120px !important;
  }
  #savedViewHUDPageContainer {
    padding: 0 !important;
  }
  .selectedSavedViewPage {
    box-shadow: none !important;
    border: none !important;
    top: 0 !important;
  }
  .savedViewPage {
    padding: 0.6em 1em !important;
    margin: 0 !important;
    font-size: 14px !important;
    font-weight: bold !important;
    display: block !important;
    height: auto !important;
    text-align: left;
    background: #383C4A !important;
    border: none !important;
    color: #AAAAAA !important;
    border-radius: 0px !important;
    width: auto !important;
    font-family: "Helvetica Neue",Arial,sans-serif !important;
  }
  .savedViewPage:hover {
    background: #2F343F !important;
    border: none !important;
    color: #DD5944 !important;
    border-radius: 0px !important;
  }
  /* Save indicator */
  .saveButton_saving {
    color: #AAA !important;
  }
  .saveButton_saved {
    color: #DD5944 !important;
  }
  
  /* Item controls */
  #controlsLeft,
  #itemSelectionControls,
  #moreControls {
    padding: 0 !important;
    margin: 0 !important;
  }
  #controlsLeft,
  #itemSelectionControls {
    width: auto !important;
    background: #495157 !important;
    border: none !important;
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23) !important;
    border-radius: 0px !important;
  }
  .selected .project #controlsLeft>.mouse-helper:before {
    border-bottom: 15px solid #495157 !important;
  }
  #controlsLeft a,
  #controlsLeft div,
  #itemSelectionControls a,
  #itemSelectionControls div {
    color: #AAAAAA !important;
    font-size: 13px !important;
    margin: 0 !important;
    padding: 7px 17px !important;
    border-radius: 0px !important;
  }
  #controlsLeft a:hover,
  #itemSelectionControls a:hover {
    color: #DD5944 !important;
    text-decoration: none !important;
    background-color: #2F343F !important;
  }
  #controls #move {
    background: url(https://workflowy.com/media/i/move_icon_white.png) no-repeat !important;
  }
  .share .sharedName {
    text-shadow: none !important;
    color: #DD5944 !important;
  }
  .delete .sharedName {
    text-shadow: none !important;
    color: #DD5944 !important;
  }
  
  /* Help panel */
  #keyboardShortcutHelper {
    box-shadow: none !important;
    border: none !important;
  }
  #keyboardShortcutHelper > div {
    background: #2F343F !important;
    color: #AAAAAA !important;
  }
  #keyboardShortcutHelper > div:hover {
    color: #DD5944 !important;
  }
  #keyboardShortcutHelper td {
    background: #495157 !important;
    color: #AAAAAA !important;
    padding: 0.6em 1em !important;
  }
  #keyboardShortcutHelper td {
    border: none !important;
  }
  
}
