css:
@-moz-document domain("web.whatsapp.com"){
/* Common */

.app-wrapper-web .app {
	width: 500px;
}
.three .pane-three {
	min-width: 250px;
}
.message-system {
	visibility: hidden;
	height: 0px;
}
/* left panel controls */

.pane-list-header {
	padding-right: 0px;
}
.menu-horizontal>span>.menu-item {
	padding: 0px;
}
/*side bar size change*/

.pane-one {
	flex-basis: 95px;
	min-width: 105px;
	transition: flex-basis 0.1s, min-width 0.1s;
}
.pane-one:hover {
	flex-basis: 500px;
}
.app .pane-two {
	width: 100%;
}
/* Left alignment */

body {
	margin: 0;
	padding: 0;
}
.app {
	position: absolute;
	left: 10px;
	width: 300px;
}
/* friend description */
.pane-header .chat-title {
	color: #eee;
}
.pane-header .chat-title:hover {
	color: gray;
}
.pane-chat .icon-user-default{
filter: blur(5px); 
-webkit-filter: blur(5px); 
-moz-filter: blur(5px);
-o-filter: blur(5px); 
-ms-filter: blur(5px);
}
.pane-chat .icon-user-default:hover{
	filter:none;
	-webkit-filter: none; 
-moz-filter: none;
-o-filter: none; 
-ms-filter: none;
	zoom: 150%;
}

/* Compose Adjustments */

.input-placeholder {
	font-size: 14px;
}
.block-compose {
	padding: 9px 6px;
}
.block-compose .btn-emoji {
	margin-right: 5px;
	margin-bottom: 7px
}
.block-compose .input-container {
	margin-right: -17px;
}
/* Chatlist */
/* chat general */

.chatlist .avatar {
	height: 35px !important;
	width: 35px !important;
filter: blur(5px);
-webkit-filter: blur(5px); 
-moz-filter: blur(5px);
-o-filter: blur(5px); 
-ms-filter: blur(5px);
}
.chatlist .avatar:hover {
	zoom: 200%;
	filter: none;
		-webkit-filter: none; 
-moz-filter: none;
-o-filter: none; 
-ms-filter: none;
}
.avatar,
.avatar-image {
	border-radius: 10%;
	overflow: inherit;
}
/* chat normal list */

.chat-status {
	color: rgba(0, 0, 0, 0.0);
}
.chat-drag-cover .chat-title {
	color: rgba(0, 0, 0, 0.0);
}
/* chat normal list hover */

.chat-title:hover {
	color: gray;
}
.chat-status:hover {
	color: gray;
}
.chatlist .chat:hover {
	background-color: #d4d4d4;
}
/* chat active */

.chatlist .chat.active {
	background-color: #676767;
}
.chat.active .chat-status {
	color: rgba(0, 0, 0, 0.0);
}
.chat.active .chat-status:hover {
	color: rgba(255, 255, 255, 0.9);
}
.chat.active .chat-title:hover {
	color: rgba(255, 255, 255, 0.9);
}
.chat.active .chat-meta {
	color: rgba(255, 255, 255, 0.9);
}
/* chat unread */

*.chatlist .unread .chat-title {
	color: rgba(0, 0, 0, 0.0);
}
*.chatlist .unread .chat-title:hover {
	color: rgba(0, 0, 0, 0.5);
}
.chatlist .unread .chat-status {
	color: rgba(0, 0, 0, 0.0);
}
.chatlist .unread .chat-status:hover {
	color: rgba(0, 0, 0, 0.5);
}
/* message conversations */

.message-text {
	color: rgba(0, 0, 0, 0.0);
}
.message-text:hover {
	color: rgba(0, 0, 0, 0.8);
}
.image-thumb {
	width: 200px !important;
	height: 200px !important;
}
.image-thumb-body {
	width: 200px !important;
	height: 200px !important;
	filter: blur(15px);
    -webkit-filter: blur(15px); 
-moz-filter: blur(15px);
-o-filter: blur(15px); 
-ms-filter: blur(15px);
}
.message-text .emoji {
	background-size: 0px;
}
.message-text:hover .emoji {
	background-size: initial;
}
.message-chat {
	max-width: 85%;
}
.context.context-in {
	width: 20px;
}
.message-text a {
	color: rgba(0, 0, 0, 0.0);
}
.message-text:hover a {
	color: rgba(0, 0, 0, 0.8);
}
.link-preview-title {
	color: rgba(0, 0, 0, 0.0);
}
.link-preview-title:hover {
	color: rgba(0, 0, 0, 0.9);
}
.link-preview-media {
filter: blur(5px); 
-webkit-filter: blur(5px); 
-moz-filter: blur(5px);
-o-filter: blur(5px); 
-ms-filter: blur(5px);
}
.link-preview-media:hover {
	filter: inherit;
}
.link-preview-source {
	color: rgba(0, 0, 0, 0.0);
}
.link-preview-source:hover {
	color: rgba(0, 0, 0, 0.9);
}
/* background */

.app-wrapper:after {
	background-color: #d8dbd6;
}
.drawer-container-right,
.pane-chat {
	background-image: url("data:") !important;
	background-color: rgba(82, 82, 80, 0.9) !important;
}

.chat-secondary .ellipsify {
	visibility: hidden;
}
.single-emoji {
filter: blur(10px); 
-webkit-filter: blur(10px); 
-moz-filter: blur(10px);
-o-filter: blur(10px); 
-ms-filter: blur(10px);
}
.single-emoji:hover {
filter: blur(0px); 
-webkit-filter: blur(0px); 
-moz-filter: blur(0px);
-o-filter: blur(0px); 
-ms-filter: blur(0px);
}
.icon-user-default-square{
filter: blur(10px); 
-webkit-filter: blur(10px); 
-moz-filter: blur(10px);
-o-filter: blur(10px); 
-ms-filter: blur(10px);
}
.icon-user-default-square:hover{
filter: blur(0px); 
-webkit-filter: blur(0px); 
-moz-filter: blur(0px);
-o-filter: blur(0px); 
-ms-filter: blur(0px);
}
}
