css:
@-moz-document domain("tivocommunity.com") {
html {
    background-color: rgb(235, 244, 250);
}
body {
    background-color: rgb(250, 250, 250);
}
div.pageWidth {
    background-color: rgb(235, 244, 250);
}
a:link,
a:visited,
a:hover,
a:active,
a:focus,
.titleBar h1 {
    color: rgb(100, 100, 100);
}
.nodeList .categoryStrip {
    border-bottom: 3px solid rgb(205, 100, 255);
    background: rgb(135, 144, 150);
}
.sharePage .textHeading {
    color: rgb(132, 79, 255);
}
.textHeading,
.xenForm .sectionHeader {
    border-bottom: 1px solid rgb(205, 100, 255);
}
.sectionFooter {
    color: rgb(132, 79, 255);
    border-bottom: 1px solid rgb(205, 100, 255);
}
.redactor_box {
    border: 1px solid rgb(205, 100, 255);
}
.messageList .message {
    background-color: rgb(235, 244, 250);
    padding: 0px;
    margin-bottom: 15px;
    border: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
}
.quickReply {
    background-color: rgb(235, 244, 250);
    padding: 10px;
    margin-bottom: 15px;
    border: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
}
.XenBase .nodeList .node.level_1 {
    border: 1px solid rgb(205, 100, 255);
}
.XenBase .messageSimple,
.XenBase .profilePage .eventList li,
.XenBase .searchResult {
    background-color: rgb(235, 244, 250);
    padding: 10px;
    margin-bottom: 5px;
    border: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
}
.nflj_showcase_search_form .xenForm,
.nflj_sportsbook_search_form .xenForm {
    background-color: rgb(235, 244, 250);
    padding: 10px;
    border: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
}
.messageHeading {
    font-weight: bold;
    font-size: 12px;
    color: rgb(0, 0, 0);
    padding: 2px 3px;
    margin-bottom: 10px;
    overflow: hidden;
    position: relative;
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#a79fff+0,c59fff+2,a19fff+5,aa9dff+46,bd9fff+94,ca9fff+98,e79fff+100 */
    
    background: #a79fff;
    /* Old browsers */
    
    background: -moz-linear-gradient(top, #a79fff 0%, #c59fff 2%, #a19fff 5%, #aa9dff 46%, #bd9fff 94%, #ca9fff 98%, #e79fff 100%);
    /* FF3.6-15 */
    
    background: -webkit-linear-gradient(top, #a79fff 0%, #c59fff 2%, #a19fff 5%, #aa9dff 46%, #bd9fff 94%, #ca9fff 98%, #e79fff 100%);
    /* Chrome10-25,Safari5.1-6 */
    
    background: linear-gradient(to bottom, #a79fff 0%, #c59fff 2%, #a19fff 5%, #a9dff 46%, #bd9fff 94%, #ca9fff 98%, #e79fff 100%);
    /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    /*filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#a79fff', endColorstr='#e79fff', GradientType=0);*/
    /* IE6-9 */
}
/* Provide a gentle shadow behind each post of a thread */

ol#messageList.messageList li {
    box-shadow: 5px 5px 15px rgba(0, 0, 0, 0.25);
}
.XenBase .navTabs .navTab.selected .tabLinks {
    margin-top: 2px;
    background-color: rgb(175, 184, 210);
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
}
.navTabs .navTab.selected .tabLinks {
    background-color: rgb(235, 244, 250);
    width: 100%;
    padding: 0;
    border: none;
    overflow: hidden;
    zoom: 1;
    position: absolute;
    left: 0px;
    top: 45px;
    height: 40px;
}
.navTabs .navLink,
html .navTabs .navTab.selected .navLink {
    background-color: rgb(179, 179, 179);
    border: 1px solid rgb(205, 100, 255);
}
.navTabs .navTab.forums.selected .navLink:hover {
    color: rgb(20, 20, 20);
    background-color: rgb(235, 244, 250);
}
.navTabs .navTab.PopupClosed:hover .navLink,
.navTabs .navTab.PopupClosed:hover .SplitCtrl {
    color: rgb(20, 20, 20);
    background-color: rgb(235, 244, 250);
}
.navBarHelper {
    background-color: rgb(235, 244, 250);
    position: absolute;
    box-sizing: border-box;
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#a79fff+0,c59fff+2,a19fff+5,aa9dff+46,bd9fff+94,ca9fff+98,e79fff+100 */
    
    background: #a79fff;
    /* Old browsers */
    
    background: -moz-linear-gradient(top, #a79fff 0%, #c59fff 2%, #a19fff 5%, #aa9dff 46%, #bd9fff 94%, #ca9fff 98%, #e79fff 100%);
    /* FF3.6-15 */
    
    background: -webkit-linear-gradient(top, #a79fff 0%, #c59fff 2%, #a19fff 5%, #aa9dff 46%, #bd9fff 94%, #ca9fff 98%, #e79fff 100%);
    /* Chrome10-25,Safari5.1-6 */
    
    background: linear-gradient(to bottom, #a79fff 0%, #c59fff 2%, #a19fff 5%, #a9dff 46%, #bd9fff 94%, #ca9fff 98%, #e79fff 100%);
    /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    /*filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#a79fff', endColorstr='#e79fff', GradientType=0);*/
    /* IE6-9 */
    
    width: 100%;
    height: 45px;
}
#content .pageContent {
    background-color: rgb(235, 244, 250);
    padding: 10px 0px;
    position: relative;
}
.footer a:link,
.footer a:visited {
    font-weight: bold;
    color: rgb(64, 64, 64);
    display: block
}
.footer a:hover {
    font-weight: bold;
    color: rgb(240, 240, 240);
    display: block
}
.footer .pageContent {
    font-size: 12px;
    color: rgb(255, 255, 255);
    background-color: rgb(36, 36, 36);
    overflow: hidden;
    zoom: 1;
    position: relative;
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#a79fff+0,c59fff+2,a19fff+5,aa9dff+46,bd9fff+94,ca9fff+98,e79fff+100 */
    
    background: #a79fff;
    /* Old browsers */
    
    background: -moz-linear-gradient(top, #a79fff 0%, #c59fff 2%, #a19fff 5%, #aa9dff 46%, #bd9fff 94%, #ca9fff 98%, #e79fff 100%);
    /* FF3.6-15 */
    
    background: -webkit-linear-gradient(top, #a79fff 0%, #c59fff 2%, #a19fff 5%, #aa9dff 46%, #bd9fff 94%, #ca9fff 98%, #e79fff 100%);
    /* Chrome10-25,Safari5.1-6 */
    
    background: linear-gradient(to bottom, #a79fff 0%, #c59fff 2%, #a19fff 5%, #aa9dff 46%, #bd9fff 94%, #ca9fff 98%, #e79fff 100%);
    /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    /*filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#a79fff', endColorstr='#e79fff', GradientType=0);*/
    /* IE6-9 */
}
.node .nodeText {
    background-color: rgb(235, 244, 250);
    padding: 10px;
    margin-right: 270px;
    margin-left: 65px;
    border-right: 1px solid rgb(205, 100, 255);
    border-left: 1px solid rgb(205, 100, 255);
    box-sizing: border-box;
    min-height: 59px;
}
#forums .node.primaryContent {
    background-color: rgb(155, 254, 224);
    padding: 10px;
    border-bottom: 1px solid rgb(205, 100, 255);
}
.XenBase .messageSimple,
.XenBase .profilePage .eventList li,
.XenBase .searchResult {
    background-color: rgb(235, 244, 250);
    padding: 10px;
    margin-bottom: 5px;
    border: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
}
.sectionMain {
    background-color: rgb(235, 244, 250);
    padding: 10px;
    margin: 10px auto;
    border: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
}
body .itemPageNav a {
    border: 1px solid rgb(205, 100, 255);
}
body .itemPageNav a:hover {
    color: rgb(110, 110, 110);
    background-color: rgb(255, 254, 224);
    border-color: rgb(205, 100, 255);
}
.PageNav a {
    background-color: rgb(235, 244, 250);
    border: 1px solid rgb(205, 100, 255);
}
.PageNav a:hover,
.PageNav a:focus {
    color: rgb(205, 100, 255);
    text-decoration: none;
    background-color: rgb(215, 224, 230);
    border-color: rgb(205, 100, 255);
}
.PageNav a.currentPage {
    color: rgb(248, 248, 248);
    background-color: rgb(185, 194, 220);
    border-color: rgb(205, 100, 255);
    position: relative;
}
a.PageNavPrev,
a.PageNavNext {
    background-color: transparent;
    border: 1px none black;
}
.primaryContent {
    background-color: rgb(235, 244, 250);
    padding: 10px;
    border-bottom: 1px solid rgb(205, 100, 255);
}
.tabs {
    font-size: 12px;
    background-color: rgb(235, 244, 250);
    padding: 0 10px;
    border-bottom: 1px solid rgb(205, 100, 255);
    word-wrap: normal;
    min-height: 23px;
    _height: 23px;
    display: table;
    width: 100%;
    *width: auto;
    box-sizing: border-box;
}
.tabs li a,
.tabs.noLinks li {
    color: rgb(20, 20, 20);
    text-decoration: none;
    background-color: rgb(235, 244, 250);
    padding: 0 8px;
    margin-right: -1px;
    margin-bottom: -1px;
    border: 1px solid rgb(205, 100, 255);
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    display: inline-block;
    line-height: 30px;
    cursor: pointer;
    outline: 0 none;
    white-space: nowrap;
    word-wrap: normal;
    height: 30px;
}
.Menu .primaryContent {
    background-color: rgba(235, 244, 250, 0.96);
}
#StackAlerts .stackAlertContent {
    padding: 10px;
    padding-right: 30px;
    color: rgb(20, 20, 20);
    border-radius: 0px;
    border: solid 2px rgb(205, 100, 255);
    background-color: rgb(155, 254, 224);
    font-size: 12px;
    font-weight: bold;
    box-shadow: 2px 2px 5px 0 rgba(0, 0, 0, 0.4);
}
#calroot {
    margin-top: -1px;
    width: 198px;
    padding: 2px;
    background-color: rgb(235, 244, 250);
    font-size: 11px;
    border: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
    box-shadow: 0 0 7px rgba(37, 37, 37, 0.46);
    z-index: 7500;
}
#QuickSearch {
    top: -36px;
    background-color: rgb(255, 254, 237);
    box-shadow: 5px 5px 25px rgba(0, 0, 0, 0.5);
}
#QuickSearch.active {
    background-color: rgb(235, 244, 250);
    padding-bottom: 5px;
    box-shadow: 5px 5px 25px rgba(0, 0, 0, 0.5);
}
.tagList .tag:hover {
    text-decoration: none;
    background-color: rgb(155, 254, 224);
    border-color: rgb(205, 100, 255);
}
.attachedFiles {
    border: 1px solid rgb(205, 100, 255);
}
.attachedFiles .attachedFilesHeader {
    padding: 1px 2px;
    border-left: 5px solid rgb(205, 100, 255);
}
.attachedFiles .attachmentList {
    padding: 10px 10px 10px 10px;
    background: rgb(249, 249, 249) url('http://www.tivocommunity.com/community/styles/tivoc/xenforo/gradients/category-23px-light.png') repeat-x top;
    border-radius: 5px;
}
.attachment .boxModelFixer {
    background-color: rgb(235, 244, 250);
    margin: 0px;
}
.XenBase .attachedFiles .attachedFilesHeader {
    padding-top: 1px;
    padding-bottom: 2px;
}
.bbCodeBlock {
    border: 1px solid rgb(205, 100, 255);
}
.bbCodeBlock .type {
    font-size: 10px;
    font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    color: rgb(20, 20, 20);
    background-color: rgb(220, 200, 240);
    padding: 1px 2px;
    border-left: 5px solid rgb(205, 100, 255);
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}
.bbCodeQuote .quoteContainer {
    overflow: hidden;
    position: relative;
    font-style: italic;
    font-size: 12px;
    background-color: rgb(220, 220, 240);
    padding: 10px;
    border-top: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
}
.bbCodeQuote .quoteContainer .quoteExpand {
    background-color: rgb(220, 220, 240);
}
.bbCodeBlock pre,
.bbCodeBlock .code {
    background-color: rgb(220, 220, 240);
    border-top: 1px solid rgb(205, 100, 255);
}
.bbCodeImageFullSize {
    position: absolute;
    z-index: 50000;
    background-color: rgb(155, 254, 224);
}
.hasJs .bbCodeSpoilerText {
    border: 1px solid rgb(205, 100, 255);
}
.bbCodeBlock .type {
    border-left: 5px solid rgb(205, 100, 255);
}
.breadcrumb {
    font-size: 12px;
    background-color: rgb(205, 214, 220);
    border: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
    overflow: hidden;
    zoom: 1;
    max-width: 100%;
    height: 24px;
}
.breadcrumb .crust a.crumb {
    cursor: pointer;
    text-decoration: none;
    background-color: rgb(205, 214, 220);
    padding: 0 10px 0 18px;
    margin-bottom: -1px;
    border-bottom: 1px solid rgb(205, 100, 255);
    outline: 0 none;
    -moz-outline-style: 0 none;
    display: block;
    line-height: 24px;
}
.breadcrumb .crust .arrow {
    border-left-color: rgb(205, 100, 255);
    -moz-border-right-colors: rgb(205, 100, 255);
}
.breadcrumb .crust .arrow span {
    border: 12px solid transparent;
    border-right: 1px none black;
    border-left-color: rgb(205, 214, 220);
    -moz-border-right-colors: rgb(205, 100, 255);
    display: block;
    position: absolute;
    left: -13px;
    top: -12px;
    z-index: 51;
    white-space: nowrap;
    overflow: hidden;
    text-indent: 9999px;
    width: 0px;
    height: 0px;
}
.breadcrumb .crust:hover a.crumb {
    color: rgb(132, 79, 255);
}
.button {
    font-style: normal;
    font-size: 12px;
    font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    color: rgb(248, 248, 248);
    background-color: rgb(179, 179, 179);
    padding: 0px 6px;
    border: 1px solid rgb(179, 179, 179);
    border-radius: 0px;
    text-align: center;
    outline: none;
    line-height: 28px;
    display: inline-block;
    cursor: pointer;
    box-sizing: border-box;
    box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    height: 30px;
}
.userBanner.bannerOrange {
    color: black;
    background-color: thistle;
    border-color: rgb(205, 100, 255);
}
.userBanner.bannerOrange.wrapped span {
    background-color: #AFC520;
}
.userBanner.bannerRed {
    color: black;
    background-color: thistle;
    border-color: rgb(205, 100, 255);
}
.userBanner.bannerRed.wrapped span {
    background-color: #AFC520;
}
.discussionList .sectionHeaders {
    border-bottom: 3px solid rgb(205, 100, 255);
    background: rgb(135, 144, 150);
}
.discussionListItem {
    background-color: rgb(235, 244, 250);
    border-bottom: 1px solid rgb(205, 100, 255);
}
.discussionListItem .noteRow {
    background-color: rgb(235, 244, 250);
    padding: 5px;
    text-align: center;
    font-size: 11px;
    color: rgb(36, 36, 36);
}
.DiscussionListOptions {
    border: 1px solid rgb(205, 100, 255);
}
.XenBase .discussionListItem .main {
    border-right: 1px solid rgb(205, 100, 255);
    border-left: 1px solid rgb(205, 100, 255);
}
.XenBase .discussionListItem .lastPost {
    border-right: 1px solid rgb(205, 100, 255);
    border-left: 1px solid rgb(205, 100, 255);
}
.XenBase .discussionListItem .posterAvatar {
    background-color: rgba(200, 200, 200, 0.6);
    border-right: 1px solid rgb(205, 100, 255);
    border-left: 1px solid rgb(205, 100, 255);
}
.XenBase .discussionListItem .stats {
    background-color: rgba(200, 200, 200, 0.6);
    border-right: 1px solid rgb(205, 100, 255);
    border-left: 1px solid rgb(205, 100, 255);
}
.discussionListItem.sticky {
    background-color: rgb(225, 234, 250);
}
.message .likesSummary {
    background-color: rgb(225, 234, 240);
    padding: 5px;
    margin-top: 10px;
    border: 0px;
    border-top: 1px solid rgb(205, 100, 255);
    border-radius: 0px;
    zoom: 0.75;
}
.message .signature {
    background-color: rgb(225, 234, 240);
    font-size: 60%;
    color: rgb(110, 110, 110);
    padding: 5px 0 0;
    margin-top: 5px;
    border-top: 1px dashed rgb(205, 100, 255);
}
.messageUserBlock {
    zoom: 0.8;
    color: rgb(150, 88, 249);
    border: 1px solid rgb(205, 100, 255);
}
.messageUserBlock a.username {
    zoom: 0.8;
    color: rgb(132, 79, 255);
}
.messageUserBlock .arrow {
    border-left-color: rgb(205, 100, 255);
    -moz-border-left-colors: rgb(205, 100, 255);
}
.tabs li.active a,
.tabs.noLinks li.active {
    font-weight: bold;
    background-color: rgb(175, 184, 210);
    padding-bottom: 1px;
    border-bottom: 1px none black;
}
a.callToAction {
    background-color: rgb(179, 179, 179);
}
a.callToAction span {
    background-color: rgb(179, 179, 179);
}
a.callToAction:hover span {
    color: rgb(100, 100, 100);
}
.avatar img,
.avatar .img,
.avatarCropper {
    border: 1px solid rgb(205, 100, 255);
}
.messageUserBlock div.avatarHolder {
    background-color: rgb(255, 255, 255);
    padding: 5px;
    border-radius: 0px;
    text-align: center;
    position: relative;
}
.xbStickyBar {
    border-bottom: 1px solid rgb(205, 100, 255);
}
#DiscussionListOptionsHandle a {
    background-color: rgb(235, 244, 250);
    border-color: rgb(205, 100, 255);
}
.userBanner.bannerOrange {
    border-color: rgb(130, 68, 229);
}
.userBanner.bannerOrange.wrapped span {
    background-color: rgb(130, 68, 229);
}
.thread_view .threadAlerts {
    border: 1px solid rgb(205, 100, 255);
}
.memberListItem {
    border: 1px solid rgb(205, 100, 255);
}
.textCtrl {
    border: 1px solid rgb(205, 100, 255);
}
.sidebar .secondaryContent,
.xengallerySideBar .secondaryContent {
    border: 1px solid rgb(205, 100, 255);
}
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3,
.XenBase .xengallerySideBarContainer .xengallerySideBar .section h3 {
    border-bottom: 3px solid rgb(205, 100, 255);
}
.search_form .xenForm,
.search_form_post .xenForm,
.search_form_profile_post .xenForm,
.search_form_resource_update .xenForm,
.tag_search .xenForm,
.xengallery_search_form_media .xenForm,
.nflj_showcase_search_form .xenForm,
.nflj_sportsbook_search_form .xenForm {
    background-color: rgb(235, 244, 250);
    border: 1px solid rgb(205, 100, 255);
}
.blockLinksList a:hover {
    text-decoration: bold;
    color: rgb(135, 104, 250) !important;
}
.ugc a:link,
.ugc a:visited,
.ugc a:active {
    color: rgb(50, 50, 50);
}
.ugc a:hover {
    color: rgb(150, 150, 150);
}
div#QuoteSelected.xenTooltip {
    background: rgb(100, 100, 100);
    color: rgb(205, 220, 255);
}
div#QuoteSelected.xenTooltip a {
    color: rgb(205, 220, 255);
}
ul.secondaryContent.blockLinksList {
    display: -webkit-box;
    /* OLD - iOS 6-, Safari 3.1-6 */
    
    display: -moz-box;
    /* OLD - Firefox 19- (buggy but mostly works) */
    
    display: -ms-flexbox;
    /* TWEENER - IE 10 */
    
    display: -webkit-flex;
    /* NEW - Chrome */
    
    display: flex;
    /* NEW, Spec - Opera 12.1, Firefox 20+ */
    
    list-style: none;
}
ul.secondaryContent.blockLinksList li {
    margin: -1px;
}
ul.secondaryContent.blockLinksList li:nth-child(1) {    /* Mark Forums Read */
    -webkit-box-ordinal-group: 5;    /* OLD - iOS 6-, Safari 3.1-6 */
    -moz-box-ordinal-group: 5;    /* OLD - Firefox 19- */
    -ms-flex-order: 5;    /* TWEENER - IE 10 */
    -webkit-order: 5;    /* NEW - Chrome */
    order: 5;    /* NEW, Spec - Opera 12.1, Firefox 20+ */
}
ul.secondaryContent.blockLinksList li:nth-child(2) {    /* Search Forums */
    -webkit-box-ordinal-group: 6;
    -moz-box-ordinal-group: 6;
    -ms-flex-order: 6;
    -webkit-order: 6;
    order: 6;
}
ul.secondaryContent.blockLinksList li:nth-child(3) {    /* Watched Forums */
    -webkit-box-ordinal-group: 1;
    -moz-box-ordinal-group: 1;
    -ms-flex-order: 1;
    -webkit-order: 1;
    order: 1;
}
ul.secondaryContent.blockLinksList li:nth-child(4) {    /* Watched Threads */
    -webkit-box-ordinal-group: 0;
    -moz-box-ordinal-group: 0;
    -ms-flex-order: 0;
    -webkit-order: 0;
    order: 0;
}
ul.secondaryContent.blockLinksList li:nth-child(5) {    /* New Posts */
    -webkit-box-ordinal-group: 2;
    -moz-box-ordinal-group: 2;
    -ms-flex-order: 2;
    -webkit-order: 2;
    order: 2;
}
ul.secondaryContent.blockLinksList li:nth-child(6) {    /* Threads You Ignore */
    -webkit-box-ordinal-group: 3;
    -moz-box-ordinal-group: 3;
    -ms-flex-order: 3;
    -webkit-order: 3;
    order: 3;
    /*-webkit-transform: rotate(-180deg);*/
}
ul.secondaryContent.blockLinksList li:nth-child(7) {    /* Forums You Ignore */
    -webkit-box-ordinal-group: 4;
    -moz-box-ordinal-group: 4;
    -ms-flex-order: 4;
    -webkit-order: 4;
    order: 4;
    /*-webkit-transform: rotate(-180deg);*/
}
}
