css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dimensionpeliculas.net") {
  /*Cuerpo*/
body {color:#D54E21;font-size: 12px;background:rgb(28, 28, 28); font-family: Tahoma, Geneva, sans-serif;}
  
  /*Caja contenedora peliculas*/
#caja{margin:20px auto;background: rgb(40, 40, 40); padding: 15px 10px 0px 0px !important;}
  
  /*Texto peliculas*/
  .poster h2 a:link, .poster h2 a:visited{font-size:10px;line-height: 12px;color:rgb(195, 195, 195);height: 25px;display: block;overflow: hidden; font-family: Tahoma, Geneva, sans-serif;}
  
  /*Caja contenedoras secundarias*/
  #sidebar ul li a {color:rgb(195, 195, 195); padding-left: 0px; font-size: 12px;line-height: 15px;margin: 5px 0;display: block;}/*Color texto*/
 .mwidget{background:rgb(50, 50, 50);display:inline-block;padding:1px!important;width: 298px; margin-bottom: 20px;-moz-border-radius: 0px 0px 3px 3px;-webkit-border-radius: 0px 0px 3px 3px;border-radius: 0px 0px 3px 3px;} 
  
  
  /*Texto derechos reservados*/
  #footer {padding:5px 2px 15px 2px;color:#ccc;font-size:12px;color:rgb(195, 195, 195); overflow:hidden; }
  
  /*Menu principal*/
  
  a.menu2{
font-family: 'Homenaje', sans-serif;
font-size:17px;
padding:13px 25px 0px 25px;
height:45px;
background:rgb(40, 40, 40); /*color de fondo*/
color:rgb(195, 195, 195); /*Color texto*/
}  
  #toppic {
width:1044px; /* Ancho del menú */
height:45px;
background:rgb(40, 40, 40);
margin:0 auto;padding:0 auto;
overflow:hidden;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
border-radius: 3px;
}
  /*Opción Selecionada*/
  a.menu2:hover {
background:rgb(50, 50, 50); 
color:#CE5043; 
}
  #top li ul {
margin-top: -13px;
z-index:100;
position: absolute;
display: none;
background-color:rgb(40, 40, 40); /* Color de fondo del contenedor de las subpestañas */
margin-left:-0px;
padding:10px 0;
border-radius: 0px 0px 3px 3px;
box-shadow:0 2px 2px rgba(0,0,0,0.6);
filter:alpha(opacity=87);
opacity:.87;
}
  #top li:hover ul a, #top li.hvr ul a {
color:rgb(195, 195, 195); /* Color del texto de los submenús */
background-color:transparent;
text-decoration:none;
}
  
  /*Buscador*/
  #search p {margin:0;font-size:85%;}
  #s {width: 190px;background: rgb(40, 40, 40);padding: 5px 5px 6px 5px;height: 20px;border: 2px solid rgb(28, 28, 28);border-right: none;color: #555;font-style:italic;font-size:13px; float: left;display: inline;-moz-border-radius-topleft: 5px;font-family: Tahoma, Geneva, sans-serif;
    -moz-border-radius-topright: 0px;
-moz-border-radius-bottomright: 0px;
-moz-border-radius-bottomleft: 3px;
-webkit-border-radius: 3px 0px 0px 3px;
border-radius: 3px 0px 0px 3px;}
  #searchsubmit {background: rgb(40, 40, 40);display: block;width: 25px;height: 31px;line-height: 30px;float: right;cursor: pointer;border: 2px solid rgb(28, 28, 28);border-left: none;}
 /*Fin menu principal*/ 
    
    /*Inicio Post*/
    
    /*Titulo Post*/
  a {color:rgb(195, 195, 195);text-decoration:none;}
    
    /*Contenedores Post*/
    fieldset {
border:0px solid rgb(28, 28, 28) ; /* Borde */
background:rgb(40, 40, 40); /* Color de fondo */
color:rgb(195, 195, 195); /* Color del texto de todo el contenido */
text-align: center;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
border-radius: 5px;
width: 620px;
margin:0px auto;
padding:0px 10px 10px 10px;
target:_blank
}
  /*Descarga*/
.titular { background: rgb(50,50,50); border:0px solid #E8E8E8; padding:5px; text-align:center; font-size:15px; margin:20px 0px 0px 0px;margin-top:10px;font-weight: bold;}
.enlaces { padding:10px 0; text-align:center; word-spacing:30px;background: rgb(60,60,60); border:0px solid #E8E8E8;line-height:20px;font-style:italic;}

/*Relacionados*/
  .tpost h2 a{
color: rgb(195, 195, 195);
font-size: 10px;
}
  #relacion {
background:rgb(50,50,50);
    /*Fin Post*/ 
    
    
  
}
}
