}

#contener_profil_param .menu_div_param div.div_tab_selector
{
	background-color: #313131;
	color: #777777;
}

#contener_profil_param
{
	background: #313131;
}

.stats_profil
{
	background-color: #292929;
}

#timeline_profil
{
	background-color: #292929;
}

#timeline_profil .white_triangle_timeline
{
	border-right: 10px solid #292929;
}

#timeline_profil div.content_timeline_div div.content a:first-child, #timeline_profil div.content_timeline_div div.content p:first-child
{
	color: #e3652d;
}

#timeline_profil div.content_timeline_div div.content a:first-child span.hot
{
	color: #8C071F;
}

#contener_profil_param .menu_div_param div.div_tab_selector:hover
{
	background-color: #292929;
	color: #31484e;
}

.mini_header_thread
{
	border-bottom: 1px solid #1f1f1f;
	background: #292929;
}

.title_thread_contener
{
	background: #292929;
	border-bottom: 1px solid #1f1f1f;
}

.title_thread_contener .left_cat_part > div.title a
{
	color: #bbbbbb;
}

.title_thread_contener .right_cat_part .info_bloc p a
{
	color: #e3652d;
}

.title_thread_contener .left_cat_part p a
{
	color: #fb915d;
}

.title_thread_contener .left_cat_part div.auteur_pagination_div > a, .title_thread_contener .left_cat_part div.auteur_pagination_div p.etc
{
	background: #636363;
}

.sub_categorie_contener .right_cat_part .info_bloc p a
{
	color: #e3652d;
}

.right_part_contener div.price_div div.price_border
{
	border: 1px solid rgba(217, 217, 217, 0);
}

