{
	background-color: #292929;
}

input[type="text"], input[type="tel"], input[type="password"], input[type="email"], input[type="search"], input[type="number"], textarea
{
	border: 0px solid #c5c5c5;
	background: #272424;
	color: #c7c7c7;
}

.background_color_violet
{
	background-color: #e3652d;
}

#post_left .selection_type_div label:before
{
	background: #983b12;
}

#contener_search_part .input_search
{
	background: white;
}

#post_deal_right div.online_instore_div .selected
{
