}

.option_deal_div > a.resoudre
{
	background: #272424 url(https://static.dealabs.com/images/deal_detail/icon_forum_solved.png) no-repeat 15px center;
}

.center_part_contener h1
{
	color: #e2e2e2;
}

#comment_contener
{
	background: #292929;
}

@-webkit-keyframes slideOutDown
{
	0%
{
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
	50%
{
		-webkit-transform: translateY(140px);
		transform: translateY(140px);
	}
	100%
{
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}

@keyframes slideOutDown
{
	0%
{
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
	50%
{
		-webkit-transform: translateY(140px);
		transform: translateY(140px);
	}
	100%
{
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}
}
