css:
@-moz-document domain("titlekeys.gq"), domain("titledb.com") {
/*This is the second in a series of Style Sheets made for a number of websites used by the 3DS hacking community*/
/*This is the sheet for the Title Key Site and Homebrew Title Database*/
/*Main Body Stuff*/

body {
    background-color: #333;
    color: #e2e2e2;
}
.form-control,
.panel {
    background-color: #222;
    color: #fff;
    border-color: #666;
}
/*Table Stuff*/

.table-bordered>tbody>tr>td,
.table-bordered>tbody>tr>th,
.table-bordered>tfoot>tr>td,
.table-bordered>tfoot>tr>th,
.table-bordered>thead>tr>td,
.table-bordered>thead>tr>th,
.table-bordered {
    border-color: #666;
}
.table>tbody>tr.active>td,
.table>tbody>tr.active>th,
.table>tbody>tr>td.active,
.table>tbody>tr>th.active,
.table>tfoot>tr.active>td,
.table>tfoot>tr.active>th,
.table>tfoot>tr>td.active,
.table>tfoot>tr>th.active,
.table>thead>tr.active>td,
.table>thead>tr.active>th,
.table>thead>tr>td.active,
.table>thead>tr>th.active {
    background-color: #444;
}
/*Button Stuff*/

.btn-success,
.panel-success>.panel-heading {
    background-color: #183015;
    border-color: #486045;
    color: #fff;
}
.btn-success:hover {
    background-color: #284025;
    border-color: #587055;
}
.btn-warning {
    background-color: #453020;
    border-color: #756050;
}
.btn-warning:hover {
    background-color: #554030;
    border-color: #857060;
}
.btn-primary,
.btn-info,
.panel-primary>.panel-heading {
    background-color: #282830;
    border-color: #585860;
}
.btn-primary:hover,
.btn-info:hover {
    background-color: #383840;
    border-color: #686870;
}
.btn-default {
    color: #fff;
    background-color: #444;
    border-color: #555;
}
.btn-default:hover {
    color: #fff;
    background-color: #555;
    border-color: #666;
}
input[type=file] {
    color: black;
    -webkit-filter: invert(100%);
    -moz-filter: invert(100%);
    -ms-filter: invert(100%);
    -o-filter: invert(100%);
    filter: invert(100%);
}
/*QR Code Popup*/

.modal-header {
    background-color: #222;
}
.close,
.close:active {
    color: #eee;
}
.close:hover {
    color: #fff;
}
/*Pagination*/

.pagination>.disabled>a,
.pagination>.disabled>a:focus,
.pagination>.disabled>a:hover,
.pagination>.disabled>span,
.pagination>.disabled>span:focus,
.pagination>.disabled>span:hover {
    color: #999;
    background-color: #444;
    border-color: #666;
}
.pagination>.active>a,
.pagination>.active>a:focus,
.pagination>.active>a:hover,
.pagination>.active>span,
.pagination>.active>span:focus,
.pagination>.active>span:hover {
    background-color: #282830;
    border-color: #585860;
}
.pagination>li>a,
.pagination>li>span {
    color: #63aae7;
    background-color: #555;
    border-color: #666;
}
.pagination>li>a:focus,
.pagination>li>a:hover,
.pagination>li>span:focus,
.pagination>li>span:hover {
    color: #73baf7;
    background-color: #666;
    border-color: #777;
}
/*Fix that one stupid looking button on Title Keys*/

a[href="/downloadmissingforencryption"] {
    font-size: 11px !important;
}
}
