css:
@-moz-document domain("xdiscuss.net") {
.navbar-brand img {
    content: url("http://i.imgur.com/29fKRzP.png");
}
.panel-primary>.panel-heading {
    background-color: #3D3D3D;
}
.navbar,
.navbar-default {
    background-color: #3D3D3D;
}
.panel-body {
    border: 3px solid #3D3D3D;
}
.panel-heading {
    padding: 10px 15px;
    border-bottom: 0px solid transparent;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
}
body {
    font-family: 'Comic Sans MS', Comic Sans MS;
    -webkit-font-smoothing: antialiased;
    background: #3D3D3D;
    -moz-background-size: cover;
    background-size: cover;
    -o-background-size: cover;
    -webkit-background-size: cover;
    background-image: url("http://images.rbxcdn.com/7b740ce2fe1bb189612252b3b93c5b83.jpg")
}
/*-----NAVBAR-----*/

.navbar,
.navbar-default {
    background-color: #3D3D3D;
    color: rgba(255, 255, 255, .84);
}
.navbar,
.navbar-default-default.header-cont[style] {
    box-shadow: none !important;
    margin: auto!important;
}
.navbar .navbar-nav>li>a:hover {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Comic Sans MS;
    font-weight: normal;
    font-size: 1.25em;
    text-decoration: underline;
}
.navbar .navbar-nav>li>a {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Comic Sans MS;
    font-weight: normal;
    font-size: 1.25em;
}
.navbar .navbar-brand {
    height: 60px;
    line-height: 30px;
    font-family: Comic Sans MS;
    color: #FFFFFF;
}
.navbar .navbar-brand>hover {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Comic Sans MS;
    font-weight: normal;
    font-size: 1.25em;
    text-decoration: underline;

}

/*-----MEDIA-----*/
.sidebar {
    position: fixed;
    top: 51px;
    bottom: 0;
    left: 0;
    z-index: 1000;
    display: block;
    padding: 20px;
    overflow-x: hidden;
    overflow-y: auto;
    background-color: #3D3D3D !important;
    border-right: 1px solid #3D3D3D;
}
.navbar-collapse.collapse {
    background-color: #3d3d3d !important;
}
.breadcrumb {
    padding: 8px 15px;
    margin-bottom: 23px;
    list-style: none;
    background-color: #3d3d3d !important;
    border-radius: 3px;
}
.footer {
    position: absolute;
    bottom: 0;
    width: 100%;
    height: 100px;
    background-color: #3d3d3d !important;
}
.well {
    background-color: #3d3d3d !important;
}
.panel-body {
    border: 3px solid #000000;
}
.btn-default {
    color: #ffffff !important;
    background-color: #3d3d3d !important;
    border-color: transparent;
}
.panel {
    background: #565656 !important;
}
.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
    padding: 8px;
    line-height: 1.846;
    vertical-align: top;
    border-top: 1px solid #000000;
}
.table>thead>tr>th {
    vertical-align: bottom;
    border-bottom: 2px solid #080808;
}
a {
    color: #FFFFFF !important;
    text-decoration: none;
}
body {
    font-family: 'Comic Sans MS', Comic Sans MS;
    font-size: 13px;
    line-height: 1.846;
    color: #ffffff;
    background-color: #ffffff;
}
.panel-footer {
    padding: 10px 15px;
    background-color: #3d3d3d !important;
    border-top: 1px solid #3d3d3d !important;
    border-bottom-right-radius: 2px;
    border-bottom-left-radius: 2px;
}
.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-nav>.open>a:focus {
    background-color: #3d3d3d;
    color: #212121;
}
.pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus {
    color: #3d3d3d;
    background-color: #3d3d3d;
    border-color: #3d3d3d;
    cursor: not-allowed;
}
.pagination>li>a, .pagination>li>span {
    position: relative;
    float: left;
    padding: 6px 16px;
    line-height: 1.846;
    text-decoration: none;
    color: #2196f3;
    background-color: #3d3d3d;
    border: 1px solid #3d3d3d;
    margin-left: -1px;
}
.pager .disabled>a, .pager .disabled>a:hover, .pager .disabled>a:focus, .pager .disabled>span {
    color: #3d3d3d;
    background-color: #3d3d3d;
    cursor: not-allowed;
}
.pager li>a, .pager li>span {
    display: inline-block;
    padding: 5px 14px;
    background-color: #3d3d3d;
    border: 1px solid #3d3d3d;
    border-radius: 15px;
}
.form-control {
    display: block;
    width: 100%;
    height: 37px;
    padding: 6px 16px;
    font-size: 13px;
    line-height: 1.846;
    color: #ffffff;
    background-color: transparent;
    background-image: none;
    border: 1px solid transparent;
    border-radius: 3px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    list-style: none;
    font-size: 13px;
    text-align: left;
    background-color: #3d3d3d;
    border: 1px solid #cccccc;
    border: 1px solid rgba(0,0,0,0.15);
    border-radius: 3px;
    -webkit-box-shadow: 0 6px 12px rgba(0,0,0,0.175);
    box-shadow: 0 6px 12px rgba(0,0,0,0.175);
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
}
.nav-tabs>li>a, .nav-tabs>li>a:focus {
    margin-right: 0;
    background-color: transparent;
    border: none;
    color: #666666;
    -webkit-box-shadow: inset 0 -1px 0 #ddd;
    box-shadow: inset 0 -1px 0 #565656;
    -webkit-transition: all 0.2s;
    -o-transition: all 0.2s;
    transition: all 0.2s;
}
.btn-primary {
    color: #ffffff;
    background-color: #284b67;
    border-color: transparent;
}
h1, h2, h3, h4, h5, h6 {
    color: white;
}
.btn-success {
    color: #ffffff;
    background-color: #215623;
    border-color: transparent;
}
.pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus {
    z-index: 3;
    color: #ffffff;
    background-color: #284b67;
    border-color: #284b67;
    cursor: default;
}
.center User {
    display: none;
}
span.panel-panel-primary > .panel-heading-center {
    display: none;
}
span.glyphcon.glyphcon-comment {
    display: none;
}
a:focus {
    color: #3D3D3D;
}
a:hover {
    color: #DD6900;
}
}
