css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vaughnlive.tv")  {



div.theWrapper {
    background-color: rgb(34, 34, 34);
    overflow: visible !important;
}

div.theMainContent, div.menu2Wrapper, div.topBarV2, div.topBarV2options, div.videoStatusText, div.theMenu, div.theBar, DIV.mainContent, div.topbar {
background: #222222 !important;
}

DIV#topBarV2toggle.topBarV2toggle.tooltip-bottom {
background: #222222 !important;
}

div.menuBtnBlue {
background: #222222 !important;
color: #e1e1e1 !important;
}

a {
background: #222222 !important;
color: #e1e1e1 !important;
}

body#vn_body {
background: #222222 !important; 
}

p.chatTagCaster {
background-image: linear-gradient(-45deg, #e1e1e1 58%, #222222 1px) !important;
color: #ff0000 !important;
}

p.chatTagMod {
background-image: linear-gradient(90deg, #ff0000, #222222, #ff0000, #222222, #ff0000) !important;
color: #e1e1e1 !important;
}

p.mvn_roundup_right {
height: 20px;
width:5px;
}

p.chatTag {
font-size: 0% !important;
}

.chat_vaughn_wrapper {
background: #222222 !important; 
color: #ff0000 !important;
}

span.chatLine {
color:#e1e1e1 !important;
}

a.chat_url {
color:#FFFF00 !important;
border-bottom: 1px solid rgb(255, 255, 0) !important;
}

a.chat_url:hover {
color:#ffffff !important;
border-bottom: 1px solid rgb(255, 255, 255) !important;
}

a.chat_url:visited {
color:#cc0000 !important;
border-bottom: 1px solid rgb(204, 0, 0) !important;
}

DIV.CV3setmoney {
display:none !important;
}

TEXTAREA#vn_chat_message.CV3input {
background: #222222 !important; 
color:#e1e1e1 !important;
}

#vn_chat_box {
color:#e1e1e1 !important;
background: #222222 !important; 
}

span.chatUsername {
text-shadow: 1px 1px 1px rgb(0, 0, 0) !important;
}

div.CV3input {
background-color: rgb(34, 34, 34) !important;
}

div.CV3dosend {
background: #222222 !important;
color: #555555 !important;

}

div.noticeLine {
background: #222222 !important;
color: #444444 !important;
}

div.abvsDynamic {
display: none !important; 
}
 
div.CV3pic {
background: #222222 !important;
}

div.CV3userboxContainer {
background: #222222 !important;
}

div.modalv3_right {
background: #222222 !important;
}

div.modalv3_status {
color: rgb(225, 225, 225) !important;
background: black !important;
border-bottom: 0px solid rgb(48, 91, 138) !important;
}

div.CV3userbox {
background-color: #222222 !important;
}

div.CV3overlayLoading {
background: #222222 !important;
}

div.chatOptColLeft {
background: #222222 !important
}

div.chatOptSettings {
background-color: rgb(34, 34, 34) !important;
}

DIV#chatOptBtn_general.chatOptMenuItem.chatOptMenuItemAccentSelected {
background: #222222 !important;
color: #e1e1e1 !important;
}

div.CV3chattersListBox {
background-color: rgb(34, 34, 34);
}

div.CV3chattersListItem {
background: #222222 !important;
color: #e1e1e1 !important;
}

div.CV3chattersListHeader {
background: #191919 !important;
color: #ff0000 !important;
}


    
    
div.menu2Spacer {
width: 1% !important; 
background: #222222 !important; 
}
    
div.header_spread {
background: #222222 !important; 
}
    
div.header {
background: #222222 !important;
}
    
div.CV3noIMs {
background: #222222 !important;
}

div.chatBoxHeader {
background-color: #222222 !important;
}

div.CV3setspacer {
background: #222222 !important;
}
}
