css:
@-moz-document url-prefix("https://tools.ietf.org/html/rfc") {
  /* Color provided by: https://github.com/mrmrs/colors */

  .bgblue {
    background-color: #0074D9;
  }

  .bggreen {
    background-color: #2ECC40;
  }

  .bgyellow {
    background-color: #FFDC00;
  }

  .bgorange {
    background-color: #FF851B;
  }

  .bgred {
    background-color: #FF4136;
  }

  .bgwhite {
    background-color: #FFFFFF;
  }

  .bggrey {
    background-color: #AAAAAA;
  }

  .blue {
    color: #0074D9;
  }

  .green {
    color: #2ECC40;
  }

  .yellow {
    color: #FFDC00;
  }

  .orange {
    color: #FF851B;
  }

  .red {
    color: #FF4136;
  }

  .white {
    color: #FFFFFF;
  }

  .grey {
    color: #AAAAAA;
  }

  * {
    margin: 0;
    padding: 0;
    color: #111111;
  }

  body {
    margin: 0;
  }

  a {
    color: #0074D9;
  }

  a:visited {
    color: #B10DC9;
  }

  .pre.noprint.docinfo {
    width: 662px;
  }

  div.content {
    width: 49.7em !important;
    margin: 0 auto;
  }

  pre,
  span.pre {
    font-size: 1.15em;
  }

  hr {
    width: inherit !important;
  }
}
