css:
/* create by lofox  */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lilo.org") {
  
  html, body {background: #000}
  
  body {color: #e1e1e1}
  
  
  
    /* paramaitres   ---------------------------------------------  */   
 
 .fade {
background-color:rgba(102,102,102,0.5);
}
  
  
  .modal-content {

	background-color: #000;
	border: 1px solid #666;
	border: 1px solid rgba(255,255,255,.2);
	-webkit-box-shadow: 0 3px 9px rgba(0,0,0,.5);
	box-shadow: 0 3px 9px rgba(0,0,0,.5);
}
  
  
  
  .modal-header, .nav-tabs {
	border-bottom: 1px solid #1e1e1e;
}
  
  
  .modal-footer {
	border-top: 1px solid #1e1e1e;
}
  
  
  .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
	color: #bbb;
	background-color: #222;
	border: 1px solid #444;
}
  
  
  .nav-tabs > li > a:hover {
  color: #bbb;
	background-color: #222;
	border: 1px solid #444;
	}
  
  
  
  input, button, select, textarea {
	  color: #bbb;
	background-color: #222;
	border: 1px solid #444;
}
  
	
	.btn-default {
		  color: #bbb;
	background-color: #222;
	border: 1px solid #444;
}
	
	
	
	
	/*  home  ------------------------------------------------------   */  
	
	
	
	/* logo  */     
	

	#logo  {margin-top: 70px; margin-left: 10px}
	
	
	
	#searchbloc > div:nth-child(1)
	{
		background-color: #fff ; height: 220px; width: 220px; 
	border: 1px solid #ccc;    box-shadow : 3px 3px 0.5em 0.5em  rgba(204,204,204,0.5);
	border-bottom-left-radius : 150px;
  border-bottom-right-radius : 150px;
  border-top-left-radius : 150px;
  border-top-right-radius : 150px; 
	}
	
	
	
	/*   zone recherche    */   
	.input-group {
	 padding-top: 20px;
}
	
	.form-control {
 
	color: #e1e1e1;
	background-color: #222;
	background-image: none;
	border: 1px solid #333;
	-webkit-box-shadow: inset 0 1px 1px rgba(255,255,255,.075);
	box-shadow: inset 0 1px 1px rgba(255,255,255,.075);
	
}
	
	
	.form-control:focus {
	border-color: #056fb6;   /*  bleu  */
	outline: 0;
	-webkit-box-shadow: inset 0 1px 1px rgba(255,255,255,.075),0 0 8px rgba(5,111,182,.6);
	box-shadow: inset 0 1px 1px rgba(255,255,255,.075),0 0 8px rgba(5,111,182,.6);
}
	
	
	.ui-widget-content {
	border: 1px solid #666;
	background: #222;
	color: #e1e1e1;
}
	
	
	.ui-menu .ui-menu-item:hover   {
	border: 1px solid #666;
	background: #056fb6;
	color: #e1e1e1;
}
	
	
	
	/* favoris     */   
	
	
	.favorite {
	
	box-shadow: 1px 1px 5px 1px #262626;
	-webkit-box-shadow: 1px 1px 5px 1px #262626;
	-o-box-shadow: 1px 1px 5px 1px #262626;
	box-shadow: 1px 1px 5px 1px #262626;
	
}
	
	.favorite .info2 {
	background-color: #222;
	color: #e1e1e1;
}
	
	
	/*   head   */
	
	
	.thehome.fixedtop #searchbloc .containerSearch {
	background-color: #000; border-bottom: 1px solid #333;
	
}
	
	.fixedtop #logo_top {
filter:  brightness(130%)
}
	
	
	
	/*  fil d'actu  */
	
	
	.barTop {
	
	background-color: #056fb6;

}
	
	.logoTop{
filter:  brightness(60%) contrast(180%)
}
	
  
  .socialBaseProjects {
	
	background-color: #000;
}
	
	.socialproject {
	
	background-color: #222 !important;
}
	
	.socialImg , .projectText
	{
	background-color: #222;
}
	
	.thumbnail {
	background-color: #222;
	border: 1px solid #333;
}
	
	
	
	/*  RESULTATS RECHERCHE ----------------------------------------------------------------------------*/
	
	/*  web ----------------   */  
	
	/* head   */   
	
	
	#col1 {
		background-color: #000;
	border: 1px solid #333;
}
	
	/*  logo   */ 
	.bandeau #logolilo
	{
		background-color: #fff; border: unset; 
	}
	
	.bandeau .tablecell:first-child {
		background-color: #fff ; height: 40px; width: 40px; 
	border: 1px solid #ccc;    box-shadow : 1px 1px 0.2em 0.2em  rgba(204,204,204,0.5);
	border-bottom-left-radius : 40px;
  border-bottom-right-radius : 40px;
  border-top-left-radius : 40px;
  border-top-right-radius : 40px;
}
	
	#gsc-i-id1 ,#col1 .btn-default {
		margin-left: 20px;
	border: 1px solid #056fb6;
	}

   div.tablecell:nth-child(4) {
	padding-left:  20px ;
}
	
	
	.gsc-tabHeader.gsc-tabhInactive {
	border-left: 1px solid #333;
	border-right: 1px solid #333;
	border-top: 2px solid #333;
	border-bottom: 0;
	background: #222 !important;
	color: #e1e1e1;
	cursor: pointer;
}
	
	
	/* content fa9049 orange --    0097d8  bleu 2       */   
	
	.addivwhite {
	background-color: #111; border-left: 5px solid #111 ; 
	-webkit-box-shadow: 1px 1px 5px 1px #262626;
	-o-box-shadow: 1px 1px 5px 1px #262626;
	box-shadow: 1px 1px 5px 1px #262626;
}
	
	
	.result a {
	color: #006ced;  text-shadow: 1px 1px 0.0em #444;  
}
	
	.result a:visited {
	color: #BF00FF !important;
	text-shadow: 1px 1px 0.0em #444;
}
	
	
	.resulturl , div.result > a:nth-child(3) , .news .infos1 .from
	{
	color: #1da549 !important;
}

	.infoinfo {
	background: #002e84;
}
	
	#sidebar p ,.h3news
	{
	color: #fff;
}
	
	
	/* image ---------    */  
	
	.choose_search_engine > div {
	background-color: #222;
}
	
	.choose_search_engine {
	border: 3px solid #333;
	background-color: #0e72b5;
}
	
	.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
	color: #e1e1e1;
	background-color: #222;
	border-color: #333;
}
	
	.dropdown-menu {
	background-color: #222;
	border: 1px solid #333;
	border: 1px solid rgba(0,0,0,.15);
	-webkit-box-shadow: 0 6px 12px rgba(255,255,255,.175);
	box-shadow: 0 6px 12px rgba(255,255,255,.175)
}
	
	
	.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
	color: #e1e1e1;
	background-color: #056fb6;
}
	
	.dropdown-menu > li > a{
	color: #e1e1e1;
	
}
	
	
	/*  vid√©o    ----------------  */
	
	
	div.videodiv {
	border-top:  1px solid #056fb6;
}
	
	div.videodiv > h2 > a {   
		color: #e1e1e1;
	}
	div.videodiv > h2 > a:visited {
		color: #BF00FF !important;
	}
	
	/* maps  -------------------*/    
	
	.layers {
	background-color: #222;
	top: 6px;
}
	.layers button{
	
	background-color: #fff;
}
	
	
	
}
