css:
@-moz-document domain("toggl.com") {
.NavigationContainer__outerBox {
  background: #0b0b0b;
  border: 1px solid #000;
  box-shadow: 0 0 5px 1px #000; }

.TimeEntryForm__newTimeEntry {
  background: #0b0b0b;
  border: 1px solid #000;
  box-shadow: 2px 0 5px 1px #000; }

.Timer__button, .TimeEntryForm__discardTimeEntry {
  filter: brightness(75%); }

.Timer__button:focus, .Timer__button.Timer__active:focus, .ManualTimer__confirm:focus {
  border: none; }

.TimerModeSelector__mode, .TimeEntryForm__discardTimeEntry {
  border-left: 1px solid #000; }

.ProjectsPopdown__content {
  background-color: #222;
  border: 1px solid #000;
  box-shadow: 0 0 5px #000;
  margin: 0; }

input.ProjectsPopdown__filter {
  background: #171717;
  border: none !important; }

.WorkspaceSelector__container {
  border-bottom: 1px solid #000; }

.Project__container {
  width: 215px; }

.Project__container.Project__focused {
  background-color: #171717; }

.CreateNewProject__newProject {
  border-top: 1px solid #000; }

input.TagsPopdown__filter {
  border: none; }

.TagsPopdown__content {
  background: #222;
  margin: 0;
  border-top: 1px solid #000; }

.TagsPopdown__items a {
  background-color: #111;
  border: 1px solid #000; }

.TagsPopdown__items a:hover {
  background-color: #0b0b0b;
  border: 1px solid #000; }

.DateTimePopdown__popdownContent {
  background: transparent;
  border: 1px solid #000;
  box-shadow: 0 0 5px #000; }

.Cal__Header__root {
  background-color: #0b0b0b !important; }

.Cal__Header__date {
  color: #ccc; }

.Cal__Weekdays__root {
  background-color: #171717 !important; }

.Cal__List__root {
  background-color: #222; }

.Cal__Month__row:first-child li {
  background-color: #171717; }

.Cal__Month__row li, .Cal__Month__row, .Cal__Month__row.Cal__Month__partial, .Cal__Month__row.Cal__Month__partial:first-child li:first-child {
  border-bottom: none;
  border-left: none;
  box-shadow: none !important; }

.Cal__Day__root span {
  color: #999; }

.Cal__Day__root.Cal__Day__enabled.Cal__Day__highlighted:before, .Cal__Day__root.Cal__Day__enabled:active:before, .Cal__Day__root.Cal__Day__enabled:hover:before {
  background-color: #111;
  border-color: #111 !important; }

.TimeEntriesList__list {
  border: 1px solid #000;
  box-shadow: 0 0 5px #000;
  width: 80%;
  margin: auto;
  margin-bottom: 30px; }

.DayListItem__container {
  background: #0b0b0b;
  border-bottom: 1px solid #000;
  box-shadow: none;
  color: #ccc; }

.ListItem__item, .ListItem__item:first-child {
  background: #111;
  box-shadow: none;
  border-bottom: 1px solid #000; }
  .ListItem__item:hover, .ListItem__item:first-child:hover {
    background: #222222 !important; }

.ListItem__item:last-child {
  box-shadow: none;
  border-bottom: none; }

.TimeEntriesListGroupedItem__subEntriesCount span {
  background: #0b0b0b;
  border: 1px solid #000;
  color: #666; }

.TimeEntriesListGroupedItem__subEntriesCount.TimeEntriesListGroupedItem__isExpanded span {
  border: 1px solid #000;
  background-color: #0b0b0b; }

.ListItem__item.ListItem__isSubEntry {
  background: #222; }

.TagsResponsiveDisplay__dots {
  display: none; }

.TagsResponsiveDisplay__tag {
  background-color: #0b0b0b;
  border: 1px solid #000;
  color: #666; }

::-webkit-scrollbar {
  background: #111 !important;
  width: 10px !important; }

::-webkit-scrollbar-thumb {
  background: #050505 !important;
  border-radius: 10px; }

body {
  color: #999; }

a {
  color: #999; }

a:hover {
  color: #ccc; }

input[type=text] {
  background: transparent;
  color: #999; }

body, .TimerContainer__timerContainer {
  background: url("http://i.imgur.com/ohklSyB.png");
  background-repeat: repeat; }

.Project__color, li.ProjectTotalsChart__block, .Projects__trigger > span {
  -webkit-filter: hue-rotate(10deg) brightness(75%);
  filter: hue-rotate(10deg) brightness(75%); }

/*# sourceMappingURL=base.css.map */

}
