css:
@-moz-document domain("trello.com") {

/* I use the Scrum for Trello extension in addition */
 
body .list-card-labels {
  float: right;
  top: -2px;
  right: -6px;
  max-width: 200px;
  margin: 0;
  text-align: right;
}
 
body .list-card-labels .card-label {
  float: right;
  width: auto;
  height: 16px;
  line-height: 16px;
  padding: 0 3px;
  margin: 0 0 2px 2px;
  font-size: 9px;
  border-radius: 3px;
  overflow: visible;
}
 
body .list-card-details {
  padding: 4px 8px;
}
 
 
/* display card id */
 
body .card-short-id.hide {
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  height: 16px;
  line-height: 16px;
  padding: 0 3px;
  font-weight: bold;
  font-size: 11px;
  background: gold;
  border-radius: 3px 0;
}
 
body .has-cover .card-short-id.hide {
  border-radius: 0 0 3px 0;
}
 
body .list-card-title {
  margin: 16px 0 10px;
}
 
 
/* bottom edit link */
 
body .list-card-operation {
  top: auto;
  right: -2px;
  bottom: -2px;
  padding: 1px;
  border-radius: 3px;
  box-shadow: 1px 1px rgba(0,0,0,.3), -1px -1px white;
}
 
body .list-card {
  margin-top: 10px;
}
 
body .list-card:first-child {
  margin-top: 2px;
}
 
 
/* display total cards count */
 
body .list-header-num-cards.hide {
  display: inline-block;
  font-size: 11px;
}

/* i really want this to be global */
/* i really want this to be global */
#header {
  background: #2B2B2B !important;
  padding: 10px !important;
}

.header-user {
  top: 10px !important;
  right: 5px !important;
}

.board-header {
  height: 40px !important;
  background: #474747 !important;
}

.member {
  border-radius: 20px !important;
  border: none !important;
}

.member img {
  border-radius: 0 !important;
  border: none !important;
}

.list-area-wrapper {
  padding-left: 10px;
}

.list-area {
  width: 3500px !important;
}

.fancy-scrollbar::-webkit-scrollbar-track-piece {
  background: none !important;
}

.list-title .icon-menu {
  right: 0px !important;
  top: 8px !important;
}

.list-gradient-top,.list-gradient-bottom {
  display: none !important;
}

.fancy-scrollbar::-webkit-scrollbar-thumb:vertical,.fancy-scrollbar::-webkit-scrollbar-thumb:horizontal{
  background: #555 !important;
}


.list-header {
  box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.2) !important;
  padding: 10px !important;
  background: #fff !important;
  color: #CE8C14 !important;
  z-index: 10 !important;
  margin: 0 !important;
}

.list-header h2 {
  font-size: 16px !important;
}

.list-header-menu-icon {
  top: 5px !important;
  right: 0px !important;
}

.board-canvas {
  background-color: #fff !important;
  background-image: url('data:image/jpg;base64,') !important;
  background-size: cover !important;
}

#board {
  padding: 5px 0 0 0 !important;
}

.list {
  background: rgba(252, 252, 252, 0.5) !important;
  width: 350px !important;
  margin: 0 10px !important;
  padding: 0 !important;
}

.list-cards {
  padding: 8px !important;
}

.list-card {
  padding: 15px 15px 5px 15px !important;
  margin: 10px 0 !important;
}

.open-card-composer:hover {
  background: rgba(255, 255, 255, 0.5) !important;
  color: #666 !important;
  text-decoration: none !important;
}

.window-wrapper {
  background: #F9F9F9 !important;
  padding: 10px 20px !important;
  border-radius: 5px !important;
}

.board-widgets {
  background: rgba(255,255,255,0.98) !important;
}

.sidebar-hide-btn {
  top: 5px !important;
}

.atMention {
  background: #A3359F !important;
  border-radius: 5px !important;
  color: #fff !important;
  font-size: 12px !important;
}

.window {
  width: 790px !important;
}

code {
  font-family: Menlo,Monaco,"Courier New",monospace !important;
  background-color: #F7F7F9 !important;
  border: 1px solid #E1E1E8 !important;
  border-radius: 3px !important;
  padding: 3px 4px !important;
  font-size: 12px !important;
  color: #D14 !important;
}

.board-header {
    height: auto !important;
}

.list-card {
    max-width: inherit;
} 

.js-list {
    width: 392px;
}

.js-add-list {
    width: 100px;
}


.list {
    background: none;
    width: 380px !important;
}

/* open card */
body.fixed-content #content{
  width:890px !important;
}

div.window {
  width:890px !important;
}

div.window-main-col {
  width:672px !important;
}

}
