css:
@-moz-document domain("0nyan.ml"), domain("nullnyan.net"), domain("www.nullnyan.net"), domain("0nyan.space"), domain("www.0nyan.space") {
* {
  font-family: "xos4 Terminus", Terminus, Consolas, "Liberation Mono", Courier, monospace;
  font-size: 10pt;
}
html, body {
  background-color: #151515;
  color: #77cd3b;
  font-size: 1em;
}
body, .wrapper {
  background: #151515;
  color: #77cd3b;
}
.post {
  display: block;
  overflow: hidden;
  background: none repeat scroll 0 0 #292929;
  margin: 4px;
  border: 1px dotted #77cd3b;
}
.post:not(.popupPost) {
  width: 100%;
}
a, .logo, .fileType, [class^="icon"], [class*=" icon"], .topbar label, .postcounter, #postform .iconHelp::before, #postform #lock::before {
  color: #77cd3b;
  text-decoration: none;
}
aside a, aside .icon::before {
  color: #777777;
}
a {
  color: #68696b;
  padding: 0;
}
a:hover, aside a:hover {
  color: #dd0000 !important;
}
aside .icon:hover::before {
  color: #dd0000;
}
.post, .bubble, .help > div {
  color: #77cd3b;
}
aside div {
  font-weight: bold;
}
aside a, aside label, aside a:visited {
  background: none repeat scroll 0 0 #292929;
  border: 1px solid #393939;
}
.active, aside a:hover, aside label:hover, aside a.icon:hover {
  background: #101010 !important;
}
aside a:hover, aside label:hover, aside a.icon:hover {
  color: #dd0000 !important;
}
aside a.active {
  color: #77cd3b;
}
.reflink a {
  color: #68696b;
  font-weight: bold;
}
.refmap {
  font-size: small;
  font-style: normal;
}
.refmap a.postlink {
  font-size: small;
}
.post:target, .highlight, .popupPost, .managePost {
  background: #101010;
  border: 1px double #77cd3b;
}
.filetitle {
  color: #cc1105;
  font-size: 1.1em;
  font-weight: bold;
}
.unkfunc {
  color: #206620;
}
.spoiler .unkfunc, .spoiler a, .spoiler {
  background: #000000;
}
.spoiler:hover {
    color: white;
}
hr {
  border-top: 1px solid rgba(50,50,50,0.5);
  border-bottom: none;
}
#postform {
  border: 1px dotted #77cd3b;
}
input[value="Log In"], #submit, #postform #dragCell {
  background-color: #292929;
}
button, .hidePostform {
  color: #77cd3b;
}
#submit:hover {
  color: #dd0000;
}
input, textarea, .post-mark, .captchaWrapper {
  background-color: #101010;
  color: #77cd3b;
  font-family: "xos4 Terminus", Terminus, Consolas, "Liberation Mono", Courier, monospace;
  font-size: 10pt;
}
::-webkit-input-placeholder {
  font-family: "xos4 Terminus", Terminus, Consolas, "Liberation Mono", Courier, monospace;
  font-size: 10pt;
}
::-moz-placeholder {
  font-family: "xos4 Terminus", Terminus, Consolas, "Liberation Mono", Courier, monospace;
  font-size: 10pt;
}
::-ms-input-placeholder {
  font-family: "xos4 Terminus", Terminus, Consolas, "Liberation Mono", Courier, monospace;
  font-size: 10pt;
}
#postform p, .post-mark, .post-mark a, input[type="text"], input[type="file"], input[name="email"], input[name="subject"] {
  border-color: rgba(10, 10, 10, 0.9) !important;
}
.helpMark, .rules {
  background: #101010 !important;
  border: 1px double #77cd3b;
}
.helpMark {
  border-top: none !important;
}
.rules {
  border-bottom: none !important;
}
.pagenavigator, .pagenavigator button, .pagenavigator a:first-of-type {
  color: #68696b;
  background: transparent;
}
.pagenavigator a:hover, .pagenavigator .visited, .pagenavigator button:hover {
  background: transparent !important;
}
.pagenavigator .visited {
  color: #77cd3b !important;
  font-weight: bold;
}
#counter {
    border-color: #77cd3b;
    opacity: .9;
}
.popupPost {
  display: table !important;
}
code, kbd, pre, samp {
  font-family: "xos4 Terminus", "Terminus", Consolas, "Liberation Mono", Courier, monospace !important;
}
pre.code {
  border: 1px solid #393939;
  background: #101010 !important;
}
}
