css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tabbrowser-strip {  height: 24px !important; }

.tabs-container { padding-left: 1px !important; padding-right: 1px !important;  }

.tabbrowser-tab, .tabbrowser-tabs, .tab-image-left, .tab-image-middle, .tab-image-right {
  background-color: transparent !important;
  background-image: none !important;
}

tab:hover {
  background-color: #F3F3F0 !important;
}

.tabs-bottom {
  display: none !important; }

.tab-close-button:hover {
  -moz-image-region: rect(0px, 14px, 14px, 0px) !important;
}
  
tab {
  border: 1px solid ThreeDShadow !important;
  -moz-border-radius:  0 !important;
  margin: 0px !important;
  -moz-border-top-colors: ThreeDShadow !important;
  -moz-border-right-colors: ThreeDShadow !important;
  -moz-border-left-colors: ThreeDShadow !important;
  -moz-border-bottom-colors: ThreeDShadow !important;
  padding-left: 1px !important;
  padding-top: 1px !important;
}

.tabbrowser-tab[selected="true"] {
  background-color: #F3F3F0 !important;
}

tab:not(:first-child) { margin-left: -1px !important; }

.tabbrowser-arrowscrollbox > .scrollbutton-up, .tabbrowser-arrowscrollbox > .scrollbutton-down {
  background-image: none !important;
  border: 1px solid ThreeDShadow !important;
  -moz-border-radius:  0 !important;
  margin: 0px !important;
}

.tabbrowser-arrowscrollbox > .scrollbutton-up > .toolbarbutton-icon, .tabbrowser-arrowscrollbox > .scrollbutton-down > .toolbarbutton-icon {
margin-top: 5px !important;
}

.tabbrowser-arrowscrollbox > .scrollbutton-down:not([disabled="true"]):hover, .tabbrowser-arrowscrollbox > .scrollbutton-up:not([disabled="true"]):hover {
  background-image: none !important;
  background-color: #F3F3F0 !important;
  padding-top: 0px !important;
}


.tabbrowser-arrowscrollbox > .scrollbutton-down[disabled="true"], .tabbrowser-arrowscrollbox > .scrollbutton-up[disabled="true"] {
  opacity: 1 !important;
}

.tabbrowser-arrowscrollbox > .scrollbutton-down[disabled="true"] > .toolbarbutton-icon, .tabbrowser-arrowscrollbox > .scrollbutton-up[disabled="true"] > .toolbarbutton-icon {
  opacity: 0.4 !important;
}
