/* -=< Dark Subz TV by Emmanuel Sarl >=- */




@namespace url(http://www.w3.org/1999/xhtml);
    @-moz-document domain("subztv.club") {








/* Main Body */

body {
    text-align:left;
    font-family:Verdana, Arial, Helvetica, sans-serif;
    padding:10px;
    margin:0px;
    color:#999999 !important;
    background:#181818 !important;
    line-height:1.3em;
}

/* Logo */

#home .navbar {
    background: #212121;
}

/* Search Button */

.nav-header .search-bar {
    background-color: #212121;
}

.btn-primary {
    color: #ffffff;
    background-color: #333;
    border-color: #505050;
}

/* In Search */

#search-page .well {
    background: #212121;
}

.btn-primary {
    color: #ffffff;
    background-color: #333;
    border-color: #505050;
}

/* In Profile Page */

#users-show #filter-bar .date-picker {
    padding-left: 30px;
    background-color: #333;
}

#users-show #under-image-cont {
    background: #212121;
    border-radius: 1px;
    padding: 8px;
    margin-bottom: 35px;
    -webkit-box-shadow: 1px 2px 1px #d1d1d1;
    -moz-box-shadow: 1px 2px 1px #d1d1d1;
    box-shadow: 1px 2px 1px #333;
}


/* Login Page */
      
#login-page #login-box {
    margin: 8% 0 19%;
    box-shadow: 0 1px 3px rgba(0,0,0,.13);
    background: #212121;
    padding: 40px 15px 35px;
}
      
#login-page #login-box .form-control {
    background: #212121;
    height: 40px;
}
      
.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:focus, .navbar-default .navbar-nav>.open>a:hover {
    background-color: #212121;
    color: #555;
}
      
.navbar-default .navbar-nav .open .dropdown-menu>li>a {
    color: #959595;
}

.simple-dropdown .dropdown-menu {
    margin-top: 21px!important;
    border-right: 1px solid #999;
    border-left: 1px solid #999;
    border-radius: 0 0 8px 8px;
    border-bottom: 2px solid #999;
    box-shadow: none!important;
    background: #212121;
    font-size: 15px;
}
      
/* News Letters */

.heading {
    background: #212121;
    text-transform: uppercase;
    padding: 15px;
    font-family: Bitter,serif;
    font-size: 19px;
    margin: 0 15px 20px;
    color: #959595;
}

/* Movies-Series Titles */

.pretty-figure figcaption {
    padding: 8px 15px;
    text-align: center;
    background: #212121;
    color: #959595;
    font-weight: 600;
    max-height: 56px;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
}

.pretty-figure figcaption a {
    color: #959595;
}

/* -------------------- In Season Selection Pages -------------------- */


/* Logo */

.navbar-default {
    background-color: #212121;
    border-color: transparent;
}

/* Second Background */

#title-page>.container {
    margin-top: 80px;
    padding: 30px;
    background: #181818;
}

/* Third Background ( Season Selection ) */

#title-page>.container #details-container #details {
    background: #181818;
    padding: 20px;
}

/* Both Borders Around images */

.img-thumbnail {
    padding: 4px;
    line-height: 1.42857143;
    border: 1px solid #2d2d2d;
    border-radius: 0;
    -moz-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
    display: inline-block;
    max-width: 100%;
    height: auto;
}

.img-thumbnail, body {
    background-color: #212121;
}

/* Cast - Reviews - Comments */

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #eff0f0;
    background-color: #212121;
    border: 1px solid #333;
    border-bottom-color: transparent;
    cursor: default;
}

.nav-tabs {
    border-bottom: 1px solid #333;
}

/* Bottom Border After Actor's Name */

#title-page>.container #cast .char-name {
    border-top: 1px solid #333;
    padding-top: 1px;
    margin-top: 1px;
    font-weight: 400;
    font-size: 12.5px;
    overflow: hidden;
    text-overflow: ellipsis;
}

/* In Reviews Page */

#title-page #filter-bar {
    background: #212121;
    padding: 15px;
    margin-top: 20px;
}

.btn-primary {
    color: #959595;
    background-color: #333;
    border-color: #505050;
}

.form-control {
    background-color: #333;
}

/* Boxed Items at Reviews */

.boxed-items>li {
    background: #181818;
    margin-bottom: 20px;
    padding: 15px 25px;
}

/* Comments Disc */

placeholder {
    color: #7f919e;
    background-color: #212121;
    line-height: 30px;
    font-size: 15px;
    position: absolute;
    top: 7px;
    left: 10px;
    right: 9px;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    cursor: text;
}

/* ------------ Episode List ------------ */

#title-page #episode-list .media {
    margin-bottom: 30px;
    background: #212121;
}

/* Subtitles Selection */

.table>thead>tr>th {
    vertical-align: bottom;
    border-bottom: 2px solid #333;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 1px solid #333;
}

.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
    background-color: #212121;
}

/* Episode Names at The Bottom */

#title-page #episode-grid figure {
    margin-bottom: 20px;
    background-color: #212121;
    -webkit-box-shadow: 0 1px 2px rgba(0,0,0,.1);
    -moz-box-shadow: 0 1px 2px rgba(0,0,0,.1);
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
}

/* Nav "Ταινίες" */

#titles-index #content .form-control {
    height: 60px;
    border: 2px solid #212121;
    border-radius: 5px;
    box-shadow: none;
}

.pika-label {
    display: inline-block;
    position: relative;
    z-index: 9999;
    overflow: hidden;
    margin: 0;
    padding: 5px 3px;
    font-size: 14px;
    line-height: 20px;
    font-weight: bold;
    background-color: #212121;
}

.pika-single {
    z-index: 9999;
    display: block;
    position: relative;
    width: 240px;
    padding: 8px;
    color: #959595;
    background: #212121;
    border: 1px solid #333;
    border-bottom-color: #333;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
}

#titles-index #content .date-picker {
    padding-left: 30px;
    background-color: #333;
}

.index-pagination>ul>li span, .index-pagination>ul>li>a {
    position: relative;
    float: left;
    padding: 8px 14px;
    border-right: 1px solid #959595;
    background-color: #333;
    border-top: 2px solid #959595;
    border-bottom: 2px solid #959595;
    color: #999;
}

#main-loading-outter {
    display: none;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(66, 66, 66, 0.8);
    z-index: 9999;
}

pre code, table {
    background-color: transparent;
}

.pika-button {
    cursor: pointer;
    display: block;
    outline: none;
    border: 0;
    margin: 0;
    width: 100%;
    padding: 5px;
    color: #666;
    font-size: 12px;
    line-height: 15px;
    text-align: right;
    background: #212121;
}

/* In Download page */

.modal-content {
    position: relative;
    background-color: #212121;
    border: 1px solid #999;
    border: 1px solid rgba(0,0,0,.2);
    border-radius: 0;
    -webkit-box-shadow: 0 3px 9px rgba(0,0,0,.5);
    -moz-box-shadow: 0 3px 9px rgba(0,0,0,.5);
    box-shadow: 0 3px 9px rgba(0,0,0,.5);
    outline: 0;
}

.close {
    font-size: 21px;
    line-height: 1;
    color: #ffffff;
    text-shadow: 0 1px 0 #fff;
    opacity: .2;
    filter: alpha(opacity=20);
}

/* Nav "Νέα" */

#pagi-bar>.pagi-pages {
    text-align: center;
    text-transform: uppercase;
    font-weight: 700;
    letter-spacing: .4px;
    color: #959595;
    vertical-align: middle;
    padding-top: 16px;
    background-color: #212121;
}

#pagi-bar {
    -webkit-box-shadow: 0 1px 2px rgba(0,0,0,.1);
    -moz-box-shadow: 0 1px 2px rgba(0,0,0,.1);
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
    background: #212121;
    margin: 15px 0 30px;
    height: 50px;
    border-radius: 2px;
}

#news-index .media {
    background: #212121;
    margin-bottom: 20px;
}

/* Reading News Tab  */

#news-show article #news-body {
    padding: 25px;
    background-color: #181818;
}
    }
