/*****************************************************
START!
******************************************************/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/****************************************************
Firebug Menu Button
*****************************************************/

#fbFirebugMenu {
    list-style-image: url(data:image/png;base64,) !important;
}

#fbFirebugMenu[open="true"] {
    list-style-image: url(data:image/png;base64,) !important;
}

/****************************************************
Changes couple images from win's skin to mac's skin
*****************************************************/

#fbCloseButton {
    margin: 0 5px 0 1px !important;
    -moz-image-region: rect(auto, auto, auto, auto) !important;
    list-style-image: url("chrome://firebug/skin/mac/close.png") !important;
}

#fbCloseButton:hover {
    list-style-image: url("chrome://firebug/skin/mac/closeHover.png") !important;
}

#fbCloseButton:hover:active {
    list-style-image: url("chrome://firebug/skin/mac/closeActive.png") !important;
}

#fbDetachButton {
    margin: 2px 3px 0 3px !important;
    -moz-image-region: rect(auto, auto, auto, auto) !important;
    list-style-image: url(chrome://firebug/skin/mac/detach.png) !important;
}

#fbDetachButton:hover {
    list-style-image: url(chrome://firebug/skin/mac/detachHover.png) !important;
}

#fbDetachButton:hover:active {
    list-style-image: url(chrome://firebug/skin/mac/detachActive.png) !important;
}

#fbCommandToggleSmall {
    list-style-image: url(chrome://firebug/skin/mac/detach.png) !important;
}

#fbCommandToggleSmall:hover {
    list-style-image: url(chrome://firebug/skin/mac/detachHover.png) !important;
}

#fbCommandToggleSmall:hover:active {
    list-style-image: url(chrome://firebug/skin/mac/detachActive.png) !important;
}

#fbCommandToggleLarge {
    list-style-image: url(chrome://firebug/skin/mac/min.png) !important;
}

#fbCommandToggleLarge:hover {
    list-style-image: url(chrome://firebug/skin/mac/minHover.png) !important;
}

#fbCommandToggleLarge:hover:active {
    list-style-image: url(chrome://firebug/skin/mac/minActive.png) !important;
}

/****************************************************
Changes searchbar images from win's skin to mac's skin
*****************************************************/

#fbSearchBox {
    -moz-appearance: none !important;
    margin: 4px 6px 1px 6px !important;
    border: none !important;
    padding: 2px 8px 4px 20px !important;
    background: url(chrome://firebug/skin/mac/searchBox.png) no-repeat !important;
    font: icon !important;
    font-size: 11px !important;
}

#fbSearchBox[disabled="true"] {
    opacity: 0.5 !important;
}

#fbSearchBox > .autocomplete-textbox-container > .autocomplete-icon {
    list-style-image: url(chrome://firebug/skin/mac/search.png) !important;
}


/****************************************************
Changes TabBox images from win's skin to mac's skin
*****************************************************/

.panelTabBox {
    background: #e0e0e0 url(chrome://firebug/skin/mac/tabBar.gif) repeat-x !important;
    border-bottom: 1px solid #BBB9BA !important;
    padding: 0 2px 0 2px !important;
    overflow: hidden !important;
}

panelTab {
    margin: 0 1px 0 3px!important;
    font-size: 11px!important;
    font-weight: bold!important;
    color: #565656!important;
}
panelTab:hover,
panelTab[selected="true"] {
    margin-bottom: 1px!important;
}

panelTab:hover > .panelTab-text,
panelTab[selected="true"] > .panelTab-text {
    padding-bottom: 3px!important;
}

.panelTab-left {
    width: 6px!important;
}

.panelTab-text {
    margin: 0!important;
    padding: 3px 1px 4px 1px!important;
}

.panelTab-right {
    width: 6px!important;
}

panelTab:hover > .panelTab-left {
    background: url(chrome://firebug/skin/mac/tabLeftHover.png) no-repeat bottom!important;
}

panelTab:hover > .panelTab-text {
    background: url(chrome://firebug/skin/mac/tabMidHover.png) repeat-x bottom!important;
}

panelTab:hover > panelTabMenu {
    background: url(chrome://firebug/skin/mac/tabMidHover.png) repeat-x bottom!important;
}

panelTab:hover > .panelTab-right {
    background: url(chrome://firebug/skin/mac/tabRightHover.png) no-repeat bottom!important;
}

panelTab[selected="true"] > .panelTab-left {
    background: url(chrome://firebug/skin/mac/tabLeft.png) no-repeat bottom!important;
}

panelTab[selected="true"] > .panelTab-text {
    background: url(chrome://firebug/skin/mac/tabMid.png) repeat-x bottom!important;
}

panelTab[selected="true"] > panelTabMenu {
    background: url(chrome://firebug/skin/mac/tabMid.png) repeat-x bottom!important;
}

panelTab[selected="true"] > .panelTab-right {
    background: url(chrome://firebug/skin/mac/tabRight.png) no-repeat bottom!important;
}

#fbToolbar {
    padding-top: 3px!important;
    padding-bottom: 2px!important;
    border-top: 1px solid #BBB9BA !important;
    background: #F6F6F6 url(chrome://firebug/skin/mac/toolbar.gif) repeat-x !important;
}

#fbCommandToggleSmall,
#fbCommandToggleLarge {
    width: 48px!important;
}
/****************************************************
Supports the firePHP extension
*****************************************************/

#firephpMenu {
    list-style-image: url(data:image/png;base64,) !important;
    -moz-opacity: 0.8!important;
}

#firephpMenu:hover {-moz-opacity: 1.0!important;}

#firephpMenu:hover:active,
#firephpMenu[open="true"] {
    list-style-image: url(data:image/png;base64,)!important;
}

/****************************************************
Toolbar buttons
*****************************************************/

.toolbar-text-button,
.toolbar-text-menubutton {
    -moz-appearance: none!important;    
    margin: 1px 1px!important;
    padding: 1px 0px 1px 7px!important;
    min-width: 0!important;
    max-width: 13em!important;
    font-weight: bold!important;
    font-size: 11px!important;
    color: #565656!important;
    border: none !important;
}

.toolbar-text-button > .toolbarbutton-text {
    margin-left: 7px!important;
    padding-right: 7px!important;
}

.toolbar-text-button > .toolbarbutton-icon {
  display: none !important;
}

.toolbar-text-button:hover:not([disabled="true"]),
.toolbar-text-menubutton:hover:not([disabled="true"]) {
  background: url("chrome://browser/skin/bookmark-hover-left.png") no-repeat left center!important;
}

.toolbar-text-button:hover:not([disabled="true"]) > .toolbarbutton-text,
.toolbar-text-menubutton:hover:not([disabled="true"]) > .toolbarbutton-menu-dropmarker {
  background: url("chrome://browser/skin/bookmark-hover-right.png") no-repeat right center;
  color: #fff!important;
}

.toolbar-text-menubutton:hover:not([disabled="true"]) > .toolbarbutton-text {
  background: url("chrome://browser/skin/bookmark-hover-mid.png") repeat-x!important;
  color: #fff!important;
}

.toolbar-text-button:hover:active:not([disabled="true"]),
.toolbar-text-button[checked="true"]:not([disabled="true"]),
.toolbar-text-menubutton:hover:active:not([disabled="true"]),
.toolbar-text-menubutton[open="true"]:not([disabled="true"]) {
  background: url("chrome://browser/skin/bookmark-open-left.png") no-repeat left center!important;
}

.toolbar-text-menubutton:hover:active:not([disabled="true"]) > .toolbarbutton-text,
.toolbar-text-menubutton[open="true"]:not([disabled="true"]) > .toolbarbutton-text {
  background: url("chrome://browser/skin/bookmark-open-mid.png") repeat-x !important;
  color: #fff!important;
}

.toolbar-text-button:hover:active:not([disabled="true"]) > .toolbarbutton-text,
.toolbar-text-button[checked="true"]:not([disabled="true"]) > .toolbarbutton-text,
.toolbar-text-menubutton:hover:active:not([disabled="true"]) > .toolbarbutton-menu-dropmarker,
.toolbar-text-menubutton[open="true"]:not([disabled="true"]) > .toolbarbutton-menu-dropmarker {
  background: url("chrome://browser/skin/bookmark-open-right.png") no-repeat right center!important;
}

.toolbar-text-button[checked="true"] {
    background-color: transparent!important;
}

.toolbar-text-button[checked="true"] > .toolbarbutton-text {
    color: #FFFFFF!important;
}

.toolbar-text-button .toolbarbutton-text,
.toolbar-text-menubutton .toolbarbutton-text {
  font-size: 100% !important; 
  margin: 0px !important;
}

.toolbar-text-button > .toolbarbutton-text,
.toolbar-text-menubutton > .toolbarbutton-text {
  display: -moz-box !important;
  height: 16px !important;
  padding-top: 1px!important;
}

.toolbar-text-button > .toolbarbutton-menu-dropmarker,
.toolbar-text-menubutton > .toolbarbutton-menu-dropmarker {
    list-style-image: url("chrome://browser/skin/bookmarks/folderarrow.png");
    width:  16px !important;
    height: 16px !important;
    padding: 5px 5px 4px 3px!important;
    margin-left:0px!important;
}

.toolbar-text-menubutton:hover:not([disabled="true"]) > .toolbarbutton-menu-dropmarker,
.toolbar-text-menubutton[open="true"]:not([disabled="true"]) > .toolbarbutton-menu-dropmarker  {
    list-style-image: url("chrome://browser/skin/bookmarks/folderarrow-hover.png")!important;
}

.toolbar-text-button[disabled="true"] > .toolbarbutton-menu-dropmarker,
.toolbar-text-menubutton[disabled="true"] > .toolbarbutton-menu-dropmarker {
    list-style-image: none!important;
}

.toolbar-text-menulist {
    margin-top: 0!important;
    margin-bottom: 0!important;
    -moz-appearance: none!important;
    color: #565656!important;
}

.toolbar-text-menulist > .menulist-label-box {
    -moz-appearance: none!important;
    border: none!important;
    padding-left: 8px!important;
    background: url(chrome://firebug/skin/menulistLeft.png) no-repeat left center!important;
}

.toolbar-text-menulist > .menulist-dropmarker {
    -moz-appearance: none!important;
    width: 20px!important;
    background: url(chrome://firebug/skin/menulistRight.png) no-repeat left center!important;
}

.toolbar-text-menulist > .menulist-dropmarker > .dropmarker-icon {
}

.toolbar-label {
    color: #565656!important;
}
