css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nanowrimo.org") {
  
  body {
    background: #222222 !important;
    color: #CCCCCC !important;
  }
  a:link {
    color: #FF7733 !important;
  }
  a:visited {
    color: #FF7733 !important;
  }
  a:hover {
    color: #FFFFFF !important;
  }
  a.btn, input.btn, li.active>a {
    background: #442200 !important;
    color: #DDBB88 !important;
    border: 1px solid #AA9966 !important;
  }
  h1, h2 {
    color: #FFDDAA !important;
  }
  #content {
    background: #111111 !important;
    color: #FFDDAA !important;
  }
  span.subtitle-hidden {
    color: #335555 !important;
  }
  nav.navbar-main {
    background: #332211 !important;
  }
  ul.dropdown-menu {
    background: #332211 !important;
  }
  div.container {
    background: #222222 !important;
  }
  div.paginated_resources, div.pagination_with_selector {
    background: #111111 !important;
  }
  div.forum {
    background: #111111 !important;
    background: linear-gradient(#111111, #555555) !important;
    color: #BBAA99 !important;
    border-bottom: 1px solid #000000 !important;
    border-bottom-right-radius: 75px;
    border-top: 5px solid #111111 !important;
  }
  div.forum_meta {
    background: #554433 !important;
    color: #DDAA88 !important;
  }
  div.col-md-pull-9 {
    background: #222222 !important;
  }
  div.well {
    background: #222222 !important;
  }
  table.forum_threads {
    background: #555555 !important;
  }
  th {
    background: #332211 !important;
  }
  td.forum_thread_row_icons {
    background: #222222 !important;
  }
  td.forum_thread_row_content {
    background: #222222 !important;
    color: #AAAAAA !important;
  }
  tr.odd {
    background: #333333 !important;
  }
  tr.even {
    background: #222222 !important;
  }
  div.subject, div.forum_thread_created, div.forum_thread_comment_counts, div.forum_thread_row_last_comment {
    background: #222222 !important;
    background: linear-gradient(#222222, #555555) !important;
    color: #AAAAAA !important;
    border-bottom: 1px solid #000000 !important;
    height: 50px;
  }
  div.forum_thread_row_last_comment {
    border-bottom-right-radius: 100px;
  }
  span.created_time_ago {
    color: #AADDDD !important;
  }
  span.unread {
    color: #FFCC99 !important;
  }
  span.new {
    background: #663300 !important;
  }
  span.disabled {
    color: #993300 !important;
  }
  div.forum_thread_comment, div.forum_thread_comment_authorbar {
    background: #222222 !important;
    border: 0px;
  }
  div.forum_comment_content {
    color: #BBAA99 !important;
  }
  div.signature {
    color: #AA9988 !important;
    font-size: smaller !important;
  }
  div.forum_thread_comment_level {
    background: #111111 !important;
  }
  div.forum_thread_comment_header, div.forum_thread_comment_footer {
    background: #332211 !important;
    border: 1px solid #000000 !important;
  }
  div.forum_thread_comment_body {
    background: #222222 !important;
    background: linear-gradient(#222222, #555555) !important;
    border-bottom: 1px solid #000000 !important;
    border-bottom-right-radius: 100px;
  }
  div.deleted_forum_thread_comment {
    background: #332211 !important;
    border: 1px solid #000000 !important;
    
  }
  div.forum_thread_comment_user_info {
    background: #111111 !important;
    color: #EEEEEE !important;
  }
  div.field {
    color: #FFDDAA !important;
  }
  div.forum_customization {
    background: #554433 !important;
  }
  blockquote {
    background: #333333 !important;
    background: linear-gradient(#222222, #555555) !important;
    color: #CCBBAA !important;
    border-bottom: 1px solid #000000 !important;
    border-bottom-right-radius: 100px;
    border-left: 1px solid #000000 !important;
  }
  em.current {
    background: #663333 !important;
    color: #FF7733 !important;
  }
  div.stats {
    background: #333333 !important;
  }
  .panel-heading {
    background: #332211 !important;
  }
  .panel-sub {
    background: #333333 !important;
  }
  .panel-body {
    background: #222222 !important;
  }
  .panel-info {
    background: #222222 !important;
  }
  #region_dashboard {
    background: #333333 !important;
  }
  h3.toggle_region_box {
    background: #222222 !important;
    color: #FFDDAA !important;
  }
  div.content {
    background: #222222 !important;
    background: linear-gradient(#222222, #555555) !important;
    border-bottom: 1px solid #000000 !important;
  }
  div.region_note {
    background: #222222 !important;
    background: linear-gradient(#111111, #555555) !important;
    color: #FFDDAA !important;
  }
  #region_words_and_donations {
    background: #442200 !important;
    color: #DDBB88 !important;
    border: 1px solid #AA9966 !important;
  }
  #wordcount_and_badge {
    color: #FFDDAA !important;
  }
  div.synopsis {
    background: #222222 !important;
  }
  div.instructions {
    background: #333333 !important;
  }
  div.user_badge {
    background: #111111 !important;
  }
  div.form-control {
    background: #333333 !important;
    color: #DDBB88 !important;
  }
  div.alert {
    background: #442200 !important;
    color: #DDBB88 !important;
    border: 1px solid #AA9966 !important;
  }
  li.mynano_tab {
    background: #442200 !important;
    color: #DDBB88 !important;
    border: 1px solid #AA9966 !important;
  }
  li.selected {
    background: #553300 !important;
    color: #EECC99 !important;
    border: 1px solid #AA9966 !important;
  }
  tr.personal_achievement_badge>td {
    background: #111111 !important;
  }
  tr.personal_achievement_badge>td:not(:last-of-type):not(:first-of-type) {
    background: #222222 !important;
    background: linear-gradient(#222222, #555555) !important;
    border-bottom: 1px solid #000000 !important;
    border-bottom-right-radius: 100px;
  }
  #personal_achievement_badge_table>tbody>tr:last-of-type>td {
    background: #332211 !important;
    border: 1px solid #000000 !important;
  }
  div.col-sm-12>span {
    color: #FFDDAA !important;
  }
  #messages_inbox>tbody>tr>td {
    background: #222222 !important;
    border-bottom: 1px solid #BBBBBB !important;
  }
  #message_metadata>tbody>tr>td {
    background: #333333 !important;
    border-bottom: 1px solid #BBBBBB !important;
  }
  .greeting>a, ul.navbar-nav>li>a {
    background: #332211 !important;
    color: #DDBB88 !important;
  }

}
