css:
@-moz-document domain("heise.de")

{
  
  body
  {
    background: #FFF !important;
  }  
   
  div#container
  {
    position: relative !important;
    top: -90px !important;
    width: auto !important;
   }
  
  div#mitte_links
  {
    position: relative !important;
    width: auto !important;
   }
  
  
    div#bannerzone, div#bannerzone_top, div#mitte_rechts, div.cadv, div.cadv_start, div.bcadv, div.bcadv_start, div.adbottom, .banner, .sky, .skyscraper, .heise_advert, table.druck, .leaderboard, .ISI_IGNORE table, div.teaser_liste_foren, div.teaser_liste_software, div.naviad, div.teaser_adliste, div#mitte_rechts, div.clear, div.online-markt, div.col100preisvergleich, div.col100weiss, div.col50anschnitt, div.teaser_abdeckung, div#sitemap
  {
    display: none !important;
  }
  
}


@-moz-document url-prefix(https://www.heise.de/tp/), url-prefix(http://www.heise.de/tp/) 
{
   html, body.tp, div.container_all, div.row, div.col-md-10, div.container, div.row.tp_content, main.col-md-8, article.tp_topteaser, div#zeile_b.topteaser_master.layout_6.print-avoid-pagebreak-inside, div.multiple
  { 
    background-color: #FFFFFF !important;
    width: auto !important;
    font-size: 1em;
  }
    
  aside, b.dachzeile
  {
    display: none !important;
  } 
  
  /*teaser*/ 
  img.img-responsive, html body.tp div.container_all div.row div.col-md-10 div.container div.row.tp_content main.col-md-8 ol.list-unstyled.tp_beitragsliste li.news a article.row figure.tpteaser_figure.col-sm-4.col-md-4.hidden-xs
  {
    width:150px; /* you can use % */
    height: auto;
  }

   html body.tp div.container_all div.row div.col-md-10 div.container div.row.tp_content main.col-md-8 article.tp_topteaser div#zeile_a.topteaser_master.layout_5.print-avoid-pagebreak-inside a.the_content_url h2
  {
    position: relative !important;
    top: 40px !important;
    margin-left:200px !important
  }
  
   html body.tp div.container_all div.row div.col-md-10 div.container div.row.tp_content main.col-md-8 article.tp_topteaser div#zeile_a.topteaser_master.layout_5.print-avoid-pagebreak-inside a.the_content_url p, div.kommentare
  {
    position: relative !important;
    top: -50px !important;
    margin-left:200px !important
  }
  
  /*nachrichten*/
  
  
    html body.tp div.container_all div.row div.col-md-10 div.container div.row.tp_content main.col-md-8 ol.list-unstyled.tp_beitragsliste li.news a article.row section.tpteaser_figcaption.col-sm-8.col-md-8
   {
    position: relative !important;
    top: -100px !important;
    margin-left:200px !important
  }
    
}
