css:
@-moz-document domain("simplenote.com") {
.note #txtarea {
    font-size: 15px;
    color: #DCDCDC!important;
}
.app {
    background: #131313!important;
}
@media screen and (max-width:1279px) {
    .sidebar {
        width: 20px
    }
    .sidebar:hover {
        width: 360px;
    }
    .sidebar:hover ~ .note {
        left: 361px
    }
    .note {
        left: 0px
    }
    ::-webkit-scrollbar {
        display: none;
    }
}
.wrapper {
    bottom: 0px;
}
.footer {
    display: none;
}
.searchfield {
    color: #ddd;
}
.notes li {
    padding: 5px;
    text-shadow: none;
    opacity: 1;
}
.notes li:hover {
    opacity: 1!important;
}
.notes li .note-preview-title {
    color: #ddd;
    font-size: 13px;
}
.note #txtarea {
    padding: 20px;
}
.note #static_content {
    color: #ddd;
}
.notes li:hover {
    background-color: #555;
}
.notes li.selected {
    background: #444;
    border-right: 3px solid #448ac9;
}
.toolbar {
    border: none!important;
    opacity: 1;
}
.toolbar:hover {
    opacity: 1;
}
.sidebar {
    border-right: 1px solid #444!important;
}
.tagbar {
    border-bottom: 1px solid #444!important;
    opacity: 1;
}
.tagbar:hover {
    opacity: 1;
}
.sideview {
    border-bottom: 1px solid #444!important;
    opacity: 1;
}
.sideview:hover {
    opacity: 1;
}
.searchfield {
    border: 1px solid #444!important;
    -webkit-box-shadow: none!important;
}
.notes {
    overflow: hidden;
}
.app {
    border-bottom: none;
}
.notes:hover {
    overflow: auto;
}
.as-input.as-prompt {
    color: #ddd!important;
}
.note #txtarea {
    overflow: hidden;
    color: #a8a8a8!important;
    opacity: 1;
}
.note #txtarea:hover {
    overflow: auto;
    opacity: 1;
}
input.searchfield {
    border-radius: 0px;
}
.notes li.selected {
    opacity: 1!important;
}
.tagbar li {
    color: #448ac9
}
* {color: #555555}
}
