css:
/* i really want this to be global */
@-moz-document domain("tinhte.vn") {

body{
  	background: #f1f1f1!important
  }
a{ text-decoration:none!important}
.TT_UTP_layoutNewsFull{
  width: 720px;
	max-width: 100%;
  margin: 0 auto 16px auto!important;
  
}

.TT_UTP_layoutNewsFull>.threads>div {
    margin: 0 0 15px 0 !important;
    padding: 0 15px 0 15px !important;	
     	background: #fff;
       	box-shadow: 0 1px 4px rgba(0,0,0,.04);
       	border: 1px solid rgba(0,0,0,.09);
       	border-radius: 3px;
}

.TT_UTP_layoutNewsFull>.threads>div.adWidget-gpt {
    overflow: hidden;
    width: auto;
    padding: 10px!important;
}

.TT_UTP_layoutNewsFull .promote .poster-avatar img {
    -webkit-border-radius: 36px!important;
    -moz-border-radius: 36px!important;
    -khtml-border-radius: 36px!important;
    border-radius: 36px!important;
    height: 36px!important;
    width: 36px!important;
  	-webkit-box-shadow: 0 0 0;
    -moz-box-shadow: 0 0 0;
    -khtml-box-shadow: 0 0 0;
   	box-shadow: 0 0 0;
}
.TT_UTP_layoutNewsFull .promote .thread-info {
    margin-top: 15px;
}
@media screen and (min-width: 970px){
.TT_UTP_layoutNewsFull .promote .thread-info {
    line-height: 36px;
}
}

.TT_UTP_layoutNewsFull .promote .thread-info {
    height: 36px;
}

.TT_UTP_layoutNewsFull .separator {
    border-bottom: none;
    height: 0;
    margin-top: 15px !important;
}

.TT_UTP_layoutNewsFull .next-link a {
    background: #5e98c2;
}

.TT_UTP_layoutNewsFull .promote .poster {
    color:#5e98c2;
    font-size: 10pt;
    font-weight: normal;
}
.TT_UTP_layoutNewsFull .promote .post-body {
    margin-top: 15px;
}

@media screen and (min-width: 970px){
.TT_UTP_layoutNewsFull .promote .process-time, .TT_UTP_layoutNewsFull .promote .forum {
    margin-top: 0;
}
}
.TT_UTP_layoutNewsFull .promote .thread-title {
    color: #3a3d40;
}
.TT_UTP_layoutNewsFull .promote .forum a {
    color: #5e98c2;
}

/* scroll */
@media screen and (min-width: 970px){
  .TT_UTP_layout1l_2s_list .list .thread-title {
      color: #475b6f;
      font-size: 13pt;
  }
  
  .TT_UTP_layout1l_2s_list .main {
    width: 60%;
	}
	.TT_UTP_layout1l_2s_list .list {
    right: 0px;
    top: 0px;
       width: 40%;
}
  .TT_UTP_layout1l_2s_list .list .thread {
    margin-bottom: 15px;
    margin-left: 15px;
    margin-right: 15px;
    border-bottom: 1px solid #ddd;
    padding-bottom: 15px;
}
}
}
