css:
@-moz-document domain("geizhals.at"), domain("geizhals.de"), domain("geizhals.eu"), domain("skinflint.co.uk"), domain("cenowarka.pl") {
.logotop {
    -webkit-filter: grayscale(100%) invert(82%);
    filter: grayscale(100%) invert(82%);
}

.modal__container {
    -webkit-filter: invert(85%);
    filter: invert(85%);
    color: #222222;
}

div#gh_content_wrapper,
body#ghbody,
div.nl_box,
ul.ghnavsub,
.ghloginbox,
.productlist__name,
.productlist__image,
#acbox,
table.gh_fstbl > tbody,
th.xf_th,
#gh_filterbox {
    background-color: #2a2a2a;
}

.boxfm,
.boxfmh,
a.gh_pag_i {
    background: #212121 !important;
}
.gh_pag_act,
a.gh_pag_i:hover {
    color: #dfdfdf;
    background: #0c355c;
    border-color: #dfdfdf;
    text-decoration: none;
}

a,
a:visited,
div#gh_leftnav,
body#ghbody,
div.nl_box,
ul.ghnavsub,
a.m,
a.n,
a.gh_tabs,
.ghloginbox,
.xf_hidden_button,
.xf_hidden_button:visited,
.xf_i,
a.xf_i,
.productlist__description,
.gh_afilterbox_h,
.table-of-content__item,
.table-of-content__item:visited,
.productlist__header,
.btn3,
a.btn3,
.productlist__header .productlist__item,
.acbox_ntry > a.acs,
#xf_table.xf_this_cat_open .xf_tr:nth-child(2n+1),
.productlist__header,
.productlist__header .productlist__compare,
.btn3,
a.btn3,
.productlist__breadcrumb,
.productlist__breadcrumb-dummy,
.productlist__footer-cell,
.descr,
#act1, #act2, #act3,
.categorylist_headline, .gh_listtitle, div.arthdr {
    color: #dfdfdf;
}



#xf_table.xf_this_cat_open .xf_tr:nth-child(2n+1),
.productlist__header,
.productlist__header .productlist__compare,
.btn3,
a.btn3,
.productlist__breadcrumb,
.productlist__breadcrumb-dummy,
.productlist__footer-cell,
.descr,
#act1, #act2, #act3,
table.gh_fstbl > tbody > tr:nth-child(2n) {
    background-color: #3B3B3B;
}




div#gh_hdr {
    background: #2a2a2a;
    color: #dfdfdf;
}

li.gh_tabs {
    background: #2a2a2a;
    border-right: 2px solid #151515;
    *float: left;
}

div#gh_statline {
    background: #151515;
    color: #dfdfdf;
}

#gh_main {
    background-color: #212121;
    background: linear-gradient(90deg, #212121 17.8em, #dfdfdf 0);
}






#ghbody div.ghinfoboxh,
.box10h,
#ghfooter,
#gh_content_wrapper.gh_content_wrapper--new, #gh_content_wrapper_nonav.gh_content_wrapper--new {
    background: #212121;
}

#ghbody div.ghinfobox {
    border: 1px solid #151515;
}


div.gh_srch_wrp {
    background: #212121;
    box-shadow: inset 1px 1px 4px #191919, 0 0 3px rgba(0, 0, 0, .2);
}



div#gh_hdr_toplinks {
    text-shadow: none;
    color: #dfdfdf;
}


.gh_listtitle,
div.arthdr {
    color: #dfdfdf;
    background: #212121;
    background: linear-gradient(180deg, #212121, #212121);
    border-bottom: none;
}


.gh_cat {
    border: 1px solid #3b3b3b;
}
.t10i {
    border-bottom: 1px solid #3b3b3b;
}


.gh_gradient0 {
    background: linear-gradient(#3B3B3B, #2A2A2A);
    color: rgb(213, 213, 213);
}


#xf_table .xf_tr {
    background-color: #2a2a2a;
    border-bottom: .1em solid #d4d4d4;
}
.xf_tr.xf_tr_visible .xf_th {
    border-color: #d4d4d4;
}
.productlist__product:nth-child(2n) {
    background-color: #3B3B3B;
}



#ghbody .av_e,
.offer__delivery-time {
    background: #212121;
}
.av_sel.av_e,
.av_sel.av_l,
.av_sel.av_k {
    border-bottom: 1px solid #212121;
}
#ghbody .av_l,
.offer--available .offer__delivery-time {
    background: #003806;
}
#ghbody .av_k,
.offer--shortly .offer__delivery-time {
    background: #3c3c0e;
}


.lh1,
a.menulink0,
.offer__delivery-header {
    color: #dfdfdf;
}
.lh1 {
    background: #3B3B3B;
    font-weight: 800;
    border-top: 1px solid #777;
}
.gh-card,
.compare__row:nth-child(2n),
.compare__table tr:nth-child(2n) td.compare__data.no-highlight {
    background-color: #3B3B3B;
}
.table-of-content__item:hover,
tr.compare__row:nth-child(1).highligh,
.gh-data-table__row:nth-child(2n+1) {
    background-color: #2a2a2a;
}

.offer:nth-child(2n) {
    background-color: #2a2a2a;
}

.offer_bt,
.offer_bt:visited {
    background: #3B3B3B;
    background: linear-gradient(#3B3B3B, #2A2A2A);
    border: 1px solid #3B3B3B;
    color: #dfdfdf;
}

.ct-label {
    fill: #dfdfdf;
    color: #dfdfdf;
    font-size: .75rem;
    line-height: 1;
}
}
