css:
@-moz-document url-prefix("https://play.google.com/music/") {
body {
    background-color: #000;
    color: #ccc;
}
.material-card {
    background-color: #111;
    color: #fff;
}
.material-card .title {
    color: #ccc;
}
.fade-out:after {
    background: linear-gradient(to right, rgba(17, 17, 17, 0), rgba(17, 17, 17, 1));
}
#player {
    background-color: #111;
    color: #ff5722;
}
#player .now-playing-actions paper-icon-button {
    color: #ff5722;
}
#player-artist,
.player-album,
.player-dash {
    color: white;
}
#playerSongInfo .now-playing-info-wrapper #currently-playing-title {
    color: white;
}
.material .cluster-text-protection::before {
    background: black;
}
.material .cluster-text-protection {
    background: black;
}
.material .recommended-header {
    color: #ccc;
}
.cluster .header .cluster-title {
    color: #ccc;
}
.column .material-card[data-size="small"][data-type="imfl"] .title {
    color: #ccc;
}
}

@-moz-document url-prefix("https://play.google.com/music/") {
\\.paper-toolbar-0 {
    background: #009b4b;
}
}
