css:
@-moz-document url-prefix(https://quitter.is) {

.topbar .global-nav:before {
    height: 0;
}

#site-notice {
    display:none;
}
#logo, .topbar {
 	background-image: url('http://i.imgur.com/4xYw7Aq.png') !important;
}
#logo {
 background-position: 0 0;
	width: 70px;
    height: 32px;
    background-size: cover;
    transition: background-color 0.2s;
} 
#logo:hover{
 background-color: white;
top: 8px;
}
.topbar .global-nav {
 	background-color:rgba(37,37,37, 1);
}
div.global-nav.show-logo::before {
 display: none;   
}
#search-query {
 background-color: #eee;   
}
.loader {
display:none;
}

#user-container {position:fixed !important;}

}
