css:
@-moz-document domain("themis.lo14.wroc.pl") {
body {
    background: #0f0914;
    color: #ED8600;
    text-shadow: 1px 1px #0f0f0f;
    font-family: verdana;
    font-size: 12px;
    overflow: auto;
    min-width: 600px;
}
em {
    font-style: normal;
    color: #7AB117;
}
b {
    color: #D61043;
}
pre {
    overflow: auto;
    color: #f0f0f0;
}
a {
    color: #ffa500;
    text-decoration: none;
    text-shadow: 1px 1px #0f0f0f;
}
a:hover {
    text-decoration: underline;
}
small {
    font-size: 10px;
    font-weight: normal;
}
form {
    display: inline;
}
input,
select,
textarea {
    color: #8b0000;
    font-family: verdana;
    font-size: 12px;
    font-weight: normal;
    border: 1px solid #303030;
    margin: 1px 3px 1px 0;
    padding: 1px;
    background: inherit;
}
input:hover,
textarea:hover,
select:hover {
    background: inherit;
    border: 1px dashed #606060;
    color: #cb0000;
}
input.submit {
    border: 0;
    color: #8b0000;
    text-decoration: bold;
    padding: 0;
    margin: 0 0px;
    background: none;
}
input.submit:hover {
    cursor: pointer;
    color: #cb0000;
    background: none;
}
td {
    margin: 0;
    padding: 5px 0;
    border-top: 1px dashed #ed8600;
}
table.problems {
    table-layout: fixed;
    width: 100%;
}
td.problem-icon {
    width: 10%;
}
td.problem-code {
    width: 20%;
}
td.problem-name {
    width: 60%;
}
td.problem-term {
    width: 20%;
}
th {
    margin: 0;
    padding: 0 0 5px;
}
table {
    margin: 0;
    padding: 0;
}
table.noborder td {
    border: 0;
}
iframe {
    width: calc(100% - 345px);
    min-height: 800px;
}
.right {
    float: right;
    font-size: 12px;
    margin-left: 0;
    margin-right: 5px;
    font-weight: normal;
}
.ajax-iframe {
    display: none;
}
.ajax-clickable {
    color: inherit;
    cursor: pointer;
}
.ajax-clickable:hover {
    text-decoration: underline;
}
.textbox-idle {
    color: #808080;
}
/* ************************************************************************** */

#top {
    min-height: 140px;
    min-width: 610px;
    width: 85%;
    padding: 15px;
    margin: 20px auto 20px;
    background: none;
}
#title {
    display: block;
    width: 500px;
    height: 64px;
    background: url("http://s16.postimg.org/okrx0r9t1/themis.png") no-repeat;
    background-size: 100%;
    color: #f0af0a;
    margin: auto;
    padding: 50px;
}
#login-no,
#login-fail,
#login-ok {
    height: 25px;
    width: calc(100% - 5px);
    line-height: 25px;
    margin: 0;
    padding-left: 5px;
    min-width: 600px;
}
#login-no {
    background: #b31010;
}
#login-fail {
    background: #ed8600;
    color: #e21c21;
}
#login-ok {
    background: none;
    border-color: #ed8600;
    border-bottom: 1px;
    border-bottom-style: solid;
    border-top: 1px;
    border-top-style: solid;
    color: #c95100;
}
input.login-text {
    width: 165px;
    background: none;
    margin-right: 50px;
    border: none;
    border-bottom: 1px solid #303030;
}
.admin {
    color: #f0d040;
}
div.section-info {
    width: calc(100% - 10px);
    height: 32px;
    padding: 5px 5px;
    margin-top: 5px;
    background: #b7121b;
    font-size: 10px;
    line-height: 16px;
}
a.section-info {
    font-size: 12px;
    font-weight: bold;
}
div.admin-info {
    width: 440px;
    height: 32px;
    padding: 4px 10px 4px 50px;
    background: "https://themis.lo14.wroc.pl/gfx/admin-info.png";
    font-size: 10px;
    line-height: 16px;
}
a.admin-info {
    font-size: 12px;
    font-weight: bold;
}
.server-up {
    color: #20C020;
}
.server-down {
    color: #C02020;
}
/* ************************************************************************** */

#content {
    width: 85%;
    margin: auto;
    padding: 15px;
    border: none;
    background: none;
}
h1 {
    font-size: 16px;
    font-family: verdana;
    font-weight: bold;
    color: #e0be00;
    padding: 0 0 10px 0;
    margin: 0;
}
h2 {
    color: #93D9AD;
    font-family: verdana;
    font-weight: normal;
    text-align: left;
    margin: 0;
    padding: 0 10px 0 10px;
    line-height: 25px;
    height: 30px;
}
#generic {
    background: #170224;
    margin-bottom: 10px;
}
#problem {
    background: #170224;
    margin-bottom: 10px;
}
#admin {
    background: "https://themis.lo14.wroc.pl/gfx/hdr-yellow.png" no-repeat;
}
#stats {
    background: #170224;
    margin-bottom: 10px;
}
h3 {
    color: #F0AF0A;
}
h3.admin {
    color: #f0f0f0;
    font-weight: bold;
    font-size: 12px;
    border-bottom: 1px solid #D0D0D0;
    padding: 0 15px 3px;
    margin: 15px 0 10px;
}
p {
    text-indent: 20px;
    text-align: justify;
    line-height: 17px;
    margin: 5px 0 0;
    padding: 0;
}
#subtitle {
    text-indent: 0;
    text-align: left;
    margin: 0 0 20px 35px;
    padding: 0;
}
/* ************************************************************************** */

#sidepanel {
    max-width: 315px;
    border: 3px dashed #7ab117;
    border-radius: 15px;
    background: #170224;
    float: right;
    margin: 0 0 0 20px;
}
div.box {
    width: 295px;
    border: none;
    padding: 10px;
    margin-bottom: 10px;
}
div.boxhdr {
    margin: -11px -11px 0 -11px;
    padding: 5px 15px;
    font-weight: bold;
    color: inherit;
}
div.boxscroll {
    float: right;
    width: 64px;
    height: 16px;
    line-height: 15px;
    font-weight: normal;
    color: #ED8600;
    text-align: center;
    font-size: 10px;
}
div.boxscroll-btn {
    width: 15px;
    text-align: center;
}
div.boxscroll-prev {
    float: left;
    position: absolute;
}
div.boxscroll-next {
    float: right;
    position: absolute;
    margin-left: 49px;
}
div.boxscroll-avail {
    cursor: pointer;
}
div.boxscroll-avail:hover {
    font-weight: bold;
    color: #f0f0f0;
}
div.boxscroll-gray {
    cursor: default;
    display: none;
}
div.redbox {
    border-color: #2c0505;
    background: #080202;
}
div.redbox.box .boxhdr {
    background: none;
}
div.redbox.box .boxscroll {
    background: none;
}
div.greenbox {
    border: none;
    background: none;
}
div.greenbox.box .boxhdr {
    background: none;
}
div.greenbox.box .boxscroll {
    background: none;
}
div.yellowbox {
    border: none;
    background: #080802;
}
div.yellowbox.box .boxhdr {
    background: none;
}
div.yellowbox.box .boxscroll {
    background: none;
}
div.bluebox {
    border: none;
    background: none;
}
div.bluebox.box .boxhdr {
    background: none;
}
div.bluebox.box .boxscroll {
    background: none;
}
div.smallbox {
    width: 268px;
    border-width: 1px;
    border-style: solid;
    padding: 10px;
    margin: 5px;
}
div.smallbox .boxhdr {
    height: 40px;
    margin-bottom: -20px;
    padding: 3px 8px;
}
div.redbox.smallbox .boxhdr {
    background: #e21c21;
}
div.greenbox.smallbox .boxhdr {
    background: #e21c21;
}
div.yellowbox.smallbox .boxhdr {
    background: #e21c21;
}
div.bluebox.smallbox .boxhdr {
    background: #e21c21;
}
/* ************************************************************************** */

.lock,
.denied,
.signup,
.tmwarning {
    width: 310px;
    height: 50px;
    color: #f0f0f0;
    text-align: center;
    font-size: 16px;
    font-weight: bold;
    font-family: arial;
    letter-spacing: 1px;
    line-height: 25px;
}
.lock {
    margin: 0 auto 20px;
    padding: 40px 0px 40px 0px;
    border: 3px dashed #e21c21;
    border-radius: 15px;
    background: #300000;
    color: #93D9AD;
}
.denied {
    margin: 25px auto 45px;
    padding: 15px 0px 15px 90px;
    background: "https://themis.lo14.wroc.pl/gfx/denied.png" no-repeat;
}
.tmwarning {
    margin: 0 auto 20px;
    width: 450px;
}
.signup {
    margin: 0 auto 20px;
    padding: 25px 0px 55px 70px;
    background: "https://themis.lo14.wroc.pl/gfx/keys.png" no-repeat;
}
.signup small {
    font-size: 11px;
    line-height: 14px;
    color: #e0e0e0;
}
.signup-fail {
    color: #ff2020;
    font-weight: bold;
    font-size: 12px;
    margin-top: 10px;
}
/* ************************************************************************** */

.hintsbox {
    position: absolute;
    display: none;
    border: 1px solid #505050;
    background: #080810;
    margin: 1px 0 0 0;
    font-size: 10px;
}
.hint {
    cursor: pointer;
}
.hint-current {
    background: #000080;
    color: #f0f0f0;
}
.hint-cell {
    padding: 1px 3px;
    border: 0;
}
/* ************************************************************************** */

#index {
    display: table;
    margin: -15px;
    width: 100%;
}
#site-news {
    display: table-row;
    width: 300px;
    padding: 20px;
    border: none;
    background: none;
}
div.site-news-date {
    float: none;
    position: relative;
    font-size: 9px;
    color: #7ab117;
    margin: 5px 20px;
    margin-bottom: 3px;
    max-width: 180px;
}
div.site-news-title {
    max-width: 180px;
    display: block;
    color: #e21c21;
    float: none;
    font-size: 14px;
    font-weight: bold;
    border: 3px dashed #7ab117;
    border-radius: 15px;
    padding: 5px;
    padding-left: 10px;
    margin-bottom: 10px;
    margin-right: 100px;
    margin-left: 10px;
}
div.site-news-title:hover + div.site-news-text {
    transition: opacity 0.2s linear;
    opacity: 1.0;
}
div.site-news-text {
    display: block;
    opacity: 0.0;
    transition: opacity 0.3s linear;
    transition-delay: 0.1s;
    position: absolute;
    min-width: 300px;
    max-width: 45%;
    text-align: justify;
    border: 3px dashed #e21c21;
    border-radius: 15px;
    background: #200630;
    padding: 5px 10px;
    margin-top: -43px;
    margin-left: 220px;
    clear: both;
}
div.site-news-text:hover {
    opacity: 1.0;
}
#welcome {
    font-size: 13px;
    line-height: 22px;
    text-align: justify;
}
#sections,
#welcome {
    display: table-cell;
    padding: 0px;
    padding-bottom: 15px;
    padding-top: 30px;
}
.section {
    padding: 15px 0 5px 20px;
    margin-left: 0px;
    border-left: 3px solid #e21c21;
}
.section-type {
    float: right;
    font-size: 10px;
    margin-left: 10px;
}
.section-signup {
    color: #e21c21;
}
.section-signup~* {
    color: #630000;
}
.section-observer {
    color: #909090;
}
.section-observer~* {
    color: #5d5b5b;
}
.section-guest {
    color: #11a0ff;
}
.section-guest~* {
    color: #034169;
}
.section-user {
    color: #20e020;
}
.section-principal {
    color: #6020f0;
}
.section-admin {
    color: #e02020;
}
.section-enter {
    font-weight: bold;
}
div.section-descr {
    margin: 5px 0 0 10px;
    text-align: justify;
}
#section-archive {
    margin: 15px 0 0 0;
}
#section-arch-toggle {
    margin: 20px 0 0 0;
    font-size: 10px;
}
/* ************************************************************************** */

#account-side {
    float: right;
    width: 410px;
    margin: 0 30px 0 0;
    line-height: 20px;
    font-size: 13px;
    text-align: justify;
}
table.account {
    background: #020208 "https://themis.lo14.wroc.pl/gfx/account-hdr.png" no-repeat;
}
td.account-top {
    border: 0;
    font-weight: bold;
    color: #e0e0e0;
    padding: 3px 0 10px 10px;
}
td.account-hdr {
    border: 0;
    border-left: 1px solid #05052c;
    text-align: right;
    padding: 5px;
}
td.account-val {
    border: 0;
    border-right: 1px solid #05052c;
}
td.account-bottom {
    border: 1px solid #05052c;
    border-top: 0;
    text-align: center;
}
input.account,
select.account {
    width: 240px;
}
input.passwd {
    width: 200px;
}
div.account-ok {
    font-weight: bold;
    color: #20f040;
}
div.account-fail {
    font-weight: bold;
    color: #f02040;
}
#account-form,
#passwd-form {
    display: table-cell;
    padding: 0 30px;
    vertical-align: top;
}
/* ************************************************************************** */

#secind-left {
    display: table-cell;
    width: 70%;
    vertical-align: top;
    padding: 15px 10px 0 0;
}
#secind-right {
    display: table-cell;
    width: 30%;
    vertical-align: top;
    padding: 0 0 70px 0;
    border-left: none;
}
.secind-tab {
    display: block;
    font-size: 10px;
    color: #ED8600;
    margin: 0 0px 0 -1px;
    padding: 5px 5px 5px 10px;
}
.secind-tab-red {
    text-align: justify;
    border: 3px dashed #e21c21;
    border-radius: 15px;
    background: #300000;
    padding: 5px 10px;
    margin-bottom: 20px;
}
.secind-tab-green {
    text-align: justify;
    border: 3px dashed green;
    border-radius: 15px;
    background: #003000;
    padding: 5px 10px;
    margin-bottom: 3px;
}
.secind-tab-blue {
    text-align: justify;
    border-radius: 15px;
    background: #200630;
    padding: 5px 10px;
    border: 3px dashed #802050;
    margin-bottom: 3px;
}
.secind-tab-r2b {
    border-top-color: #802050;
}
.secind-tab-y2b {
    border-top-color: #808050;
}
.secind-tab-bottom-red,
.secind-tab-bottom-green,
.secind-tab-bottom-blue {
    margin-right: -15px;
    height: 30px;
}
.secind-tab-bottom-red {
    border-top: #fff;
}
.secind-tab-bottom-green {
    border-top: #fff;
}
.secind-tab-bottom-blue {
    border-top: #fff;
}
.secind-tab:hover {
    color: #e21c21;
    text-decoration: none;
}
.secind-tab-green:hover {
    color: #ED8600;
    background: #002000;
}
.secind-tab-blue:hover {
    color: #ED8600;
    background: #000030;
}
.secind-tab-sel {
    font-weight: bold;
    color: #e21c21;
    background: #130335;
    cursor: default;
}
.secind-news {
    text-align: justify;
    margin: 5px 5px 5px 0;
    font-weight: normal;
    color: #d0d0d0;
}
.secind-news-hdr {
    font-size: 12px;
    cursor: pointer;
}
.secind-news-hdr:hover {
    text-decoration: none;
}
#secind-hourglass {
    text-align: center;
    height: 74px;
    color: #d0d0d0;
}
#secind-hourglass-icon {
    position: absolute;
    width: 41px;
    height: 74px;
    margin-left: 5px;
    background: "https://themis.lo14.wroc.pl/gfx/hourglass.png";
}
#secind-hourglass-hdr {
    font-weight: bold;
    font-size: 13px;
    color: #f0f0f0;
    padding: 10px 0 5px;
}
#secind-embed {
    margin: 0 -5px 0 10px;
}
/* ************************************************************************** */

.problems-sort>td {
    border: none;
    color: #404040;
    font-size: 10px;
    text-align: right;
    padding: 0 3px;
    width: 100%;
}
.problems-sort>td>.ajax-clickable {
    color: #ffa500;
}
.problem-icon {
    border: none;
    height: 40px;
    min-width: 40px;
    width: auto;
    background: none;
}
.problem-code {
    border: none;
    height: 34px;
    min-width: 110px;
    width: auto;
}
.problem-name {
    border: none;
    height: 34px;
    min-width: 350px;
    width: auto;
}
.problem-term {
    border: none;
    height: 34px;
    font-size: 10px;
    text-align: right;
    min-width: 110px;
    width: auto;
}
.problem-sub {
    color: #606060;
    font-size: 10px;
    padding-top: 2px;
}
.problem-past td {
    color: #404040;
}
.problem-past .ajax-clickable,
.problem-past a {
    color: #707070;
}
.problem-future td {
    color: #508050;
}
.problem-future .ajax-clickable,
.problem-future a {
    color: #80c080;
}
.problem-done {
    width: 24px;
    height: 24px;
    border-radius: 50%;
    background: green;
    cursor: pointer;
}
.list-stats {
    float: right;
}
.list-solved {
    display: inline-block;
    width: 20px;
    text-align: center;
    color: #20FF20;
}
.list-total {
    display: inline-block;
    width: 20px;
    text-align: center;
}
.news-author {
    float: right;
    margin: 0 0 2px 0;
    font-size: 10px;
}
.news-title {
    float: left;
    margin: 0 0 2px 20px;
    font-weight: bold;
    color: #BB0000;
}
.news-text {
    clear: both;
    margin: 0 0 20px;
    border-bottom: 3px dashed #303030;
    padding: 0 0 10px;
    line-height: 18px;
    text-align: justify;
}
/* ************************************************************************** */

#problem-nav {
    float: right;
    margin: 0 -10px 0 0;
}
#problem-nav div {
    display: table-cell;
    border-spacing: 3px;
    height: 30px;
    width: 106px;
    cursor: pointer;
}
#problem-status {
    background: green;
    border-radius: 5px;
    font-size: 12px;
    text-align: center;
    margin: 3px;
    padding: 3px;
}
#problem-status:after {
    content: "Problem status";
}
#problem-best {
    background: #ED8600;
    border-radius: 5px;
    font-size: 12px;
    text-align: center;
    margin: 3px;
}
#problem-best:after {
    content: "TOP";
}
#problem-submit {
    background: #8b0000;
    border-radius: 5px;
    font-size: 12px;
    text-align: center;
    margin: 3px;
}
#problem-submit:after {
    content: "SUBMIT";
}
#problem-questions {
    background: url("https://themis.lo14.wroc.pl/gfx/problem-question.png") no-repeat;
}
em.problem-notsolved {
    color: #ff4010;
}
em.problem-solved {
    color: #40ff10;
}
em.problem-stats-frozen {
    color: #a0a0ff;
}
#problem-notes {
    margin: 10px -15px -15px -15px;
    border-top: 1px solid #282828;
    background: #101010 "https://themis.lo14.wroc.pl/gfx/problem-notes.png" no-repeat 15px 10px;
    padding: 10px 25px 15px 70px;
    min-height: 55px;
    clear: both;
}
#problem-submit-box {
    display: none;
    height: 260px;
    background: none;
}
#problem-status-box {
    display: none;
    min-height: 300px;
    background: none;
}
#problem-best-box {
    display: none;
    min-height: 300px;
    background: none;
}
#problem-submittxt {
    text-align: center;
    font-size: 10px;
}
#problem-submitsrc {
    background: none;
    display: block;
    width: 290px;
    height: 160px;
    margin: 3px auto;
    font-family: monospace;
    font-size: 12px;
    color: inherit;
    overflow: auto;
}
#problem-submitfile {
    display: block;
    margin: 5px auto 10px;
}
#problem-submitrate {
    margin: 10px 0;
    text-align: center;
    font-size: 10px;
}
#problem-submitlang {
    background: #170224;
    float: left;
    width: 160px;
}
#problem-submitbtn,
#problem-cancelbtn {
    float: right;
    width: 60px;
    height: 21px;
}
#problem-submitsrc:hover,
#problem-submitbtn:hover,
#problem-cancelbtn:hover,
#problem-submitfile:hover {
    background: #0f0914;
}
#problem-submitiframe {
    display: none;
}
#problem-status-loading {
    line-height: 270px;
    text-align: center;
    color: #f0f0f0;
}
#problem-status-table {
    color: #ED8600;
    margin: 0 auto;
    text-align: center;
    font-size: 10px;
}
#problem-status-table td {
    border-top: 1px dashed #ed8600;
}
.problem-status-verdict {
    width: 35px;
    font-weight: bold;
}
.problem-status-date {
    width: 55px;
}
.problem-status-user {
    width: 110px;
    font-size: 12px;
    word-spacing: 100px;
    color: #D61043;
}
.problem-status-pts {
    width: 25px;
    text-align: center;
}
.problem-status-res {
    width: 65px;
    text-align: right;
}
a.problem-status-my {
    font-size: 9px;
    font-weight: normal;
