css:
@-moz-document domain("impress.co.jp") {
header>nav>div{
    min-width: 980px;
    width: 100%;
  }
  #page-wrap {
    width: 100%;
  }
  #page {
    width: 100%;
  }
  #main {
    float: none;
    width: auto;
    min-width: 650px; 
  }
  footer>* {
    min-width: 980px;
    width: auto;
  }

  aside.ad.billboard {
    width:100%;
    min-height:1px;
    height:1px;
  }
  aside.ad.background {
    width:100%;
  }
  .recommend > ul.list-02 li.item {
    min-width: 655px;
    width: 100%;
  }
 /* .recommend > ul.list-02 li.item div.body div.text p.title {
    min-width: 420px;
    width: 100%;
  }*/
  /*.recommend > ul.list-02 li.item div.body div.text {
    min-width: 435px;
    width: calc(100% - 220px);
  }*/
ul#recommend-1.list-02 li.item.selected div.body div.text{
    width: calc(100% - 169px);
}
ul#recommend-1.list-02 li.item div.body div.text{
    width: calc(100% - 169px);   
}
ul#recommend-1.list-02 li.item div.body div.text p.title{
    width: 100%
}
  .recommend > ul.list-02 li.item div.body div.image {
    min-height: 60px;
  }
  .recommend > ul.list-02 li.item p.outline {
    min-height: 60px;
  }
  #extra ad.right > .recommend > ul.list-02 li.item {
    width: 300px;
    min-width: 300px;
  }
  #extra .list ul.list-02 li.item {
      width: 300px;
  }

  #daily-block-0  div.body, #summary-block div.body, 
  #watch-static-pc-c div.body, #daily-block-1 div.body,
  #daily-block-2 div.body, #daily-block-3 div.body {
    min-width: 300px;
    width: 100%;
  }
.latest-backnumber > ul:nth-child(1) > li:nth-child(1) > div.body {
    min-width: 300px;
    width: 100%;
  }
  /*  aside.ad.haikei {
    width: 100%;
    margin-left: 0%;
  }
*/
  paginate.corner > ul > li {
    min-width: 325px;
    width: calc(50% - 50px);
  }
  .top-news > div > ul > li {
    width: 50%;
    min-width: 330px;
  }
  .top-news > div > ul > li.index-1 div.body > div.text p {
    width: 100%;
  }
  .top-news > div > ul > li.index-1 div.body > div.text {
    width: 100%;
  }
  .top-news > div > ul > li div.body > div.text {
    min-width: 165px;
    width: calc(100% - 165px);
  }
nav.paginate.corner ul li{
    width: 50%;
}

nav.paginate.corner ul li div.body{
    width: 100%;
    height: 107px;
}
nav.paginate.corner ul li.next div.body div.image{
    left: unset;
    right: 30px;
}
nav.paginate.corner ul li.next div.body div.text{
    width: calc(100% - 100px);
}
@media screen and (max-width: 979px){
    /* no sidebar */
    body {
		min-width: unset;
    }
    #main{
        min-width: unset;
    }
    #extra {
	    display: none;
    }
    .top-news > div > ul {
	    width: 100%;
        min-width: 660px;
    }
    .top-news > div > ul > li.index-2 {
	    width: 50%;
    }
    #summary-block > li {
	    overflow: hidden;
	    max-width: 660px;
        width: 100%;
    }
    body #global-nav {
	    width: 100%;
    }
    #global-nav > div > div {
	    min-width: 80px;
    }
    .recommend > ul.list-02 li.item{
	    min-width: unset;
    }
    ul.list-02 li.item div.body {
	    min-width: 340px;
    }
}
}
