css:
@-moz-document domain('deviantart.com'){
.cc-name {
    letter-spacing: 0;
    font-size: inherit;
}

.cc-time {
    margin-left: 3px;
}

.cc-time a {
    color: #97a5a4 ;
}

.cc-avatar {
    margin-top: 0px;
    margin-left: 0px;
    margin-right: 19px;
    padding-left: 1px;
}

.ccomment {
    margin-bottom: 0px;
    padding-right:0px;
}

.cctextarea
{
margin-left:-2px;
}


.cc-meta {
    margin-bottom: 4px;
}

.ccomment div.text {
}

#gmi-CComment .ch-ctrl a.reply {
    margin-top: -30px;
    position: absolute ;
    right: 40px ;
    top: 32px ;
    left:auto;
    bottom: auto ;
}


.pagination-wrapper 

{
padding-left:20%;
padding-right:10%;
margin-top:-20px;
}


.ccomment .cc-in {
    min-height: 32px ;
    padding: 3px 4px;
    margin-bottom:-2px;
}

.ccomment .ch-ctrl .ch-ctrl {
    border-width: 0px ;
    border-bottom-left-radius: 4px 4px;
    border-bottom-right-radius: 4px 4px;
    border-top-left-radius: 4px 4px;
    border-top-right-radius: 4px 4px;
    font-size: 1em;
    background-color: #E4EBDE;
    border-bottom: 1px solid #C9D3CA ;
    border-top: 1px solid #F5FAF5 ;
    padding-bottom:5px;
    padding-left:5px;
}

.ccomment i.l {
    display: none;
}

.ctext .cc-manage-button {
    visibility: hidden;
}

.ctext:hover .cc-manage-button {
    visibility: visible;
}

.cc-name .u {
    text-decoration: underline;
    color: #3B5A4A;
}

.ch-ctrl .reply {
    visibility: hidden;
}

.ch-ctrl:hover .reply {
    visibility: visible;
}

.talk-tower h2.c, .talk-tower .grmood, .cc-mood {
    display: none;
}

.cc-avatar .avatar {
    width: 50px;
    height: 50px;
    padding-top:2px;
}

div.nest {
    padding-left: 27px;
}
/* post reply */
.ccomment-post {
margin-top: 0px;
}
/* hide emoticons */
.ch-ctrl .previewbox td.rr {
display:block;
}

.ccomment-form .avatar
{
left: -69px;
    margin: 1px 0 0;
    position: absolute;
}




div.talk-post textarea, div.talk-post div.pager-holder, div.talk-post div.pager2 {
height: 70px !important;

}
}
