css:
@-moz-document domain(www.metafilter.com), domain(metafilter.com) {

	body {
		color: #000 !important;
		margin: 0px !important;
		padding: 0px !important;
		background: #D3D3D3 !important;
	}

	.copy {
		line-height: 108% !important;
	}
	
	.comments {
		line-height: 108% !important;
	}
	
	a:link {
		color: #516874 !important;
		font-weight: normal !important;
		text-decoration: none !important;
		background-color: transparent !important;
		border-color: #516874 !important;
		border-bottom-width: 1px !important;
		border-left-width: 0px !important;
		border-right-width: 0px !important;
		border-top-width: 0px !important;
		border-style: dashed !important;
	}

	a:visited {
		color: #516874 !important;
		font-weight: normal !important;
		text-decoration: none !important;
		background-color: transparent !important;
		border-color: #516874 !important;
		border-bottom-width: 1px !important;
		border-left-width: 0px !important;
		border-right-width: 0px !important;
		border-top-width: 0px !important;
		border-style: dashed !important;
	}

	a:active {
		color: #516874 !important;
		font-weight: normal !important;
		text-decoration: none !important;
		background-color: transparent !important;
		border-color: #516874 !important;
		border-bottom-width: 1px !important;
		border-left-width: 0px !important;
		border-right-width: 0px !important;
		border-top-width: 0px !important;
		border-style: dashed !important;
	}

	a:hover {
		color: #516874;
		text-decoration: none !important;
		background-color: transparent !important;
		border-color: #516874 !important;
		border-bottom-width: 1px !important;
		border-left-width: 0px !important;
		border-right-width: 0px !important;
		border-top-width: 0px !important;
		border-style: dashed !important;
	}

	#logo a {
		border-top-width: 0px !important;
		border-bottom-width: 0px !important;
	}

	.smallcopy {
		color: #666 !important;
	}
	
	.sidebar {
		background: #EFEFEF !important;
		border-top: 1px solid #666 !important;
		border-bottom: 1px solid #666 !important;
	}
	
	#topline {
		background: #D3D3D3 repeat-x top left !important;
		height: 28px !important;
		line-height: 28px !important;
		text-align: right !important;
	}
	
	#yellowbar {
		font-size: 10px !important;
		color: #333 !important;
		background: #EE0 !important;
		border-top: 1px solid white !important;
		border-bottom: 1px solid white !important;
		height: 12px !important;
		line-height: 12px !important;
		text-align: right !important;
		padding-right: 6px !important;
	}
	
	#bottomline {
		height: 31px !important;
		line-height: 14px !important;
		padding-top: 2px !important;
	}
	
	#navglobal a, #navseldom a, #navoften a {
		text-align: center !important;
		padding: 0px 7px 0px 7px !important;
		margin: 0px !important;
		font-weight: bold !important;
		text-decoration: none !important;
		color: #06C !important;
	}
	
	#navglobal a:hover, #navseldom a:hover, #navoften a:hover {
		padding: 0px 7px 0px 7px !important;
		margin: 0px !important;
		font-weight: bold !important;
		text-decoration: none !important;
		color: #C00 !important;
	}
	
	#footer {
		font-size: 12px !important;
		font-family: arial !important;
		background: #EFEFEF !important;
		width: 100% !important;
		padding-bottom: 10px !important;
		margin-top: 40px !important;
		margin-left: auto !important;
		margin-right: auto !important;
		border-top: 1px solid #666 !important;
		border-bottom: 1px solid #666 !important;
	}
	
	.fineprint {
		font-size: 11px !important;
		color: #666 !important;
	}
	
	.posttitle {
		margin-left: 50px !important;
		font-weight: normal !important;
		color: #000 !important;
		font-family: Cambria, "Times New Roman",serif !important;
		margin-bottom: 15px !important;
		font-size: 20px !important;
		font-style: normal !important;
	}
	
	.tags {
		background: #EFEFEF !important;
		margin-left: 20px !important;
		padding: 8px !important;
		line-height: 150% !important;
		width: 120px !important;
		text-align: left !important;
	}

	.tags a {
		color: #06C !important;
		border-top-width: 0px !important;
		border-bottom-width: 0px !important;
	}

	.copy a, #navseldom a, #navoften a, #navglobal a, .mefimessages a, .footpad a, .posttitle .smallcopy a, .sidebar a, .leftside a, #commentform .smallcopy a, .comments .whitesmallcopy a
		{
		color: #000000 !important;
		font-weight: bold !important;
		text-decoration: none !important;
		background-color: transparent !important;
		border-color: #000000 !important;
		border-bottom-width: 0px !important;
		border-left-width: 0px !important;
		border-right-width: 0px !important;
		border-top-width: 0px !important;
		border-style: dashed !important;
		}
	
	.copy a:hover, #navseldom a:hover, #navoften a:hover, #navglobal a:hover, .mefimessages a:hover, .comments a:hover, .footpad a:hover, .posttitle .smallcopy a:hover, .sidebar a:hover, #commentform .smallcopy a:hover, .comments .whitesmallcopy a:hover

		{
		background-color: #888888 !important;
		color: #FFFFFF !important;
		font-weight: bold !important;
		text-decoration: none !important;
		border-color: #000000 !important;
		border-bottom-width: 0px !important;
		border-left-width: 0px !important;
		border-right-width: 0px !important;
		border-top-width: 0px !important;
		border-style: dashed !important;
		}

	.copy a:hover
		{
		font-weight: normal !important;
		}
	
	.comments .smallcopy a
		{
		font-weight: bold !important;
		border-top-width: 0px !important;
		border-bottom-width: 0px !important;
		}

	.reason
		{
		background-color: #CCCCCC !important;
		border-radius: 15px 15px 15px 15px !important;
		padding: 8px !important;
		}
	
	.best
		{
		background: #FFFFFF !important;
		}
	
	.bestleft
		{
		border-color: #999999 !important;
		}
	
	.darker
		{
		background-color: #999999 !important;
		}
	
	#commentform .comments
		{
		background-color: #999999 !important;
		} 
	
	.mycomments
		{
		background-color: #999999 !important;
		border-radius: 15px 15px 15px 15px !important;
		padding: 8px !important;
		}
	
	#yellowbar
		{
		background-color: #EFEFEF !important;
		border-radius: 15px 15px 15px 15px !important;
		padding: 8px !important;
		}
		
	#maintab li.selected a
		{
		background-color: #CCCCCC !important;
		color: #000000 !important;
		border-radius: 11px 11px 0px 0px !important;
		}
	
	#maintab li a
		{
		background-color: #EFEFEF !important;
		color: #000000 !important;
		border-radius: 11px 11px 0px 0px !important;
		}
	
	#maintab li a:hover
		{
		background-color: #999999 !important;
		color: #000000 !important;
		border-radius: 11px 11px 0px 0px !important;
		}
	
	div.comments span.smallcopy
		{
		display: block !important;
		color: #606060 !important;
		background: #A2A2A2 !important;
		margin: -1px 1px -1px 1px !important;
		padding: 1px !important;
		}
	
	.comments .smallcopy a
		{
		color: #606060 !important;
		font-weight: bold !important;
		}

	.warning
		{
		color: #606060 !important;
		background: #A2A2A2 !important;
		}

	.warning a
		{
		color: #606060 !important;
		background: #A2A2A2 !important;
		font-weight: bold !important;
		}

	/* This doesn't work. */
	h2#body a:link
		{
		color: #000000 !important;
		}
	
	.posttitle a
		{
		color: #000000 !important;
		}
	
	.copy .userpage p, #contactPrefs .userpage, #optional .userpage
		{
		background-color: #cccccc !important;
		}

	.copy a
		{
		color: #516874 !important;
		font-weight: normal !important;
		text-decoration: none !important;
		background-color: transparent !important;
		border-color: #516874 !important;
		border-bottom-width: 1px !important;
		border-left-width: 0px !important;
		border-right-width: 0px !important;
		border-top-width: 0px !important;
		border-style: dashed !important;
		}

	.copy .smallcopy a
		{
		color: #000000 !important;
		font-weight: normal !important;
		font-style: italic !important;
		border-bottom-width: 0px !important;
		border-top-width: 0px !important;
		}


	.sidebar a
		{
		color: #516874 !important;
		font-weight: normal !important;
		text-decoration: none !important;
		background-color: transparent !important;
		border-color: #516874 !important;
		border-bottom-width: 1px !important;
		border-left-width: 0px !important;
		border-right-width: 0px !important;
		border-top-width: 0px !important;
		border-style: dashed !important;
		}

	#maintab li.selected a
		{
		border-style: solid !important;
		border-color: #CCCCCC !important;
		border-left-width: 1px !important;
		border-right-width: 1px !important;
		color: #000000 !important;
		}
	
	#maintab li a
		{
		border-style: solid !important;
		border-color: #CCCCCC !important;
		border-left-width: 1px !important;
		border-right-width: 1px !important;
		color: #000000 !important;
		}
	
	#maintab li a:hover
		{
		border-style: solid !important;
		border-color: #CCCCCC !important;
		border-left-width: 1px !important;
		border-right-width: 1px !important;
		color: #000000 !important;
		}
}

@-moz-document url-prefix('http://www.metafilter.com/contribute/activity/')
{
	*
		{
			line-height:108% !important;
		}
}
