css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.plex.tv") {

#container, 
.FilterMenu li a:hover
{
background:#282A2D !important;
}
input, select
{
background:#000000 !important;
}
.Breadcrumbs, .breadcrumb, .Item-Body,
.SearchForm, .well, .Item, .DiscussionHeader,
.InfoMessage, .alert-success, .DismissMessage,
.FilterMenu li a
{
background:#3F4245 !important;
}
.TagCloud li
{
background-color: #3F4245 !important;
}
.Count
{
background-color: #e5a00d !important;
}
.CategoryBox .H, .CategoryGroup .H, .MessageList .Item-Header, .MessageList.Conversation .Meta, .panel-default > .panel-heading
{
background-color:#E5A00D !important;
border-color:#E5A00D !important;
}
.breadcrumb, .Breadcrumbs,
.well.search-form
{
border: 1px #3F4245 solid !important;
}
blockquote
{
border: 1px #E5A00D solid !important;
border-left: 3px #E5A00D solid !important;
}
.InfoMessage, .alert-success, .DismissMessage, input
{
border-color: #3F4245 !important;
}
.Item 
{
border-color:#282A2D !important;
}
.Item 
{
border-width:3px !important;
}
.CommentHeader
{
padding-bottom: 0 !important;
}
.Title
{
font-weight: bold !important;
}
.Title, .ItemContent, .MItem,
.Title a, .MItem a, .Username,
.FilterMenu li a:hover
{
color: #E5A00D !important;
}
.DismissMessage, .CrumbLabel span,
.CategoryDescription, .Message p, h4,
.TagCloud li a, .TagCloud li a .Count,
.FilterMenu li a, input, select, time,
.Username, .PostCount, .Rank, .MItem, 
.Rank-PlexPass, .Message,
.MItem a, .Category a
{
color: #FFFFFF !important;
}
}
