css:
@-moz-document domain("tuoitre.vn") {
body {
    color: #a6a6a6;
    background-color: #000;
    background-image: url(data:image/png;base64,) !important;
}
.wrapper-nav {
    background: #202020;
    box-shadow: 0 0 15px #000000 !important;
    border-bottom: 2px solid rgba(0, 0, 0, .7) !important;
    text-shadow: 0px 0px 2px #000 !important;
}
.wrapper-vhgt .mega-menu,
.list-link,
.list-select,
.block-share,
.title.txt-green,
.block-camera .inner-slider,
.mega-menu,
table.desc_image .ck_legend,
.block-camera .slider-1,
.fck .ck_legend p {
    background: #202020;
}
.fck table.trichdan,
.dbox td,
.description {
    background: #161616;
}
.nav-main>ul>li>a,
.flexslider-1 .slides li a,
.block-feature-1 p,
.fck p,
.block-key li:first-child,
.lst-comment ul li dl dd p,
.list-media li h4 a,
.list-1 li a,
.feature-1 a,
.block-1 h4 a,
.list-bc li a,
.list-link dt a,
.list-link dd a,
.bottom-footer,
.bottom-footer p.float-r a,
.date,
.list-select li,
.list-select li a,
.top-menu li a,
.list-social li a,
.block-feature p,
.list-news li h4 a,
.block-left h3 a,
.block-left>p,
.highlight h2 span a,
.list-thumb a,
.block-2 .thumb a,
.highlight .slider-tvo article h2 a,
.highlight,
.highlight .art-tvo h1 a,
.list-etm li h4 a,
.carousel-1 li h3 a,
.frm-idea ul li,
.btn-view-1,
.block-enter-24 .block-right li a,
.block-enter-24 .block-left h3 a,
.title.txt-green>a,
.frm-idea ul li,
table.desc_image .ck_legend,
.block-key li a,
.inner-slider a:first-child {
    color: #a6a6a6;
}
html>body .Wrapper {
    background: #202020 !important;
}
.block-comment .form-comment textarea {
    color: #a6a6a6 !important;
    background: #202020 !important;
    border-color: #5a5a5a;
}
/* title */

.wrapper-vhgt .breadcrumb li a:hover,
.wrapper-vhgt .breadcrumb li.active a,
.wrapper-vhgt .block-feature:hover h1 a,
.wrapper-vhgt .list-news li:hover h4 a,
.wrapper-vhgt .block-1:hover h4 a,
.wrapper-vhgt .list-1 li a:hover,
.wrapper-vhgt .block-left h3 a:hover,
.wrapper-vhgt .feature-1:hover a,
.wrapper-vhgt .list-media li:hover h4 a,
.wrapper-vhgt .highlight h2>a:first-child,
.wrapper-vhgt .title a,
.wrapper-vhgt .block-qc-1 .title select,
.wrapper-vhgt .title,
.block-feature h1 a,
.detail-content .block-feature .title-2 a:hover,
.detail .block-feature:hover h1 a {
    color: #288ad9 !important;
}
.wrapper-vhgt .highlight h2 {
    border-color: #288ad9;
}
.wrapper-vhgt .title,
.title,
.nav-main>ul>li>a,
.nav-main>ul,
.block-enter-24 {
    background: none;
}
.block-left {
    background: none !important;
}
.nav-main>ul>li {
    border-bottom: none;
}
.nav-main>ul>li:first-child>a,
.list-appmobile .fa-apple:before,
.list-appmobile .fa-android:before {
    -webkit-filter: invert(100%) !important;
}
.wrapper-vhgt .title,
.title {
    border-bottom: 1px solid #288ad9;
}
.list-appmobile li a i,
.btn-top {
    background-color: #383838;
}
.wrapper-vhgt .mega-menu,
.wrapper-nav,
.frm-search,
.lst-comment > ul > li,
.lst-comment > ul,
.top-footer,
.list-link,
.mega-menu,
.btn-qt > span {
    border-color: #5a5a5a;
}
.wrapper-slider,
.block-share ul {
    border: none;
}
.block-key li a {
    background: #343434;
}
}
