css:
@-moz-document regexp("https://picarto.tv/(?!site/dashboard).*") {
/* -------------------------------------------------------------------- */
/* This is just a random CSS made to enhance small bits of Picarto...   */
/* -------------------------------------------------------------------- */
/* Avatars   */

.roundAvatar {
	border-radius: 0%;
}
.streamer_avatar_basic {
	border: 2px solid rgba(0, 0, 0, 0);
	box-shadow: 0px 0px 5px 2px rgb(171, 26, 180);
}
.streamer_avatar_prem {
	border: 2px solid rgba(0, 0, 0, 0);
	box-shadow: 0px 0px 5px 2px rgb(201, 111, 12);
}
.streamer_lang1,
.streamer_lang2 {
	display: none;
}
.accstatus {
	margin-left: 66px;
	margin-top: -20px;
}
/* Bakcground/Border colors */

#player_offimage {
	opacity: 0.8 !important;
}
.colBG1 {
	background: rgba(0, 0, 0, 0.6) !important;
}
.colBG2,
.colBG3 {
	background: rgba(0, 0, 0, 0.80);
}
.colBG3 {
	background: rgba(0, 0, 0, 0.90) !important;
}
.borderBGW0-2 {
	border-color: rgba(0, 0, 0, 0.80);
}
.frame .columnright {
	border-left: 3px solid rgba(0, 0, 0, 0.80);
}
#channel_chatbanner {
	border-bottom: 2px solid rgba(0, 0, 0, 0.80);
}
.borderBG3 {
	border: none;
}
.cutLine0 {
	border-bottom: 1px solid rgba(0, 0, 0, 0.75);
}
hr {
	border-top: 2px solid rgba(0, 0, 0, 0.9);
}
/* Text/Icons Colors */

.streamer_streamername {
	color: #90af54;
}
.streamer_follower {
	color: rgb(169, 68, 66);
}
.streamer_subbs {
	cursor: default;
	color: rgb(230, 178, 55);
}
.streamer_totalviews {
	color: rgb(4, 128, 164);
}
.streamer_extras .label {
	color: #90af54;
}
/* ------------ */
/* Chat Edits   */
/* ------------ */
/* Chat Bottom */

#chatBottom {
	border-top: none;
	border-bottom: none;
}
/* Usernames */

.msgUsername {
	padding: 0px !important;
	text-shadow: 0px 0px 3px rgba(0, 0, 0, 0.50), 1px 1px 0px rgba(0, 0, 0, 0.70);
	font-family: "Segoe UI", "Trebuchet", Helvetica, sans-serif;
	font-size: 12px !important;
	-moz-font-smoothing: antialiased !important;
	-webkit-font-smoothing: antialiased !important;
}
.msgAdminUsername {
	font-family: "Segoe UI", "Trebuchet", Helvetica, sans-serif;
	text-shadow: 0px 0px 6px rgba(255, 255, 255, 0.5);
	font-size: 13px !important;
	font-weight: bold;
	-moz-font-smoothing: antialiased !important;
	-webkit-font-smoothing: antialiased !important;
}
/* Messages */

.update {
    border-radius: 0px;
    margin: 0px 0px;
    border-top: 2px solid rgba(0,0,0,0.9);
    border-bottom: 2px solid rgba(0,0,0,0.9);
}

.globalli {
    margin: 0px 0px;
    border-radius: 0px;
    margin-bottom: 0px;
    border-bottom: 2px solid rgba(144,175,84,0.9);
    border-top: 2px solid rgba(144,175,84,0.9);
}

.colGreen {
    background-color: rgba(144, 175, 84, 0.39);
}

.removeli {
    background-color: rgba(169, 68, 66, 0.25);
    border-radius: 0px;
    border-bottom: 2px solid rgba(169, 68, 66, 0.35);
}

.msgContentIdentifier {
	display: inherit;
	width: 100%;
}
.my_msgContentIdentifier {
	width: 100%;
	font-family: verdana;
	font-size: 12px;
}
.theMsg,
.theSndMsg {
	text-shadow: 0px 0px 3px rgba(0, 0, 0, 0.50), 1px 1px 0px rgba(0, 0, 0, 0.70);
	font-size: 11px !important;
	-moz-font-smoothing: antialiased !important;
	-webkit-font-smoothing: antialiased !important;
}
.theMsg {
	display: block;
	padding: 3px 0px;
}
.theSndMsg {
	border-top: 1px solid rgba(0, 0, 0, 0.90);
}
.infoContent {
	display: inline-box;
	min-width: 320px;
	max-width: 320px;
	min-height: 60px;
	margin-left: 4px;
	padding: 5px 10px;
	border-bottom: 2px solid rgba(0, 0, 0, 0.90);
	border-radius: 0px 0px 0px 0px;
	background-color: rgba(0, 0, 0, 0.70);
	vertical-align: top;
}
.my_infoContent {
	display: inline-block;
	min-width: 310px;
	max-width: 310px;
	min-height: 60px;
	margin-right: 4px;
	padding: 5px 10px;
	border-bottom: 2px solid rgba(0, 0, 0, 0.90);
	border-radius: 0px 0px 0px 0px;
	background-color: rgba(0, 0, 0, 0.70);
	text-align: left;
	vertical-align: top;
	font-size: 12px;
}
.mymessageli {
	margin-right: 45px;
	margin-left: 5px;
	padding: 3px 5px;
	color: #C5C5C5;
	text-align: right;
	font-size: 12px;
}
/* Chat Avatars */

.chat_usr_ava {
	display: inline-block;
	width: 60px;
	height: 60px;
	margin: 0px 0px 0px -40px;
	margin-top: 0px;
	border: 2px solid rgba(0, 0, 0, 0.4) !important;
	border-radius: 0px;
	cursor: pointer;
}
.my_chat_usr_ava {
	display: inline-block;
	width: 60px;
	height: 60px;
	float: right;
	margin-top: 0px;
	margin-right: -35px;
	border: 2px solid rgba(0, 0, 0, 0.4) !important;
	border-radius: 0px;
}
/* ------------ */
/* HTML5 Player */
/* ------------ */

.jw-skin-picarto .jw-controlbar {
	border-top: rgba(0, 0, 0, 0.9)1px solid;
}
/* ------------- */
/* Fixes / Bonus */
/* ------------- */
/* Remove Premium Add */

ul.specialbanner.colBG1.row {
	display: none;
}
}
