@namespace url(http://www.w3.org/1999/xhtml);
/* i really want this to be global */
@-moz-document domain("softwaredb-dev.uwyo.edu") {

/*

Version 2.3 of the software DB style sheet
Designed after the WyoWeb protal

Friday, November 4, 2016.

Aaron Galloway-Perrell
perrella@uwyo.edu

2.5 -
Added Support for Btncss.io's .bttn-jelly class for the client portal.
Added additinoal styles for the Client Reporting Page L#286

2.2 -
Added styles for the login page - nothing major, just house keeping.

2.1 -
Fixed a series of input errors and padding issues.

2.0 -
New styling for the software db begins

*/

  table, td, th, tr, thead, tbody, tfoot, colgroup, col {
    border-color: rgba(74, 48, 36, 0.52) !important;
}


/* Login Page */

  .t12Login, .t12RegionWithoutButtonsAndTitle {
   border: 0px !important;
  }


/* Header Stylings */

  .t12PageBody {
    background: #ffc425 none repeat scroll 0 0;
    box-shadow: 0 3px 3px rgba(0, 0, 0, 0.4);
    margin-bottom: 10px !important;
    padding: 10px !important;
  }

  .t12PageBody tr {
    padding-top: 5px !important;
    padding-bottom: 5px !important;
  }

  .t12ApplicationLogo span {
      color: #492f24 !important;
      font-size: 2em !important;
      font-weight: bold !important;
      line-height: 24px;
      margin: 0 auto;
      padding-left: 5px;
      padding-right: 5px;
      text-decoration: none;
      text-transform: uppercase;
      top: 0;
      text-align: center !important;
  }

  .t12PageBody tr:nth-child(2) {
    display: list-item;
    margin: 0 auto !important;
    max-width: 1024px !important;
    overflow: hidden;
    text-align: center;
    width: 100% !important;
  }

  .t12UserName {
    font-size: 10px;
    padding: 0 20px 4px 50px;
    white-space: nowrap;
  }

  .t12ApplicationLogo {
    padding: 5px 10px !important;
  }

  .t12ApplicationLogo td {
    text-align: center;
    width: 100%;
    padding: 10px !important;
  }

  .t12PageBody tbody tr td table tbody tr td {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 0 !important;
    color: #482f25;
    padding-bottom: 3px !important;
  }

  .t12PageBody tbody tr td table tbody tr td a {
    color: #482f25;
    font-weight: bold;
  }

  .t12PageBody tbody tr td table tbody tr td a:hover {
    color: #d09615;
  }


  .t12PageBody tr:nth-child(4) {
    color: white !important;
    text-align: center;
  }

 .t12BreadcrumbRegion {
    background-color: transparent !important;
    border: 0 none;
    font-size: 12px;
    height: 16px;
    line-height: 16px;
    margin: auto;
    max-width: 960px;
    width: 100%;
}
  .t12BreadcrumbMenu {
    background-color: transparent;
    border-bottom: 0 solid #aaaaaa;
    color: white;
    font-size: 12px;
    font-weight: bold;
    padding-left: 0;
  }

  .t12BreadcrumbMenu::before {
    color: white;
    content: ">  ";
  }

  .t12BreadcrumbMenu a {
    color: #704525;
  }

  /* Body Styles */

  .apexir_WORKSHEET_DATA th {
    background: #ffc425 none repeat scroll 0 0;
    border-bottom: 1px solid #482f25;
    border-top: 1px solid #482f25;
    color: #482f25;
  }

  .apexir_WORKSHEET_DATA th a {
    color: #482f25;
  }

  .apexir_WORKSHEET_DATA th div {
   text-decoration: none;
   color: #482f25;
  }

  .apexir_WORKSHEET_DATA th.current {
    background: rgba(255, 196, 37, 0.76) none repeat scroll 0 0;
  }

  tr.odd td {
    background-color: white !important;
  }

  .apexir_WORKSHEET_DATA tr.even:hover td, .apexir_WORKSHEET_DATA tr.odd:hover td {
    background-color: rgba(255, 196, 37, 0.41) !important;
  }

  .t12SidebarRegion .t12Header, .t12header {
    background-color: #ffc425 !important;
    color: #492f24 !important;
    font-size: 14px !important;
    padding: 10px;
    text-align: center;
  }
  .t12SidebarRegion .t12Body {
    padding: 10px;
  }

  .t12data a, .t12SidebarRegion a, .rpt-sort > a {
   color: #222222 !important;
   font-weight: 600;
   font-size: 14px !important;
  }

  #report_1226620197662546_catch > table {
    width: 90%;
  }

 .t12data a:hover, .t12SidebarRegion a:hover {
      color: #0274d4;
      text-decoration: underline;
  }

  .ui-widget-header {
    background: #482f25 none repeat scroll 0 0;
    border: 1px solid #4297d7;
    color: #ffc425;
    font-weight: bold;
    margin: 0 auto;
    text-align: center !important;
  }

  .apexir_TOOLBAR_OPEN {
    background: transparent !important;
    display: inline-block !important;
    float: none !important;
    height: 40px;
    overflow: visible;
    text-align: center;
  }

  #apexir_TOOLBAR {
    display: inherit;
    float: left;
    padding: 0 0 4px;
    text-align: center;
    width: 100%;
  }

  .apexir_TOOLBAR_CLOSE {
    background: transparent none repeat scroll 0 0;
    color: #000;
    float: left;
    overflow: visible;
    padding: 4px 8px 8px 0;
  }

  .t12ReportsRegion {
    width: 100%;
  }

  #apexir_columnsearch, #apexir_col_values_drop, #apexir_rollover {
    display: list-item;
    padding: 5px 0 5px 5px;
    text-align: left;
  }

  #templatedetails {
    margin: 0 auto;
    max-width: 650px;
    width: 100%;
  }

  #templatedetails tr:nth-child(2n+1) {
    background: rgba(255, 196, 37, 0.18) none repeat scroll 0 0;
  }

  .t12standardalternatingrowcolors td.t12dataalt, .apexir_WORKSHEET_DATA tr.even td {
   background-color: rgba(255, 196, 37, 0.23);
  }

  #apexir_NO_DATA_FOUND_MSG {
    font-size: 2em;
    left: 50%;
    position: absolute;
    text-align: center;
    top: 50%;
  }

  #apexir_DaTA_PANEL {
    text-align: center;
  }
}


/* #################### FRONT END STYLING #### */


.welcomeDB { /* cetner everything for design, align text later */
    text-align: center;
}



/*  SECTIONS  */
.section {
	clear: both;
	padding: 0px;
	margin: 0px;
}

/*  COLUMN SETUP  */
.col {
	display: block;
	float:left;
	margin: 1% 0 1% 1.6%;
}
.col:first-child { margin-left: 0; }

/*  GROUPING  */
.group:before,
.group:after { content:""; display:table; }
.group:after { clear:both;}
.group { zoom:1; /* For IE 6/7 */ }

/*  GRID OF TWO  */
.span_2_of_2 {
	width: 100%;
}
.span_1_of_2 {
	width: 49.2%;
}

/*  GO FULL WIDTH AT LESS THAN 480 PIXELS */

@media only screen and (max-width: 480px) {
	.col {
		margin: 1% 0 1% 0%;
	}
}

@media only screen and (max-width: 480px) {
	.span_2_of_2, .span_1_of_2 { width: 100%; }
}


