css:
@-moz-document domain("rideuta.com") {
#site-head {
	background: black;
	position: relative;
}
#site-head nav {
	float: left;
	margin: 0 14px 0 0;
}
body {
	font-family: times roman numeral;
}
#site-footer {
	background: black;
}
.spaced {
	padding: 10px 0;
}
.btn {
	border-radius: 0px;
	padding: 0px 10px;
	line-height: 40px;
	display: inline-block;
	min-width: 120px;
	-webkit-transition: all 0.25s ease-in-out;
	-moz-transition: all 0.25s ease-in-out;
	-ms-transition: all 0.25s ease-in-out;
	-o-transition: all 0.25s ease-in-out;
	transition: all 0.25s ease-in-out;
}
#hero .language-btn {
	position: absolute;
	left: 40px;
	top: 40px;
}
.otl-btn {
	border: 2px solid rgba(255, 255, 255, 0.75);
	color: blue;
	vertical-align: bottom;
}
}
