css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yahoo.co.jp") {
   html,
  body{
  background-image: url("https://farm6.staticflickr.com/5348/30670952792_8035c5c216_h.jpg");
  background-size:cover;
  /*＊＊＊＊＊＊＊＊＊＊＊＊＊＊↑↑ここに画像までのパスを入れると，好きな画像を設定できます。***********************/
  /*********背景画像は，予告なく変更・削除される場合がありますので，ご了承ください。********************/
  background-color: #fff;
  background-repeat: no-repeat;
  background-attachment: fixed;
  }

 #wrapper{
      margin: 0 !important;
    width:980px;
      background-color: rgba(0, 105, 62, 0.7) !important;
    }


 /********************ヘッダー部分要らないもの削除***************************/
  #uhd, #mhicon, #siteinfo, #siteinfo, #changeMode, .symbol, #So1, #So2
   {
    display: none;
  }

/********************右カラム要らないもの削除***************************/

  #yads-trb-pr, #yjidbox, #scoreboard, #qurioRanking, #ultra, #pageTop, #brandpanel, #rightbox, div[id^="ADframe"], div#rightbox
  {
    display: none !important;
  }

/********************左カラム要らないもの削除***************************/

 /* #navi*/#favoriteservice, #companybox, #disasterbox, #announce
  {
    display: none;
  }



/********************中央カラム要らないもの削除***************************/
   #video, #cgmboxR, #event, #qurioRecommend,
  div#topicsbox ~ div
  {
    display: none;
  }

/********************スタイリング***************************/

  a{
    color: #FF66A7 !important;
    text-shadow: 0 0 1px #333 !important;
    font-weight: bold;
  }
  a:hover{
    color: #A1C7FD !important;
    text-shadow: 0 0 1px #333 !important;
  }
   a:visited{
    color: #CB5E9B !important;
    text-shadow: 0 0 1px #333 !important;
  }
  h1{
    width:100%;
    background-color:rgba(255, 102, 167, 0.7);
  }

  #searchbox{
    color: #fff;
      background-color: rgba(0, 105, 62, 0.7) !important;
    border: 0px;
    margin: 0px;
    padding: 0px;
  }
  #srchMorefw{
    background-color: rgba(0, 105, 62, 0.7) !important;
  }
  #srchbtn, #sbn > fieldset:nth-child(1) > div:nth-child(2) > input:nth-child(2) ,
  #sbs > fieldset:nth-child(1) > div:nth-child(2) > input:nth-child(2){
    border-radius: 25px;
    background-color:rgba(255, 102, 167, 0.8);
  }


  #srchfield, #personalbox, #yahooservice, #topicstab > li
  {
        background-color: rgba(0, 105, 62, 0.7) !important;
  }
  .bgA5, #navi .bx, #contentbox, #main,#topicsbox,#personalbox,#pbproperty{
    border: 0;
    background-color: rgba(0, 0, 0, 0) !important;
  }
  #pbdata, #pbalert, #srch{
     background-color:rgba(255, 102, 167, 0.7);
    color: #333;
  }
  #pbdata >a{ color:#333;}
  #pbalert > p> a,#pbidinfo{ color:#333;text-shadow: 0 0 1px #333;font-weight: bold;}
  #pbtoday{ background-color: rgba(0, 105, 62, 0.8) !important;}


/**************************検索結果*******************************/
  #contents, #SaA-dtl, #ygma, #Se, #Sq .inner, #Sf .inner,
  #header
  {
    margin: 0 !important;
    width:100%;
   background-color:rgba(255, 102, 167, 0);
  }
  #Sf2 .inner{
    width:100%;
  }
  #Sf2 .loginStats a,
  #Sf .fil a{
	border-bottom: 1px solid #999;
	color: #fff!important;
	text-decoration: none;
}
  #yschsp, #yschsbp{
    border-radius: 100px 25px 50px 50px / 50px 25px 50px 25px;
  }
  #Sf, #Sf2{
    border-bottom: 0px !important;
    width: 100%!important;
    background-color: rgba(252, 102,166,0.7)!important;
  }
  #Sf2 .resultNum {
    float: none!important;
	padding-left: 36px!important;
	font-size: 92%!important;
	color: #aaa!important;
}
#Sf2 .loginStats {
    float: right!important;
	padding-left: 36px!important;
	font-size: 92%!important;
	color: #666!important;
}
  #So3, #WS2ak{
    display:none;
  }

  #contents, #SaA-dtl, #ygma, #Se, #Sq .inner, #Sf {
    margin: 0 0 !important;
    width: 950px;
}
  .separation{
    display:none;
  }
  #mIn{
    background-color: rgba(0, 105, 62, 0) !important;
    border: 0;
  }
  #footer, #Sq .inner, #SqC {
     border: none !important;
      background-color: rgba(0, 105, 62, 0.8) !important;
    }
    #sq, .inner, #SqC{
      margin: 0;
     border:0;
    }
    .inner, .cf{
      border: 0 !important
    }
  #main .mdPager ul{
    background-color: rgba(252, 102,166,0.1);
  }

  .mdTgTtl .rltdWrd li a,
  #main .md_listCmmn .lst_tg .tg a{
    color: #F08600;
  }
  #bcrmb li a{
    color: #F08600;
  }
  #WS2m .a span {
	white-space: nowrap;
    color: #fff !important;
}

  ul.lst_tg{
    color: #FFE8F2;
  }
  .actionContents, .listTab{
    display: none;
  }
  #sIn, #WS2m .w .bd p, .smr,
  cite{
    width:100% !important;
    color: #fff !important;
  }
  #SqB{
    background-color: rgba(252, 102,166,0.8);
  }
    .mdPstd, .mdPstdQstn, .sttsRslvd,  .clrfx{background: #fff;}
}
