css:
@-moz-document domain("news.ycombinator.com") {

/* this section mostly modifies the front page or the home page of HN */
    
body {
    margin: 0 100px !important;
    background-color: #171c19;
    color: #87928a;
}

table[bgcolor="#f6f6ef"] {
    background-color: #171c19 !important;
}
    
#hnmain > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    background-color: #171c19;
}

.pagetop {
    color: #87928a !important;
    padding-left: 1em;
}
    
.pagetop a {
    color: #87928a !important;
    font-size: 110%;
}

.score {
    color: #87928a !important;
}

a:link {
    color: #87928a !important;
}
   
.subtext a:link, span.comhead a:link {
    color:#478C90 !important;
}
    
a:visited {
    color: #87928a !important;
}

.subtext a:visited, span.comhead a:visited {
    color: #478C90 !important;
}

#hnmain > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) {
    display: none !important;
}

#hnmain > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(1) > br:nth-child(3) {
    display: none !important;
}

.votearrow {
    background: none !important;
    width: 1em !important;
    height: 1em !important; 
    margin-left: 6px !important;
    margin-top: 0px !important;
    margin-bottom: 4px !important;
}

.votearrow:before {
    content: "â–²"; 
    color: #5f6d64; 
    font-family: monospace; 
    font-weight: bold; 
    position: relative;
}

input {
    color: #87928a !important;
    background-color: #171c19 !important;
    border: 1px solid #5f6d64;
}
    
img[src="y18.gif"] {
    display: none !important;
}
  
#hnmain > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    width: 1px !important;
    padding-right: 1px !important;
}

/* this section mostly modifies the pages with comments */

.c00 {
    color: #87928a !important;
    line-height: 1.4em;
}
    
textarea {
    border: 1px solid #5f6d64 !important;
    background-color: #171c19 !important;
    color: #87928a !important;
}
    
textarea, td {
    font-size: 100%;
}

.cdd {
    color: #5f6d64 !important;
    opacity: 0.3;
}
    
.c5a {
    color: #5f6d64 !important;
    opacity: 0.5;
}

td[bgcolor="#ffffaa"] {
    background-color: #171c19 !important;
    color: #87928a !important;
}
  
/* guidelines */
    
body > center:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(31) > table:nth-child(4) {
    display: none !important;
}
    
span.foot, body > center:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(31) > br:nth-child(2) {
    display: none !important;
}

img[src="yc500.gif"] {
    display: none !important;
}

td[bgcolor="#fafaf0"] {
    background-color: #171c19 !important;
}

body > center:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > br:nth-child(2) {
    display: none !important;
}

body > center:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > br:nth-child(3) {
    display: none !important;
}

b {
    color: #55859b !important;
}

/* faq */
    
body > center:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(50), #main > table:nth-child(5){
    display: none !important;
}

/* bookmarklet */
    
#main > br:nth-child(3), #main > br:nth-child(4) {
    display: none !important;
}

/* dmca (online censorship and threat generator mechanism) the inline css gave made me cringe */    
.MsoNormal {
    background-color: #171c19 !important;
    color: #87928a !important;
}

.MsoNormal span {
    background-color: #171c19 !important;
    color: #87928a !important;
    }

/* I have no idea what the following code is supposed to do. */
    
.title, .default, td, tr, .comment, .title {
    font-size: 100% !important;
}
  
.default {
    max-width: 40em;
}

.subtext {
    font-size: 70% !important;
}

.comhead {
    font-size: 80% !important;
}

table[style="padding:2px"] {
    padding-left: 2em !important;
    padding-right: 2em !important;
}

.pagetop div {
    background: #f2dede;
    border: 1px solid #eed3d7;
    color: #b94a48;
    display: inline;
    padding: 0.2em;
}

td {
    line-height: normal !important; 
}
  
a[href*="user?id="], #profile_link {
    text-transform: none !important;
}

.pagetop b a {
    font-size: 180% !important;
    padding-right: 120px;
}

table[width="100%"] {
    height: 82px;
    padding: 1em 0 1em 0;
}

tbody tr td table { 
    padding-left: 1em;
}

tbody tr:nth-of-type(3) {
    margin-left: 10px !important;
}

tr[style="height:10px"] {
    height: 20px !important;
}

tr[style="height:5px"] {
    height: 20px !important;
}

tr .title:first-child + td {
    padding-right: 0.5em;
}
  
img[src="http://ycombinator.com/images/s.gif"] + table {
    display: none;
}

img[src="http://ycombinator.com/images/s.gif"] + table + br + center {
    background-color: #F60;
    padding-top: 1em;
    color: white;
}

form[action*="/x"] input {
    font-size: 100%;
}

form[action*="/y"] table {
    font-size: 100%;
}

span.topsel a {
    font-weight: bolder;
}

#user_popup + center > table > tbody > tr:last-child >  td {
    padding-top: 4em;
}

#user_popup + center > table > tbody > tr:nth-child(3) >  td {
    padding-top: 0em;
}
  
}
