css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://projecteuler.net/") {

  /* HEADER */
  #header {
    background-color: #252526;
    box-shadow: 0 2px 5px #1C1C1C;
  }
  
  
  /* PAGE */
  
  body, #footer {
    background-color: #2D2D30;
    font-family: Helvetica, sans-serif;
    color: #E0DDF0;
    font-size: 100%;
  }
  
  p {
    text-align: justify;
  }
  
  h2 {
    color: #DA3B35;
    font-family: Helvetica, sans-serif;
  }
  
  h3, h4 {
    color: #E0DDF0;
    font-family: Helvetica, sans-serif;
  }
  
  a {
    color: #007ACC;
  }
  
  a:hover {
    color: #0F6098;
  }
  
  .warning {
    color: #DA3B35;
  }
  
  #message {
    background-color: #252526;
    border: 1px solid #1C1C1C;
    color: #DA3B35;
  }
  
  #account_page .small_notice {
    color: #E0DDF0;
  }
  
  /* TABLE */
  
  .grid td, .grid th, #problems_table_page #problems_table th.final_column {
    background-color: #252526;
    border: 1px solid #1C1C1C;
    color: #E0DDF0;
  }
  
  #problems_table_page .sortby {
    color: #E0DDF0;
  }
  
  /* NAV */
  
  #nav li, .pagination a, .pagination a:link {
    background-color: #3F3F46;
    border: 1px solid #1C1C1C;
  }
  
  #nav li:hover, .pagination a:hover {
    background-color: #007ACC;
  }
  
  #nav a {
    color: #E0DDF0;
  }
  
  #nav #current a, .pagination a.current {
    background-color: #DA3B35;
  }
  
  /* NEWS */
  
  #news_page .news {
    background-color: #252526;
    border: 1px solid #1C1C1C;
    color: #E0DDF0;
  }
  
  /* FORM */
  
  .form_box {
     background-color: #252526;
    border: 1px solid #1C1C1C;
    color: #E0DDF0;
    box-shadow: 5px 5px 5px #252526;
  }
  
  input, textarea, select {
    background-color: #1C1C1C;
    border: 1px solid #3F3F46;
    color: #E0DDF0;
  }
  
  /* PROBLEM */
  .problem_content {
    background-color: #252526;
    border: 1px solid #1C1C1C;
    color: #E0DDF0;
  }
  
}
