css:
@-moz-document url-prefix("http://www.adrenaline.uol.com.br"), 
regexp("https?://(www\\.)?adrenaline\\.uol\\.com\\.br/(?!forum|barra).*"){


body{
	background-color: #2c2c2c !important;
	background: #2c2c2c !important;
}

#barrauol{
	display: none;
}

#barrauol nav a{ /*Div dos links do nav*/
	background-color: #131313;
}

#Header .hat h1{
        left: 350px
}

#Header .hat{
	background-color: #141414 !important; /*logo adrena*/
        height: 115px;
}

#Header nav .em-alta ul li a, #Header nav .em-alta ul li a:visited{
	background-color: #ccc !important;
}

/*--------- cabeçalho após o logo -----------------*/

#Header nav .em-alta{
	background-color: #ccc !important;
	border: 1px solid #000;
	top: 2px;
}

#Header nav .em-alta .tag{
	top: -3px !important;
}

#Header nav .em-alta .tag a{
        color: #fff !important;
        font-weigth: bold;
}

#Header nav .em-alta>ul{
	top: 15px !important;
	border: none;
}

#Header nav .main-nav{
	background-color: #272525 !important;
}

#Header nav .main-nav .destaque{
	background-color: #ccc !important;
	border: 1px solid #000;
}

#Header nav .main-nav .secoes ul{
	background-color: #ccc;
}

#Header nav .main-nav .secoes{
	border: 1px solid #000 !important;
	border-bottom: none;
	height: 23px !important;
}

#Header nav .main-nav .secoes div:first-child{
	background-color: #ccc;
}

#Header nav .main-nav .menu{
	background-color: #ccc;
	border: 1px solid #000;
	top: 1px;
}

#Header nav .main-nav .menu .theMenu li a{
	border: none !important;
}


ol, ul{
	background-color: #ccc;
}

#Header nav .main-nav .menu{
	background-color: #ccc !important;
        width: 879px !important;
}

#Header nav .search-box{
	color: #000 !important;
	border: 1px solid #000 !important;
	top: 2px;
	z-index: 1;
}

#Header nav .search-box span{
        font-weigth: bolder;
        color: #000;
}

/*---------------- Noticias/Corpo ------------------------*/

.span_analise{
	color: #fff !important;
}

.span_analise{
	color: #c62a29 !important;
}

.span_analise_produto, .span_analise_titulo{
	color: #fff !important;
}

.alfa, .gamma{
	background-color: #404040;
	/*width: 720px !important;*/
}

.news-basket{
	background-color: #2c2c2c !important;
	border: none;
}

.news-basket article{
	background-color: #404040 !important;
	border: 1px solid;
	color: #fff; 
}

.news-basket .inbox-analises{
	background-color: #404040 !important;
}

.content article h2 a, .news-basket article h2 a{
	color: #fff !important;
}

.news-basket a{
	color: #fff !important;
}

.news-basket .inbox-analises .analise h3{
	color: #fff !important; /*Talvez exista algum problema*/
}

.popTitle{
	background-color: none;
}

.popTitle .finish{
	background-color: none;
}

.blankLine20{
	background-color: #2c2c2c;
}

.blankLine18{
	background-color: #2c2c2c;
}

.news-basket .inbox-bombando ul li{
	background-color: black;
}

.box-leitura-rapida{
	background-color: #404040 !important;
}

.homies .box-autores{
	background-color: #404040 !important;
}

.homies .box-autores a{
	color: #fff;
}

.faux3-2e1{
        background: none !important;
        background-color: #2c2c2c; 
}

.side-products-list .produto .info p a{
        color: #fff !important;
}       
        
.side-products-list{
        padding: 40px 21px 21px !important;
        background-color: #404040;
        border: 1px solid #fff;
}       
        
.techspecs td{
        color: #000;
        font-weight:bolder;
}

.produtoMaterias h2{
        color: #fff
}

.content article .author, .news-basket article .author{
        background:#404040 !important;
}

/*------------------- Rodapé--------------------------*/

#Footer .access nav{
	background-color: #cccccc;
        position: relative;
        left: -15px;
}       
        
#Footer .access nav>div ul li a{
        color: #000;
        text-decoration: none;        
}

.grid-6columns{
        background-color: #2c2c2c;
        border: none;
}       
        
.novo-grafico-box-dir td{
        background-color: #2c2c2c;
        color: #ccc;
}
  
/*------------- páginas dos posts e noticias ------------*/

.post-title{
	background-color: #404040;
	color: #fff;
}

.thePost{
	background-color: #404040 !important;
}

.thePost .content{
	background-color: #404040;
	color: #fff;
}

.side-poll{
	background-color: #404040;
        border: 1px solid #fff;
	color: #fff
}

.side-news-list{
	border: 1px solid #fff;
        background-color: #404040;
}

.side-news-list ul li a{
	background-color: #404040;
	color: #fff !important;
}

#BackSite{
	background-color: #2c2c2c;
}

.grid-3column{
	background-color: #2c2c2c !important;
}

.grid-3column .grid-2columns{
	background-color: #2c2c2c;
}
  
.grid-2columns.box-sizing p{
   width: 1000px;
   padding: 10px !important;
}

.content ol, .content p, .content ul, .news-basket ol, .news-basket p, .news-basket ul, .side-article article .descricao p, .video-data .info .descricao p{
        background-color: #404040;
	color: #fff;
}

#comments_thread, .post-title{
	background-color: #000;
}

.post-title .dataBox{
	color: #fff !important;
}

.post-content .post-message p:last-child{
	color: #fff !important;
}

#layout, .btn-default, .dropdown-menu .divider, .follow-btn, .post-content .post-body{
        color: red !important;
}

div#comments_thread{
        background-color: darkgray;
        position: relative;
        left: 25px;
        width: 950px;
}

b, strong{
   color: #ff0808;
}

.lancamento .game table td{
    color: #000;
    background-color: #a9a9a9;
}

b, strong{
    color: #f00 !important;
}

.novo-grafico-valor{
	background-color: #2c2c2c;	
}

.novo-grafico-diferenca{
	color: #000;
}

.grid-3column .column{
        background-color: #404040;
}
        
.grid-3column .grid-2columns .full-width{
        background-color: #404040;
}

.content.grid-2columns.clearfix{
        background-color: #404040;
}

.bottomInfo{
	background-color: #404040 !important;
        border: 1px solid #ccc;
}

.thePost .postShare{ /*Barra lateral de rede social que acompanha a pagina*/
        display: none;
}

/*------------- páginas de reviews de produtos ------------*/

.lista-produtos .produto .info p a{
        color: #fff;
}

.lista-produtos .produto .info p{
        background-color: #2c2c2c;
}

.content ol, .content ul, .news-basket ol, .news-basket ul, .thePost ol, .thePost ul{
        background-color:#ccc !important;
}

.coluna-filtros .filtros-horizontais{
        background-color:#ccc;
}

.lista-produtos .alert p{
        background-color:#ffc;
}

.reviewConclusao .reviewProsCons>div ul{
        background-color:#404040 !important;
}

/*------------- Categorias de vídeo ------------*/

.videos-categoria{
        background-color:#404040;
}

.video-thumb .titulo a{
        color:#000;
}

}
