css:
@-moz-document domain("allegro.pl") {

.listing-module {
    max-width: 100%; /* umożliwia wyświetlenie więcej kolumn w wyższych rozdzielczościach ekranu */
}

.offer {
    cursor: pointer;
    position: relative;
    min-height: 116px;
    padding: 10px;
    color: #7F7F7F;
    margin: 2px;
    width: 310px;
    height: 116px;
    display: inline-flex;
    border: solid 1px #f5f5f5;    
    background: #e2e2e2; /* For browsers that do not support gradients */
    background: -webkit-radial-gradient(transparent, transparent, #f5f5f5); /* Safari 5.1 to 6.0 */
    background: -o-radial-gradient(transparent, transparent, #f5f5f5); /* For Opera 11.6 to 12.0 */
    background: -moz-radial-gradient(transparent, transparent, #f5f5f5); /* For Firefox 3.6 to 15 */
    background: radial-gradient(transparent, transparent, #f5f5f5); /* Standard syntax (must be last) */
}

a.offer-title {
    font-size: 12px;
    height: 56px;
    width: 170px;
    display: block;
}

.offer-header {
    line-height: 14px;
    padding: 0;
}

.offer-header+.offer-price {
    margin: 0px;
}

.bid-count {
    display: block;
    position: absolute;
    top: 113px;
    left: 145px;
    font-size: 9px;
    color: #b5b5b5;
}

.offer-price .offer-delivery {
     font-size: 0.9000000000000002rem; 
    font-size: 9px;
     font-size: 0.9rem; 
    font-weight: bold;
    float: left;
}

.offer .icon-as {
    margin: -2px 0 0 0;
    display: inline-block;
    position: absolute;
    top: 85px;
    left: 223px;
}

.offer-price .other-prices {
    position: absolute;
    display: block;
    /* bottom: 5px; */
    top: 101px;
    left: 145px;
}

.offer-info {
    padding-left: 135px;
}

.offer-brand { /* autoryzowany sklep */
    position: absolute;
    bottom: 0px;
    left: 220px;
    width: 90px;
    font-size: 8px;
    text-align: center;
    margin: 0;
}

.offer-brand .shop-name {
    display: block;
    line-height: 6px;
    margin-top: 0px;
}


.offer-preview.popover.fade.in {
    position: fixed;
    top: 5px !important;
    right: 10px !important;
    left: auto !important;
}

.offer-preview.popover.fade.in a.offer-title {
    width: auto;
    height: auto;
}
}
