css:
@-moz-document domain("zhihu.com") {
.zu-top {
    position: fixed;
    background: #4CAF50;
    background-color: #4CAF50;
    background-image: none;
    border-bottom: none;
    box-shadow: none;
    height: 46px;
}
.zu-top-nav-li.current {
    background-image: -webkit-linear-gradient(top, #4CAF50, #8BC34A);
    background-image: -moz-linear-gradient(top, #137AD5, #1069bd);
    box-shadow: none;
}
.top-nav-profile .zu-top-nav-userinfo {
    text-indent: 52px;
    transition: none;
    background: #4CAF50;
}
a {
    color: #4CAF50;
    text-decoration: none!important;
    -webkit-transition: all .2s ease-in-out;
    -moz-transition: all .2s ease-in-out;
    -ms-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
}
.zu-top-search-form .zu-top-search-button {
    background: #4CAF50;
    background-color: #8BC34A;
    background-image: none;
    border: 1px solid #8BC34A;
    box-shadow: none;
    transition: all .1s;
}
.zu-top-search-input {
    background-color: #efffdc;
    border: 1px solid #8BC34A;
    box-shadow: inset 0 1px 3px rgba(0, 0, 0, .2), 0 1px 0 rgba(255, 255, 255, .1);
}
.zu-top-search-button:hover {
    background-color: #8BC34A;
    border: 1px solid #8BC34A;
}
html.no-touchevents .zu-top-nav-userinfo:hover {
    background-color: #8BC34A;
}
.zu-top-nav-userinfo.selected,
html.no-touchevents .top-nav-profile:hover .zu-top-nav-userinfo {
    height: 46px!important;
    background-image: -webkit-linear-gradient(top, #4CAF50, #8BC34A)!important;
    background-image: -moz-linear-gradient(top, #137AD5, #1069bd)!important;
    box-shadow: none;
}
.top-nav-profile a {
    background-color: #8BC34A;
    text-shadow: none;
}
.top-nav-dropdown li a {
    border-top: 1px solid #3cb341;
}
html.no-touchevents .top-nav-dropdown a:hover {
    background-color: #4CAF50;
}
.zu-top-add-question {
    border: 1px solid #4CAF50;
    background: #4CAF50;
    background: -webkit-linear-gradient(top, #8BC34A, #4CAF50);
    background: linear-gradient(to bottom, #8BC34A, #8BC34A);
}
.zm-item-vote-count {
    color: #8BC34A;
}
.HomeEntry-item a:hover {
    color: #8BC34A;
}
/*Topic*/
.zg-btn-blue {
    background: #8BC34A;
    background: -webkit-linear-gradient(top,#8BC34A,#8BC34A);
    background: linear-gradient(to bottom,#8BC34A,#8BC34A);
    border: 1px solid #4CAF50;
    box-shadow: 0 1px 0 rgba(255,255,255,.2) inset,0 1px 0 rgba(0,0,0,.2);
}
.topics-plaza a.text {
    color: #4CAF50;
}
html.no-touchevents .zm-topic-cat-main .zm-topic-cat-item:hover a, .zm-topic-cat-main .zm-topic-cat-item.current a {
    background: #8BC34A;
    border-color: #4CAF50;
}
.zg-link-litblue, .zg-link-litblue-normal {
    color: #4CAF50;
}
html.no-touchevents .zg-follow:hover {
    color: #4CAF50;
}
html.no-touchevents .zg-follow {
    color: #8BC34A;
}
}
