css:
@-moz-document domain("workflowy.com") {
/*part 1 - WorkFlowy [simple header] with Oleg's edit v1.0*/
#header {
        border-bottom: none !important;
        background: #383D40 !important;
		background-color: #4F595F !important;
        padding: 0px 0 !important;
    }
    #logo {
	display: none;
    }
    #searchForm {
        top: 0px !important;
        left: 120px !important;                                            
    }
    #searchBox {
        color: #A5B6C3 !important;
        background-color: #4F595F !important;
                                            
        border: none !important;
        padding: 7px 90px !important;
     
        font-size: 14px !important;
        opacity: .75 !important;
        -moz-border-radius: 0px !important;
        -webkit-border-radius: 0px !important;
        border-radius: 0px !important;

    }
    #searchBox::-webkit-input-placeholder {
        color: #A5B6C3;
    }
    #searchBox:-moz-placeholder {
        color: #A5B6C3;
    }
    #searchBox::-moz-placeholder {
        color: #A5B6C3;
    }
    #searchBox:-ms-input-placeholder {
        color: #A5B6C3;
    }
    #searchCancel {
        top: 10px !important;
        right: 10px !important;
        width: 10px !important;
        height: 10px !important;
    }
    #searchCancel > svg > line {
        stroke: #A5B6C3;
        stroke-width: 2;
    }
    #searchCancel:hover > svg > line {
        stroke: #738088 !important;
    }
    .menu-options {
        top: 36px !important;
        background: #383D40 !important;
        border: none !important;
        -moz-border-radius: 0px !important;
        -webkit-border-radius: 0px !important;
        border-radius: 0px !important;
        box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15), 0 6px 6px rgba(0, 0, 0, 0.20);
    }
    #buttonBar.open {
        background: none !important;
    }
    .saveButton,
    .showCompletedButton,
    #buttonBar,
    #helpButton {
        font-weight: 500;
        color: #AAA !important;
    }
    .showCompletedButton:hover,
    #buttonBar:hover,
    #helpButton:hover {
        background-color: transparent !important;
        color: #FFF !important;
    }
    .menu-options > div,
    .menu-options > div > a {
        color: #AAA !important;
    }
    .menu-options > div:hover,
    .menu-options > div > a:hover {
        background: none !important;
        color: #FFF !important;
    }
    .saveButton .topBarButtonTextContainer {
        color: inherit !important;
    }
    .undo-button.enabled:hover,
    .redo-button.enabled:hover {
        opacity: 1 !important;
        background: none !important;
    }
    .saveButton,
    .showCompletedButton,
    #buttonBar,
    #helpButton {
        border: none !important;
        background-color: #4F595F !important;
                                            
    }
    #savedViewHUDButton {
        width: 37px !important;
        height: 23px !important;
        margin: 2px 0 0 0 !important;
        background: url('https://workflowy.com/media/i/star-icon-empty.svg') !important;
        background-size: 23px !important;
        background-position: center center !important;
        background-repeat: no-repeat !important;
        -moz-border-radius: 0px !important;
        -webkit-border-radius: 0px !important;
        border-radius: 0px !important;
    }

/*part 2 - WorkFlowy [clear picture + bullet space 4px] Oleg's edit v1.0*/
body {
        background: #FFF !important;
    }
    #pageContainer {
        margin-top: 0px;
    }
    .page {
        max-width: 100% !important;
        border: none !important;
        margin-bottom: 0 !important;
       	margin-top:0px !important;
        padding: 4px 7%!important;                   

    }
/*double spacing between bullets*/

.selected .project > .name > .content {
    margin-bottom: 4px;
	}

    /* Remove the "Get more space" top left link so it does not interfere */
    .getMoreSpaceButton {
        display: none;
    }

/* Remove the "Make love, not war" slogan */
	.siteSlogan {
        display: none
    }

    /* Remove the "Go pro" bottom right button, and the bottom links to the Blog and Privacy Policy*/
    .proPitch {
        display: none;
    }

    #bottomLinks {
        padding-bottom: 0;
        margin-top: 0;
    }
    #bottomLinks * {
        display: none;
    }

/*I love you too, honeypie*/
}
