css:
@-moz-document domain("touchofmodern.com") {
/* Login */
.form-control {
    color: #555555;
    background-color: #222;
    border: 1px solid #444;
}

/* Header */
.navbar-default {
    background-color: hsl(0,0%,6%);
    border-color: #202020;
}

.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
    color: #4a494b;
    background-color: #0f0f0f;
}

.dropdown-menu>.active>a, .dropdown-menu>.active>a:hover, .dropdown-menu>.active>a:focus {
    background-color: #111;
}

.main-nav .my-account-dropdown .dropdown-menu {
    background: hsl(0, 1%, 2%);
}

.navbar .account-indicator .glyphicon {
    color: rgba(0,0,0,.7);
    text-shadow: 0px -1px 0px #333;
}


/* Banner and removed gradients */
.banner-carousel-wrapper {
    background: #111;
}

.banner-carousel-wrapper>div .gradient-right {
    right: 0;
    background: none;
}

.banner-carousel-wrapper>div .gradient-left {
    right: 0;
    background: none;
}

.slick-dots li {
    background-color: #111;
}

/* Main Content */
.grey-bg {
    background-color: none;
    background: url(http://i.imgur.com/1SJVuy7.png);
}

.sale-item.col-sm-6 .sale-image {
    border: 1px solid #111;
}

.sale-item .sale-link .sale-label {
    background-color: rgba(0, 0, 0, 0.83);
}

.scroll-to-top-button {
    border: 1px solid #202020;
    background-color: hsl(0,0%,6%);
}

.footer {
    border-top: 1px solid #202020;
    background-color: hsl(0,0%,6%);
}

.store-tile-container {
    background-color: #252528;
    margin-bottom: 0px;
}

hr {
    border-top: 1px solid #333;
}

body {
    background-color: none;
    background: url(http://i.imgur.com/1SJVuy7.png);
}

/* Buttons */
.btn.btn-primary, .btn.btn-info {
    border-color: #4a494b;
}

.btn-default {
    color: #555;
    background-color: #202020;
    border-color: #333;
}

.btn-default:hover {
    background-color: #202020;
    border-color: #222;
}

/* Profile */
.nav-side-border {
    border-right: 1.5px solid #333;
}

.nav-pills>li>a:hover {
    background: #0f0f0f;
    color: #4a494b;
}

/* Invite a friend */
.invite-friend-header {
    background-color: hsl(0,0%,6%);
}

.invite-friend-report {
background-color: rgba(0, 0, 0, 0.86);
}

.invite-by-link .invite_link_field {
    background-color: #222;
    border: 1px solid #444;
}

/* Product Page */
.quantity-select {
    border: 1px solid #202020;
    background: hsl(0,0%,6%);
}

.section_divider {
    border-top: 1px solid #333;
}

.variation-select, .add-to-cart-section select {
    background: hsl(0,0%,6%);
    border: none;
}

/* Mobile */
#overall_content {
    background: url(http://i.imgur.com/1SJVuy7.png);
}

/* Price Range Edit */
input, button, select, textarea {
    background: #222;
    border: 1px solid #444;
}

.navbar-submenu {
    background-color: #131212 !important;
    border-color: #0f0f0f !important;
}

.banner-carousel-wrapper .banner .description .copy {
    color: #777777;
}

.banner-carousel-wrapper .banner .description .brand {
    color: #555;
}

.promotion_banner {
    background-color: #1b1b1b;
}

.size-container .size-selector {
    color: #666;
    background-color: #2d2d2d;
}
}

@-moz-document domain("support.touchofmodern.com") {
.header {
    background-color: hsl(0,0%,6%);
    border-color: #202020;
}

.search-box input, .sub-nav .search input {
    background: hsl(0,0%,6%);
    border-color: #222;
}

.category h2 {
    border-bottom: 1px solid hsl(0,0%,6%);
}

a {
    color: #666;
}

a:hover {
    color: #333;
}

main, .header-inner, .footer-inner {
    margin: none;
    padding: 0 20px;
}

button, [role=button], [type=button], [type=submit] {
    border: 1px solid #333;
    color: #444;
}

/* Search Results */
.search-results-list > *:first-child {
    border-top: 1px solid hsl(0,0%,6%);
}

.search-results-list > * {
    border-bottom: 1px solid #333;
    padding: 20px 0;
}


/* Submit Request */
page-header {
    border-bottom: 1px solid #333;
}

input, textarea, select {
    border: 1px solid #444;
    background: #222;
}

.upload-dropzone {
    border: 1px solid #444;
}

.form footer {
    border-top: 1px solid #333;
}
}
