@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slimecraft.pt") {
  * {text-shadow: none !important;}
  
  html,body {background: url('http://tiamarth.com/images/other/whitenoise.png') fixed;}
  
  a,a:visited {color: #31a07b}
  
  a:hover,a:focus {color: #0a4f38;}
  
  div#nav {
    border: none;
    padding: initial;
  }
  
  div#nav ul{
    background: #306da3;
    padding: 6px;
    box-shadow: 0 0 3px black;
  }
  
  div#nav ul li a,div#nav ul li a:visited {}
  
  div#nav ul li a:hover,div#nav ul li a:focus {color: #15c188;}
  
  .page{background: #d6d6d6;}
  
  .page,.normal_text,.noob,.bubble {color: black;}
  
  .bubble,.chat {
    background: #e7e7e7;
    border: 1px solid #a3a3a3;
  }
  
  #userimg_header {border: none;}
  
  #userdata {
    border: none;
    background: #e7e7e7;
  }
  
  .txtinput_login,.input_button,.profile_info input,.boardButtonCurrent,.boardButton,input,select,textarea {
    color: black;
    background: #e7e7e7;
    border: 1px solid #a3a3a3;
  }
  
  div.bubble::before {border-right: 1px solid #a3a3a3;}
  
  div.bubble:before{
    border-color: #a3a3a3;
    border-top: 16px solid rgba(204,204,204,0);
    border-bottom: 0px solid rgba(204,204,204,0);
    border-right: 24px solid #a3a3a3;
  }
  
  .new_post_buttons {border-top: 1px solid #a3a3a3;}
  
  .boardButton {border: 1px solid #c6c6c6;}
  
  .input_button:hover,.boardButton:hover {
    color: #8cf2d0;
    background: #306da3;
  }
  
  .boardButtonCurrent {
    color: black !important;
    background: #31a07b;
  }
  
  .post_date {border-top: 1px solid #a3a3a3;}
  
