css:
@-moz-document domain("learncpp.com") {
body { 
	background-color: #000;
}

div#container {
	width: 85%;
	box-shadow: none;
}

/* Ads */
div.code-block { display: none }

/* removes the columns */
td#left, td#right, colgroup {
	display: none
}

td#middle {
border: 0px;
}

div#imagecontainer {
	border-bottom: 0px;
}

/* fonts */
div.post-bodycopy p {
	font-size: 14pt;
	font-family: serif;
}

div#crayon {
font-size: 14px !important;
line-height: 17px !important;
}

pre {
font-size: 14px;
}

div#footer {
border: 0px;
background-color: #000;
color: #eee;
}
div#footer a:hover {
color: #eee;
}
div.post a:visited {
color: #c66bac
}
}
