css:
@-moz-document domain('alldebrid.com'), domain('alldebrid.fr'), domain('alldebrid.org'), domain('alldebrid.it'), domain('alldebrid.es'), domain('alldebrid.de') {

body {
    background-image: linear-gradient(rgba(0, 0, 0, 0.9), rgba(0, 0, 0, 0.9));
}

.torrent-container,
.magnet-container,
#container_header,
.validlink {
    background: none!important;
    background-color: rgba(0, 0, 0, 0.7);
}

.choice {
    color: black!important
}

.title_big,
.title_medium,
.title_upload {
    color: #fac333!important
}

li,
div,
p,
td {
    color: white!important
}

tr,
.dlLinks,
#torrent table {
    color: black!important;
    background-color: black!important;
    background: black!important;
}

.function {
    color: #777
}

strong a {
    color: #777!important
}

offers_group,
.offers_bottom,
.offers_middle {
    background-color: #222!important
}

.paginate_disabled_previous,
.paginate_disabled_next,
.dataTables_info {
    background: none!important
}}
