css:
@-moz-document url(https://www.youtube.com/) {
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 400;
  src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v11/UyBMtLsHKBKXelqf4x7VRQ.woff2) format('woff2');
  unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 400;
  src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v11/1YwB1sO8YE1Lyjf12WNiUA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

/* i really want this to be global */
* {
font-family: 'Lato', sans-serif;
}

.yt-uix-button-subscribe-branded:before {
	display:none;
}

.exp-responsive .shelf-item .branded-page-module-title .branded-page-module-title-text {
	text-transform: uppercase;
}

.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text {
	background:#e74c3c;
}

#appbar-guide-menu .guide-section-separator {
	visibility:hidden; 
}  

#masthead-appbar-container {
  display:none;
}

body {
  background:#fff;
}

#browse-items-primary .item-section>li>.yt-lockup-tile, .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box { 
  border-bottom-width:0px;
}

.yt-card { 
	box-shadow:0 1px 2px rgba(0,0,0,.0);
}
#search-btn {
  background:#ecf0f1;
  border-color:transparent;
}
#masthead-search-terms {
	background-color:#ecf0f1;
}
.masthead-search-terms-border {
	border-color:#ecf0f1;
  	box-shadow:inset 0 0px 0px #ecf0f1;
}

.yt-uix-button {
	box-shadow:0 1px 0 rgba(0,0,0,0.0);
}

#masthead-search-terms {
	border-top-left-radius: 2px;
    border-bottom-left-radius: 2px;
}

#yt-masthead-container {
	border-bottom:1px solid #ecf0f1;
}

.gsfe_a {
	box-shadow:inset 0px 1px 2px rgba(0,0,0,0.0);
 	-webkit-box-shadow:inset 0px 1px 2px rgba(0,0,0,0.0);
  	border-width:0px;
}

.browse-list-item-container:hover .compact-shelf .yt-uix-button-shelf-slider-pager, .compact-shelf:hover .yt-uix-button-shelf-slider-pager {
	border-width:0px;
  	box-shadow:1px 1px 3px rgba(0,0,0,.0);
}

.yt-uix-button-default:hover, .yt-uix-button-text:hover {
	box-shadow:0 1px 0 rgba(0,0,0,0.0);
}

.exp-responsive .shelf-item .branded-page-module-title .branded-page-module-title-text {
	font-weight:700;
}

#appbar-guide-menu {
	background:#f5f7f8;
}

.guide-item .guide-count {
	color: #fff;
    background: #e74c3c;
    border-radius: 2px;
}

.guide-notification-item {
	padding-top:3px;
}

.guide-item .guide-count, .guide-badge-live {
	padding:0 10px;
}
.guide-flyout-trigger.on-hover, .guide-item:hover, .guide-item:hover .yt-deemphasized-text, .guide-collapsible-item .yt-uix-expander-head:hover {
	background:transparent;
 	 color:#222;
}

.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
	color:#4A443E;
  	font-weight:600;
}

.yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a, .yt-lockup .yt-lockup-byline a {
	color:#7f8c8d;
}

.yt-lockup-grid .yt-lockup-meta {
	display:none;
}

.exp-responsive .shelf-item .branded-page-module-title .branded-page-module-title-text {
	font-weight:600;
}

#appbar-guide-menu, .guide-flyout {
	background:#f1f4f5!important;
}

#appbar-guide-button {
	visibility:hidden;
}

[title~=Trending] {
    display:none;
}

.yt-lockup-byline {
	padding-top:8px;
}

li.guide-section h3 a {
	font-weight:600;
  	color:#c0392b;
}

#yt-masthead-container {
	padding-top:20px;
  	height:50px;
}

#appbar-guide-menu {
	top:29px!important;
}

#guide-container {
	padding-top:33px;
}

li.guide-section h3 a {
	padding-top:20px;
}

.yt-thumb-clip img, .yt-thumb-clip .vertical-align {
	border-radius:100%;
}

#masthead-search-terms {
	height:42px;
}

#masthead-search .search-btn-component {
	height:42px;
}

}
