css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("sierra.instructure.com"),domain("instructure.com"),domain("sso.sierracollege.edu") {
body {
    background-color: #222222 !important;
    color: #717171;
}
.ReactTray__Content {
    background-color: #222222 !important;
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    background-color: #606060 !important;
}
.ic-app-header__menu-list-link .ic-icon-svg {
    fill: #222222 !important;
}
.ic-app-header__menu-list-item.ic-app-header__menu-list-item--active .ic-app-header__menu-list-link {
    background: #222222 !important;
}
.menu-item__text {
    color: #535353 !important;
}
.ic-DashboardCard__action-container {
    background: #242424;
}
.ic-DashboardCard__header_content {
    background: #242424;
}
#breadcrumbs {
    background-color: #fff0;
}
.list-view > ul > li > a:hover, .list-view > ul > li > a:focus, .list-view > nav > ul > li > a:hover, .list-view > nav > ul > li > a:focus {
    background: #181818 !important;
}
.ic-app-header {
    background: #181818 !important;
}
.ic-app-header__menu-list-link:hover, .ic-app-header__menu-list-link:focus {
    background-color: #222222 !important;
}
.ic-app-header__secondary-navigation {
    background-color: rgba(255, 255, 255, 0);
}
.ui-widget-content {
    background: #181818 !important;
}
.ui-dialog .ui-dialog-titlebar.ui-widget-header, .ui-dialog .ui-datepicker .ui-widget-header.ui-datepicker-header, .ui-datepicker .ui-dialog .ui-widget-header.ui-datepicker-header {
    background: #181818 !important;
}
.ui-widget-header {
    background: #181818 !important;
}
#right-side table.summary td, #right-side table.summary tbody th {
    background-color: #181818 !important;
}
table.summary td, table.summary tbody th {
    background-color: #181818 !important;
}
.btn, .Button, .ui-button {
    background: #181818 !important;
}
.header-bar {
    border-bottom: 1px solid #FFF0;
    background-color: #fff0;
}
#grades_summary tr.assignment_graded > * {
    background-color: #fff0;
}
#grades_summary tr > * {
    background-color: #fff0;
}
#grades_summary tr.editable:hover > * {
    background-color: #181818;
}
#main-nav {
    background-color: rgb(40, 40, 40);
}
.PaneHeader {
    background-color: rgb(40, 40, 40);
}
.ic-app-header__logomark-container {
    background-color: #fff0;
}
.conversations .panel {
    background: #fff0;
}
.message-list .messages > li {
    background: #fff0;
}
.message-list .messages > li.active {
    background: #0F0F0F;
}
.message-list .messages > li:hover {
    background: #0F0F0F;
}
body.primary-nav-expanded .ic-app-header__logomark {
    height: 0;
}
.file-upload-submission {
    background: #181818;
}
.submission-details-comments .comments {
    background-color: #181818;
}
.discussionTopicIndexList .discussion-topic {
    background-color: #fff0;
}
.discussionTopicIndexList .discussion-topic:nth-child(2n+1) {
    background-color: #181818;
}
#syllabus tr.date.date_passed td, #syllabus tr.date.date_passed th {
    background-color: #fff0;
}
#syllabus tr.date.related td, #syllabus tr.date.related th, #syllabus tr.date.selected td, #syllabus tr.date.selected th {
    background-color: #181818;
}
#right-side {
    background: #282828;
    border: 1px solid #181818;
}
#minical {
    background-color: #222;
    text-shadow: none;
    border: 1px solid #FFF0;
}
table {
    background-color: #181818;
}
#context-list-holder, #undated-events {
}
.agenda-wrapper {
    background-color: #282828;
}
.agenda-day.agenda-today {
    background-color: #181818;
}
.group_course_315337, .group_course_315337:hover, .group_course_315337:focus {
    color: #DADADA;
    border-color: #DADADA;
}
.fc-event.assignment, .fc-event.assignment_override {
    background-color: #444;
}
#calendar-app .fc-event, #calendar-drag-and-drop-container .fc-event {
    background-color: #444;
}
#context-list-holder, #undated-events {
    background-color: #181818;
}
.ac-result-container {
    background: #181818;
}
.dropdown-menu {
    background-color: #181818;
}
.ac-result.selected, .ac-result:hover {
    background: #222;
}
.ui-tabs .ui-tabs-nav li.ui-tabs-active {
    background: #222;
}
.ui-tabs .ui-tabs-nav li.ui-tabs-active, .ui-tabs .ui-tabs-nav li.ui-tabs-active.ui-state-hover, .ui-tabs .ui-tabs-nav li.ui-tabs-active:hover {
    background: #222;
}
.ui-tabs .ui-tabs-nav li.ui-tabs-active a {
    color: #646464;
}
.ui-tabs .ui-tabs-nav li.ui-state-hover, .ui-tabs .ui-tabs-nav li:hover {
    background: #222;
    border-color: #C7CDD1;
}
.ui-tabs .ui-tabs-nav li.ui-tabs-active, .ui-tabs .ui-tabs-nav li.ui-tabs-active.ui-state-hover, .ui-tabs .ui-tabs-nav li.ui-tabs-active:hover {
    color: #fff0;
}
.table > thead {
    background: #0E0E0E;
}
.table-bordered th, .table-bordered td {
    border-left: 1px solid #222;
}
.table th, .table td {
    border-top: 1px solid #222;
}
.table-bordered {
    border: 1px solid #222;
}
}
